INSERT INTO `gift` (`id`, `name`, `feature`, `tel`, `picurl`, `city`, `town`, `lat`, `lng`) VALUES
(1, '烏魚子- 六六大順禮盒', '新竹地區農曆九月後，因地理環境關係，常颳著每秒20公尺的東北季風，且乾燥冷冽，稱之為九降風。每年11月開始是烏魚成熟季節，烏魚卵經過九降風風乾，風味口感極佳。新竹縣市養殖業者及南寮漁港合計有相當多的產量，在地理環境及原料相當充足之下，有很好的發展空間，且烏魚卵經北台灣和煦陽光及九降風自然風乾，風味獨特，質冠全台。新竹市漁會產銷班以多位養殖業者合作，自產自銷，並且維持一貫的優良品質，首度風城十大伴手禮選拔即入圍獲獎！\r\n購買位置：慶豐水產實業社', '03-536-2216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31824/480x360_souvenir-image-xislcjnzmkynvgrhorntgq.png', '新竹市', '北區', 24.84692709999, 120.9244968999),
(2, '虱目魚香腸', '在歷經數各多月的研發，終於研發出來了吃得到虱目魚肉細緻綿密的魚肉口感，魚肉跺碎沒經過打漿直接灌入腸衣成型，就外觀上完完全全就是香腸的面貌，口感上絕對是真實的虱目魚口味。虱目魚肉，低脂肪、低熱量、高蛋白的美味健康訴求，將會是您的新選擇！', '06-222-2210', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18963/480x360_55420201209.jpg', '臺南市', '安平區', 22.99682, 120.15362),
(3, '蓮藕粉組合式禮盒', '現有蓮藕粉產品包裝比較傳統，普遍無法吸引年輕消費族群購買本產品；擬由本計畫設計製作優質具吸引力之蓮藕系列產品包裝袋及包裝禮盒結合各式產品豐富組合式伴手禮盒內容，吸引更多國人與年輕人購買慾望。提高蓮藕系列產品價值，增加消費者購買意願，可讓消費者送禮自用兩相宜。', '06-685-0923', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18965/480x360_60720201209.jpg', '臺南市', '白河區', 23.3516817, 120.4157373),
(4, '湖苺戀系列', '精選新鮮豬肉，以特殊配方及加入「湖莓戀」 (草莓酒系列)來增加酒香，精心調製而成。口感香嫩、口齒留香，可以冷鍋、冷油、小火調理或用汆燙後再烤口感更佳。吃上一口香腸再加上一杯湖莓戀更能享用美食。', '03-799-4800', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31823/480x360_souvenir-image-qk-qogx7le2jsmspazworq.jpg', '苗栗縣', '大湖鄉', 24.4289864, 120.8660828),
(5, '有神酵系列、青梅養生系列', '有神酵系列 \n1.好好酵-梅子酵素+精神強-古法梅精+有好醋-果釀梅醋共三罐。 \n2.完全採用信義鄉出產之優質青梅。 \n3.易開罐設計，取用方便，開封後完整密封。 \n4.採用梅果原釀而成，自然健康。 \n5.不添加任何防腐劑、色素。 \n6.稀釋5-8倍食用。  \n產地同步特價優惠實施中。 \n信義鄉農會本著奉茶的分享精神，堅持採用信義鄉在地嚴選青梅，並將本地流傳數十載的傳統養生加工法，傳承保留再藉由大葉大學的製程分析研究創新，技術完整轉移後開發出一系列養生梅產品。\n（梅子酵素-好好酵 ）\n梅子酵素-好好酵 完', '0975-801-959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18969/480x360_55120201209.jpg', '南投縣', '信義鄉', 23.68870099999, 120.861791),
(6, '凍頂茶原片茶包', '本級茶使用優良之凍頂茶，經本社品管之烘焙，品質佳，價位適中，是上選之原片茶包。', '080-008-8118', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18971/480x360_55820201209.jpg', '南投縣', '鹿谷鄉', 23.7603933, 120.7376796999),
(7, '金瓜濃湯精緻禮盒', '南瓜濃湯-純台灣在地生產的南瓜製成，原料新鮮，無藥物殘留。添加澳洲有機脫脂奶粉、洋蔥、小麥、卵磷脂、葡萄糖、葡萄糖胺等製成。\n南瓜粉-純台灣在地生產的南瓜製成，原料新鮮，無藥物殘留。可做為牛奶、豆漿、麥片...等流質食品的添加物，亦可做為料理食材，使用方便，營養不流失。產品主要原料與本鄉蔬菜產銷班契作本產南瓜，使用在地農產品比例佔90%以上。\n純台灣農產品製成，原料新鮮，包裝具鄉土味，送禮大方實惠。', '05-787-2008', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18973/480x360_56020201209.jpg', '雲林縣', '四湖鄉', 23.6377404, 120.2226555),
(8, '凍頂蜜香貴妃茶', '', '080-008-8118', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31845/480x360_souvenir-image-ebsmdk-x30skac_qhnsmpw.jpg', '南投縣', '鹿谷鄉', 23.7603933, 120.7376796999),
(9, '玫瑰花纖體醋', '以糯米釀造而成的釀造醋為基底，浸漬埔里山城特選優質安全的玫瑰花，將玫瑰獨特的香氛和有益成份完整的釋放到醋裡面陳年釀造醋滑潤順口，玫瑰清香濃郁；因取用天然現採玫瑰，即使稀釋後飲用，依然芳香撲鼻、香味持久，玫瑰花中的有\n益成份讓身體充分吸收，更是受女性朋友喜愛的飲品。\n主要成分：玫瑰花、糯米\n食用方式：以冷開水或冰塊稀釋二至四倍', '04-9298-6420', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18975/480x360_56520201209.jpg', '南投縣', '埔里鎮', 23.963721, 120.9641679999),
(10, '薄鹽鯖魚片禮盒', '', '03-996-2108', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31839/480x360_souvenir-image-ut14oed2iegq12hero38kg.jpg', '宜蘭縣', '蘇澳鎮', 24.5833868, 121.8659277),
(11, '田中特選-田穗系列組合禮盒', '', '04-874-9211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31849/480x360_souvenir-image-clsyeuxw0uku6mzv-ckqaq.jpg', '彰化縣', '田中鎮', 23.85965819999, 120.5863643),
(12, '烤冰薯', '購買資訊：農創家電商-沙鹿區農會', '04-2662-3111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31840/480x360_souvenir-image-qheormwqeu-cfh3_ihopwa.jpg', '臺中市', '沙鹿區', 24.2360788, 120.5589866999),
(13, '下營三寶尊爵伴手禮盒', '', '06-689-2209', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18983/480x360_58620201209.jpg', '臺南市', '下營區', 23.2372631, 120.2672728999),
(14, '香得益彰禮盒', '經由農會挑選濁水溪流域之良質米稻米生產專區、田中酪農專區、二林雜糧作物生產等區所生產優質農產品加工組合而成的最佳伴手禮。', '04-726-3460', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18985/480x360_59320201209.jpg', '彰化縣', '彰化市', 24.0744019, 120.54353),
(15, '洛神花系列', '洛神花又名洛神葵，台東金峰鄉產量最多，因品質最好，先天環境極佳，加上環境和空氣純淨無污染，花萼果實製成的食品，讓人吃起來特別放心。', '08-978-0400', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18991/480x360_62420201209.jpg', '臺東縣', '太麻里鄉', 22.604987, 121.001855),
(16, '一口香鹹蛋', '翻轉鵪鶉蛋產業將小巧一口鳥蛋，化身各式風味蛋製品，鹹香鳥蛋，鹹度適中，蛋黃香氣濃郁，肚子餓時來一顆，補充蛋白、活化腦細胞。\r\n產地直送，如何訂購: 陽光鵪鶉牧場', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19001/480x360_64920201209.jpg', '', '', 0, 0),
(17, '純正野生烏魚鬆', '悟棲漁港所捕獲的野生烏魚品質優良，漁會為保留這彌足珍貴的季節滋味，進一步結合生產及專業加工廠，以傳統古法結合現代科技製成美味烏魚鬆；百分百純魚肉製成，不添加防腐劑，營養健康，老少咸宜。\r\n產地直送，如何訂購純正野生烏魚鬆: https://www.tcfish.org.tw/', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19003/480x360_66320201209.jpg', '', '', 0, 0),
(18, '戀戀大湖', '有大湖草莓精心釀製的微醺，香甜的草莓果香躍然而出，在唇齒間漸漸融化的甜蜜滋味；還有嚴選泰安紅肉李精心釀製的醉香，口感豐厚，略些澀味，餘韻散發淡淡杏仁香味，冰鎮享用後風味更佳。', '03-799-4800', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19005/480x360_62820201209.jpg', '苗栗縣', '大湖鄉', 24.4289864, 120.8660828),
(19, '山蕉捲捲酥', '延續集集山蕉風光金蕉年代，完全使用100%山蕉果泥製作成蛋捲形狀，絕無添加任何人工添加物，使用德紐進口奶油，每一口都能吃到山蕉果泥綿密口感。\r\n產地直送，如何訂購: 集元果 線上訂購', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31876/480x360_souvenir-image-pgkdbcnjx0-ybzxv0nhmnw.jpg', '', '', 0, 0),
(20, '金孃紅', '以農場自產的蔬菜馬齒莧(代表堅持)+在地農產品草莓果實(代表甜蜜) + 社區區花桃金孃果實(代表愛神)研發而成，並以農場毗鄰的濕地同心池為造型，將手工製作的餅乾取名為「同心愛神餅」，要讓嚐到此餅的人都感受到幸福浪漫的滋味。\r\n產地直送，如何訂購: 農驛棧農場', '0912-472-001', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19007/480x360_20210301.jpg', '臺北市', '內湖區', 25.1012, 121.589639),
(21, '貓空山色茶明信片茶包', '傳承百年工藝的木柵好茶，藉由茶包明信片文字傳達關心，熱茶傳遞溫度。輕鬆沖泡享受三色好茶，清香淡雅的包種茶、蜜香甘甜的觀音紅茶、濃香醇厚的鐵觀音茶，茶煙繚繞看似深遠的貓空山色雲霧之美。\r\n產地直送，如何訂購: 美加茶園', '02-2938-6277', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19009/480x360_20210302.jpg', '臺北市', '文山區', 24.971146, 121.5877162),
(22, '鱘龍魚干貝XO醬', '本產品由農場主人親自研發調配,主食材採用自家漁場孵化養殖的鱘龍魚,從養殖到出貨均自產自銷一手把關,訴求天然營養、健康不油膩,讓大家也都輕鬆吃到帝王級的鱘龍魚伴手禮\r\n產地直送，如何訂購: 鱘龍魚干貝XO醬 千戶傳奇生態農場官網', '02-2672-0748', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19011/480x360_20210304.jpg', '新北市', '三峽區', 24.845270488675517, 121.44951224327087),
(23, '親親草泥馬', '草泥馬呆萌超吸睛、療育人心，大人小孩都喜歡。觸感柔軟絨毛玩偶遇上農場香草植物自製的香囊，[親親香囊草泥馬]-要帶我回家喔!Live,Life,Love(Live The Life You Love, Love The Life You Live)\r\n產地直送，如何訂購: 富田花園農場', '03-387-2540', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19013/480x360_20210305.jpg', '桃園市', '大溪區', 24.839922, 121.284528),
(24, '魚香XO拌醬', '用在地漁業友善飼養及現撈魚貨為主原料製作，產地直送的差異性秉持傳統手工製法結合現代美食新概念堅持無色素無防腐劑保留漁獲的原色原味給顧客最安心的品質\r\n產地直送，如何訂購: 水月休閒農業區', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19015/480x360_20210306.jpg', '', '', 0, 0),
(25, '豆腐乳、哈密瓜醬瓜、洛神花蜜餞', '傳承ㄚ嬤的手路利用在地的產業，開發哈密瓜醬瓜、洛神花蜜餞、無添加防腐劑、吃得安心、還有媽媽的愛心與味道。\r\n產地直送，如何訂購: 官夫人田園料理', '03-925-3517', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19019/480x360_20210308.jpg', '宜蘭縣', '壯圍鄉', 24.71944885883543, 121.80688977241516),
(26, '叻沙湯包與鯛魚片', '鯛魚片:太平洋海水養殖的健康美味海鮮，用天然的飼料餵養健康的魚，加上低密度養殖，讓魚更活動空間，魚肉更Q彈。叻沙湯包:搭配南洋風味的叻沙獨特風味湯底，給你一個暖暖的胃、滿嘴的鮮美。\r\n產地直送，如何訂購: 大塭休閒農場', '03-987-0558', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19021/480x360_20210309.jpg', '宜蘭縣', '礁溪鄉', 24.819497045332145, 121.79645597934723),
(27, '旺山綠蜂膠淨手液', '綠蜂膠珍貴素材可提高手部清潔效果，蜂蠟讓手部可達防護及保濕，台灣綠蜂膠及蜂蠟，結合酒精、茶樹、金銀花及尤加利，強化清潔、防護、速乾及保濕效果，最適合頻繁使用酒精淨手的使用族群，降低酒精導致手部乾裂情形發生。', '0932-088-992', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19023/480x360_202103010.jpg', '宜蘭縣', '壯圍鄉', 24.7266496, 121.807281),
(28, '有機三穀米', '一年一耕 地力飽滿 順性自然 米粒Q軟 民豐有機米為一年一期作，藉由二期休耕種植綠肥作物，使土壤有充足時間修養生息，恢復有機養分、地力重新飽足。人類友善地取之自然、大地豐沛地永續回饋，種出來的米粒才有風土的富饒馨香，煮出來的飯才會健康、營養、美味。成立民豐的礁溪鄉有機米產銷班，自83年實行有機耕法，默默俯仰天地、與自然共生，現已擴充栽種面積達50公頃，培育出纖維含量高的多元品種，將最真實的蘭陽人情味、在地味，帶給同樣熱愛這片土地的人們。\r\n產地直送，如何訂購: 民豐有機農場', '03-928-2202', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19027/480x360_202103012.jpg', '宜蘭縣', '礁溪鄉', 24.792873, 121.757648),
(29, '點頭蒜香醬料、天生好辣醬料', '宜蘭以三星蔥及蒜為名，搭配在地青農的食材（合作社社員為主），使用次級的蔥、蒜、辣椒，開發出萬用的獨特醬料，除了符合循環經濟之精神，也讓消費者能在回家後輕鬆料理。\r\n產地直送，如何訂購: 青出宜蘭', '03-989-1280', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19029/480x360_202103013.jpg', '宜蘭縣', '', 24.706573, 121.712229),
(30, '那山那谷精釀啤酒', '那山那谷馬告精釀啤酒：使用泰雅族的傳統香料「馬告」釀製的精釀啤酒，擁有柑橘、檸檬、香茅等多層次清香，氣泡細緻滑順，單獨品飲或是佐餐飲用都是絕佳的享受。\r\n產地直送，如何訂購: 那山那谷FACEBOOK\r\n未滿18歲禁止飲酒，飲酒過量，有害（礙）健康', '0933-119-250', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19031/480x360_202103015.jpg', '宜蘭縣', '南澳鄉', 24.424623, 121.7493784),
(31, '戀戀冬山-柚花烏龍茶巧克力', '鮮為人知的宜蘭茶，運用地圖的方式讓大家認識宜蘭冬山地區，裡面使用在地友善環境無農藥的文旦柚花與烏龍茶，冷泡熱泡都適合，搭配72%紅茶黑巧克力杏仁豆，苦中帶甘，回甘花香，品嚐多層次的享受。\r\n產地直送，如何訂購: 星源茶園', '03-958-8947', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19033/480x360_202103016.jpg', '宜蘭縣', '冬山鄉', 24.6297604, 121.7480707),
(32, '果然幸福果汁、果然幸福果醬', '1.產品(確定)名稱：果然幸福 2.故事定位：來自中央山脈及雪山山脈的天然湧泉，用友善耕作蘊育天然、純凈、無毒的幸福果食，讓您體驗果然幸福的生態禮物。3.產品賣點(優於別人的地方)：用友善耕作-自然農法；蘊育天然、純凈、無毒的幸福果食4.產品與農場結合的特色：運用四季農作物透過生態體驗，讓遊客親身體驗手做DIY', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19035/480x360_202103017.jpg', '', '', 0, 0),
(33, '耕堡冰淇淋', '主打健康的理念，耕堡冰淇淋無添加人工色素，香料讓您每一口都吃的到健康幸福美味，產地直接用新鮮有機蔬果所製做的冰淇', '0988-015-066', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19037/480x360_202103019.jpg', '宜蘭縣', '三星鄉', 24.6423883, 121.6079797),
(34, '祕釀樹葡萄醋', '採用自產有機樹葡萄,委託生偉生技股份有限公司製作,以天然發酵的方式製成，營飬價值高，好喝又順口。\r\n產地直送，如何訂購: 花泉休閒農場', '03-922-1506', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19039/480x360_202103020.jpg', '宜蘭縣', '員山鄉', 24.72731340064455, 121.70462250709534),
(35, '鮮奶起司棒、乳牛斑點饅頭', '鮮奶起司棒始以鮮奶麵粉跟帕瑪氏起司粉做成，吃起來脆脆的有濃濃的奶香起司味。乳牛斑點饅頭以純鮮奶加入麵粉內製作，過程中無任何添加物，斑點以竹炭可可粉製成來點綴，製作出可愛的乳牛斑點饅頭。\r\n產地直送，如何訂購: 田媽媽寶聰牧場點心坊', '03-3818015', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19041/480x360_202103021.jpg', '桃園市', '大園區', 25.02750172440004, 121.2300582964184),
(36, '雪霸明日葉粉', '1.低溫磨粉，營養不流失 2.輕巧包裝，方便攜帶3.滅菌處理，安全無虞4.富含有機鍺及查爾酮\r\n產地直送，如何訂購： 新竹五峰鄉雪霸休閒農場(官方網站)', '03-585-6192', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19043/480x360_202103023.jpg', '新竹縣', '五峰鄉', 24.5276476, 121.1178128),
(37, '風將烏魚(子)禮盒', '本產品由福樂休閒漁村自家養殖的烏魚加工製作生產:1.養殖過程都經過定期送檢2.製作出的產品皆經過ETC檢驗合格\r\n產地直送，如何訂購: 福樂休閒漁村', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19045/480x360_202103024.jpg', '', '', 0, 0),
(38, '大山珍竹 天然桂竹筍', '橫山鄉大山背山，中海拔，自然產出獨特鮮美的桂竹筍，堅持天然無化學添加，為新竹橫山特有伴手禮---大山珍竹，有客家「山」的味道。\r\n產地直送，如何訂購: 大山背休閒農場', '0988-909-769', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19047/480x360_202103025.jpg', '新竹縣', '橫山鄉', 24.695647553000455, 121.13428831100464),
(39, '香魚酥排', '香魚酥排是以自家天然湧泉養殖,全程低溫真空油炸緊緊鎖住鮮味,魚骨已化成酥脆,美食行家華麗享受。\r\n產地直送，如何訂購: 八甲休閒魚場', '03-922-5990', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19049/480x360_202103026.jpg', '宜蘭縣', '員山鄉', 24.7265114, 121.7029361),
(40, '鳳梨豆腐乳', '一般豆腐乳都以豆米發酵而一佳村則以鳳梨發酵鳳梨為活性酵素\r\n產地直送，如何訂購: 田媽媽一佳村養生餐廳FB', '03-958-8852', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19051/480x360_202103027.jpg', '宜蘭縣', '冬山鄉', 24.627306, 121.748639),
(41, '賀ㄟ米粉', '嚴選農場的手工日曬荷葉，100%純有機米製成，使用沖泡方式享用快速方便，口味絕不馬虎，獨特調製味噌及香菇口味，美味即刻上桌\r\n產地直送，如何訂購: 蓮荷園休閒農場', '03-477-6972', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19055/480x360_202103029.jpg', '桃園市', '觀音區', 25.0129601, 121.1158275),
(42, '烏樹林香草啤酒、烏樹林香草冰淇淋', '香草啤酒-烏樹林自產香草莢和委託龍潭當地冠軍啤酒廠釀製。香草莢為全世界第二貴之香料植物，其獨特的小起配合小麥啤酒的奶油氣味融合出獨特的香甜口感，是能夠細細品嘗的精釀啤酒。 有鑑於市面販售之香草冰淇淋多以化學合成香草精為添加物，烏樹林能以自產香草莢與飛牛牧場長久以來冰淇淋製成技術合作，生產之香草冰淇淋是以真食材製作，讓國人能夠品嘗真正的香草冰淇淋。\r\n產地直送，如何訂購: 烏樹林從前從前-烏樹林Before \r\n \r\n未滿18歲禁止飲酒，飲酒過量，有害（礙）健康', '0905-016957', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19057/480x360_202103030.jpg', '桃園市', '龍潭區', 24.889357223344508, 121.21816658012138),
(43, '精油聞香木', '能形成一瓶精油的背後，是需要大量的時間與精力照顧，日復一日到適當的時機後大量採收萃取出一瓶100%濃縮精油，讓我們能透過1-2滴便能享受數百朵植物精華的能量，芳香分子能活化我們的身心靈給予正面情緒，並能抗菌，也使顧客更重視自然生態植物的重要性一份香氣不單只是氣味上的感覺，氣味會連結大腦海馬體，用久了是一份回憶記憶、磁場、正能量與平衡身心靈的狀態，是大自然留給我們有生命的禮物，並不是虛假化學合成無生命的香精調製的都市人住的房子較沒有空間種花草、但你買一瓶、兩瓶精油猶如一座森林\r\n產地直送，如何訂購: 花露休閒農場', '04-2589-1589', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19059/480x360_202103032.jpg', '苗栗縣', '卓蘭鎮', 24.333245177629507, 120.82616686820984),
(44, '飛牛水果-乳清優格(百香芒果、紅龍果蜜桃、鳳梨)', '好function＋好放心的飛牛水果乳清優格，利用複方生乳製作新鮮乳清，結合小農水果及複合菌株發酵，開發成含免疫球蛋白與乳鐵蛋白等無人工添加物食品，讓兒童、上班族女性、銀髮族能透過日常飲食來進行保健\r\n產地直送，如何訂購: 飛牛牧場', '03-778-2999', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19061/480x360_202103033.jpg', '苗栗縣', '通霄鎮', 24.441267, 120.741294),
(45, '能量黑寶貝/能量茶寶貝', '柴燒陶與在地小農農產品結合，位在客家庄的我們，仙草是小時候阿公年代家家戶戶幾乎都有栽種的植物，後來因種植過程工序繁瑣，漸漸很多人都沒有栽種了，培育友善仙草希望結合農事體驗及伴手禮開發，推廣這古早味的好。在地仙草乾、貓貍紅茶兩種產品(本農場已與在地小農長期合作在案)，並與休閒農業區小農契作共應，導入專屬柴燒杯與仙草及貓貍紅茶組合方式，並強調友善特色應可與其他農產品區隔。&#39;\r\n產地直送，如何訂購: 春田窯陶藝休閒渡假園', '03-787-7820', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19063/480x360_202103034.jpg', '苗栗縣', '三義鄉', 24.4093568, 120.7991899),
(46, '蘿蔔錢', '蘿蔔錢是用新鮮蘿蔔洗淨切薄片,直接曬成菜乾,不醃漬不加鹽,是苗栗縣內客家庄特有的名產\r\n產地直送，如何訂購: 神雕邨複合式茶棧', '03-787-5858', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19065/480x360_202103035.jpg', '苗栗縣', '三義鄉', 24.4054203, 120.7608063),
(47, '阿嬤的古早禮', '堅持選用零除草劑、零農藥、健康土壤栽培的老薑，在客家阿嬤的技術傳承下，純手工製作滿馥濃郁黑糖香氣的薑糖。\r\n產地直送，如何訂購: 水寨一方農場', '0920-210-383', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19069/480x360_202103039.jpg', '臺中市', '東勢區', 24.2591134, 120.8668735),
(48, '客家肉粽', '來自台中山城客家庄以古早傳統的道地口 味感覺，採買特選梅花肉、上等蝦仁、新社香菇、蔥頭等食材；將糯米大火蒸熟拌入蔥酥香油條味，包入炒香的餡料，再次蒸熟讓米香、餡香與粽葉香融合，呈現多層次口感與味覺。\r\n產地直送，如何訂購：石岡傳統美食小舖', '04-2572-1490', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19071/480x360_202103042.jpg', '臺中市', '石岡區', 24.273696985716438, 120.78894317150115),
(49, '好樂農莊台灣籽苦茶油', '好樂農莊 來自台中新社賴建宏與農媳婦江嘉維承接長輩東勢區的苦茶園，夫妻都是農業門外漢一步一腳印堅持友善環境種植，種植採收到販售不假他人之手，延伸發展經營休閒農場，場域提供農事活動，深度體驗農村生活。秉持初衷 生產好的農特產品 樂於分享每件事情\r\n產地直送，如何訂購: 好樂農莊/阿公ㄟ公園 | Facebook', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19073/480x360_202103043.jpg', '', '', 0, 0),
(50, '山豬愛吃蘋果脆片', '農場為臺大園藝系實習場域，以友善環境方式生產蘋果，並採冷凍乾燥加工製作脆片，保留蘋果營養及風味，口感鬆脆清爽，單一材質包裝以利回收。\r\n產地直送，如何訂購: 國立臺灣大學生物資源暨農學院附設山地實驗農場', '04-9280-3891', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19077/480x360_202103045.jpg', '南投縣', '仁愛鄉', 24.06987069999, 121.1678663999),
(51, '探好茶', '將孟宗竹炭與茶葉結合，包裝設計成伴手禮，「探好茶」它的自然甘醇、獨特鮮美的滋味，希望能成為當地特色伴手禮。\r\n產地直送，如何訂購：武岫休閒農場', '04-9267-6262', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19079/480x360_202103046.jpg', '南投縣', '鹿谷鄉', 23.718823659569427, 120.7611232995987),
(52, '院落花草茶', '我們對【院落花草茶】的真.誠.訴.求1. 融合調香專業，調製獨特精緻風味2. 72小時冷風乾燥無毒花草 3. 無添加香料、色素、防腐劑 4. 選用台東無毒香草及南投「神農獎」茶師製作的茶葉 5. 四款茶均經過「560項無農藥殘留檢 驗合格 」6. 使用PLA玉米澱粉安全環保茶包', '04-9227-3797', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19081/480x360_202103047.jpg', '南投縣', '名間鄉', 23.8646515, 120.6751937),
(53, '日月潭紅茶拿鐵米餅 (奶素)', '全台唯一『100%日月潭產地紅茶』米餅，清新紅茶香氣和入口即化的綿密口感，日月潭必買伴手禮！使用全日月潭產地紅茶，結合台灣米製作，入口即化輕食小點心，一口接一口的美味，搭配日月潭紅茶享受愉快的點心時光。\r\n產地直送，如何訂購: 日月潭-日月山莊 (facebook.com)', '04-9289-5899', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19083/480x360_202103048.jpg', '南投縣', '魚池鄉', 23.89716770159862, 120.93401849269867),
(54, '活體蛤蜊、魚鬆、醃蛤、醃蜆', '哈哈漁場處於 彰化名列台灣文蛤前三大產區，而位於斗苑休區、漢寶專業養殖區的「哈哈漁場」，也以養殖 文蛤著稱，並混養白蝦、虱目魚、黃錫鯛等魚蝦。漁場在主人陳明瞭的精心規劃下，來到這裡不但 可以下水「摸蛤仔」，還有「漁夫牽魚」、「收蛇籠捕蝦」等特色活動可以參加，藉由親自操作、 切實了解漁夫工作以及各種不同的捕魚工具如何利用。此外，在陳明暸親切活潑的帶領及講解下， 大家還能在充滿歡笑的輕鬆氣氛中進一步了解當地河口生態，並就地享用「尚青」魚場、採買新鮮 的活力蝦、元氣蝦等魚場特產，開心感受「一日漁夫」的生活！ 明瞭哥原本是做工廠的，後來中年的時候在思考要轉職，當時就決定回來家鄉做養殖業，起先 他先跟別人租養殖魚塭，慢慢拓展到有自己的魚塭，在做養殖魚塭的同時，他也思考要如何將這裡 的生態環境以及漁業概念傳遞下去，就先開啟作海岸教育的活動（講解生態等等），大約六年前左 右才開始規劃比較完整的一套體驗活動，明瞭哥採取的方式，適用較天然且貼近自然的魚塭做生態 講解，並且實地教大家如何牽罟、摸蛤仔，還可以體驗 DIY 做魚丸、魚鬆等，可以說是最好的教育 場所。\r\n \r\n產地直送，如何訂購: https://www.facebook.com/hahafish168', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19085/480x360_202103049.jpg', '', '', 0, 0),
(55, '珍的好蝦', '彰化生態蝦(白蝦)是以文蛤池混養海水養殖成長環境無用藥口感Q彈鮮甜海味。珍的好蝦以真空烘培不添加任何調味料保持原有鮮蝦味。健康安全鮮甜營養。其養殖過程全程無用藥，以自然生態方式飼養為用人工飼料天然飼養成，所以又叫人工蝦\r\n產地直送，如何訂購：王功陽光水棧海鮮餐廳', '04-893-3323', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19087/480x360_202103053.jpg', '彰化縣', '芳苑鄉', 23.969086, 120.3280699),
(56, '天使樂園無花果茶', '產品名稱：天使樂園無花茶 產品賣點：1.自然農法(運用蚯蚓土和天然酵素)、2. 380+1項農藥零檢出、 3.身心障礙者所運用自身能力種植出顆顆精華之果實。產品與農場結合的特色：無花果和農場身心障礙者一樣，似花飛花、似懂非懂，卻在這樣美麗錯誤釀成一顆顆晶瑩剔透之花蜜。產品最佳使用方法介紹：一包無花果茶可以沖泡2-3回，若想自行添加甜度可加入蜂蜜。', '04-8681199#166', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19089/480x360_202103054.jpg', '彰化縣', '二林鎮', 23.95070446156215, 120.44292211532591),
(57, '蝶藝．蘭舞', '40年來「展壯台大休閒農場」堅持以「品質第一，顧客優先」的理念，搭配即時的物流系統，將「幸福向你飛來」之蝴蝶蘭伴手禮送至客戶的手上。體積小巧的伴手禮，良好居家擺飾，品味提升。\r\n產地直送，如何訂購: 展壯台大蘭園', '04-852-7335', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19091/480x360_202103055.jpg', '彰化縣', '大村鄉', 23.996891360090817, 120.55876806378365),
(58, '菇貓水餃', '菇貓水餃採用太陽能温室栽種的極品珊瑚菇與過貓、黑木耳、紅蘿蔔，葷食採用CAS認證當天現宰國產温體豬肉,激盪出味蕾新體驗\r\n產地直送，如何訂購: 晁陽綠能農場', '05-693-8238', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19093/480x360_202103056.jpg', '雲林縣', '麥寮鄉', 23.75121035973045, 120.29707629744584),
(59, '牛樟原萃手工皂', '山上種樹牛樟手工皂是採用自家農場萃取的牛樟純露配合牛樟精油製作而成，純天然製作，可以緩解皮膚過敏的問題。\r\n產地直送，如何訂購:山上種樹 牛樟森林休閒農場', '03-799-7262', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19095/480x360_202103057.jpg', '苗栗縣', '大湖鄉', 24.39726736578472, 120.87853163480759),
(60, '紅棗香腸、香煎芋糕', '運用在地的食材，紅棗露搭配黑豬肉罐製出獨特香氣與美味的伴手禮也是店內的招牌菜之一。 香煎芋糕有別於傳統的芋粿，我們使用友善栽種的公館芋頭加上現磨的在來米、上等豬肉、香菇、油蔥等，手工制做的芋糕切片煎過後，吃起來口感外酥內軟，芋頭香味濃郁，而且沒有添加任何防腐劑，大家可以安心食用。\r\n產地直送，如何訂購: 田媽媽巧軒餐館', '03-722-6868', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19097/480x360_202103058.jpg', '苗栗縣', '公館鄉', 24.472237, 120.8195033),
(61, '金葉山莊《我們不一樣》橄欖', '臺灣橄欖、原汁鮮味，30年手藝經典天然再現吃過就知道不一樣，懷念的客家食療30年傳統古法手藝製作，經典再現橄欖鮮味，茶食、冰鎮、煲湯皆宜。', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19099/480x360_202103060.jpg', '', '', 0, 0),
(62, '紅茶麵、木耳麵', '貓裏紅茶具有紅茶與烏龍茶之果香，以此茶研磨成粉來製作麵條，讓麵條具有紅茶之風味。木耳皆是本地農民使 用友善耕作，所種植的新鮮木耳，木耳也富含膳食纖維可以幫助腸胃蠕動有助於保護腸胃。 木耳皆是本地農民使用友善耕作，所種植的新鮮木耳，木耳也富含膳食纖維可以幫助腸胃蠕動有助於保護腸胃。\r\n產地直送，如何訂購： https://pse.is/3gruap', '03-732-0530', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19105/480x360_202103063.jpg', '苗栗縣', '苗栗市', 24.56744133048793, 120.83109274506569),
(63, '國宴珍珠包', '羅美蘭女士於90年榮獲神農獎肯定，蒙總統召見。[國宴珍珠包]為純手工精製，内餡特選潭子區特產-麻竹筍，經不斷的精進，結合Q彈的外皮，終獲選為91年總統招待外賓的國宴點心及107年總統府家庭日之佳餚。\r\n產地直送，如何訂購:欣燦客家風味餐廳', '04-2534-5178', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19107/480x360_202103065.jpg', '臺中市', '潭子區', 24.2088168, 120.7032117),
(64, '綿密狀元糕', '狀元糕源於喜事訂婚糕點，也為糕餅文化由來之一。糯米粉須以手工攪拌與各食材揉合才不會結團是糕仔好吃的秘訣，綿細紮實能入口即化，甜度適宜帶來樸實高雅的滋味。狀元糕承載喜餅禮俗、祈福祭祀等許多節日祝福象徵，更是林金生香百年最重要的傳承。\r\n產地直送，如何訂購:  林金生香', '04-2389-9857', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19109/480x360_202103066.jpg', '臺中市', '南屯區', 24.1380735, 120.6390802),
(65, '蔥師傅黑芝麻青蔥脆餅', '台中市大安區優質產銷履歷青蔥加上台灣在地產黑芝麻，青蔥香味加上營養的黑芝麻，青蔥香味加上營養的黑芝麻，口感豐富且帶層次性的風味，本次伴手禮開發以大安區農會師傅品牌為主導，將餅乾視為一道精心料理的佳餚\r\n產地直送，如何訂購: 台中市大安區農會', '04-2671-2229', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19111/480x360_202103067.jpg', '臺中市', '大安區', 24.3463873, 120.5870304),
(66, '豐茶師花果茶', '在臺灣最知名茶區:鹿谷，深藏著許多茶葉職人。在這老茶區，茶葉由專業茶農師傅細心照料，再由製茶師 承襲傳統手藝經由委凋、揉捻等步驟製成臺灣好茶。我們串連了在地花果農民，開發出茶與花、果的複方產品。 讓您一同品嚐台灣農村的美好。\r\n產地直送，如何訂購: 田媽媽小半天風味餐坊', '0919-743-732', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19113/480x360_202103070.jpg', '南投縣', '鹿谷鄉', 23.731745, 120.7491051),
(67, '蜜地瓜', '將懷舊口味創新賦予食材新的生命，循古法慢火熬煮口感清香，綿密軟Q，是伴手最佳點心，冷凍後食用更好吃，包裝設計簡單清新攜帶方便，自用送禮兩相宜\r\n產地直送，如何訂購: 田媽媽竹山耄饕客棧', '0933-522-530', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19115/480x360_202103071.jpg', '南投縣', '竹山鎮', 23.7635541, 120.687042),
(68, '魔菇小食點-可口菇脆片', '可口菇脆片，嚴選魔菇部落特產黑美人菇和杏鮑菇，清香酥脆，健康享受美食，讓您愛不釋手，一口接一口。多種調味一次滿足送禮自用皆大歡喜。\r\n產地直送，如何訂購: 魔菇部落休閒農場', '04-852-1898', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19121/480x360_202103074.jpg', '彰化縣', '埔心鄉', 23.973773, 120.5204213),
(69, '龍眼乾', '今夜星辰休閒農場並效力於協助地方果農結合資源推廣地方農特產與人文特色觀光產品是與彰化石牌社區當地優質果農手工柴火燒而成，口感甜而不膩具獨特風味可作為可口零食又或者入菜調味，對人體養生非常有益\r\n產地直送，如何訂購: 今夜星辰休閒農場', '04-732-3111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19123/480x360_202103075.jpg', '彰化縣', '彰化市', 24.0521554, 120.6064615),
(70, '玫瑰洛神冰淇淋', '一款充滿著愛情浪漫與健康的冰淇淋，在不加色素及香精之堅持下，讓你看到多色玫瑰花瓣，聞到天然玫瑰花香，嚐到洛神微酸的滋味，感受到戀愛的滋味，浪漫的享受，在低卡低脂微甜的要求下，愛吃冰的你一定要愛上它!\r\n產地直送，如何訂購:  曙光玫瑰莊園', '05-259-2932', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19125/480x360_202103076.jpg', '嘉義縣', '番路鄉', 23.4582576, 120.5376963),
(71, '媠茶Sweet Day Herb Tea', '首創以四大美人西施黛玉飛燕貴妃為發想，設計開發出最具故事性，且最精緻的草木本植物沖泡飲\r\n產地直送，如何訂購: 大智慧養生農場', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19127/480x360_202103077.jpg', '', '', 0, 0),
(72, '鰲鼓海藻醋', '在風和日麗北緯23.5度東石向禾漁場營造以紅樹林生態的友善環境養殖，養殖低碳低電的海菜混養低密度的魚蝦蟹貝螺形成食物鏈，結合貓狗羊鳥等動物來協助管理漁場，不用任何藥物並搭配智慧水質管理系統，搭配在地小農養殖出高營養高膠原蛋白的海菜\r\n產地直送，如何訂購: 向禾休閒漁場', '05-360-0959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19129/480x360_202103078.jpg', '嘉義縣', '東石鄉', 23.507773, 120.148785),
(73, '精油手工皂旅行組伴手禮', '讓消費者可以同時感受彷彿進入森林的芬多精體驗，並且盡力達到無毒無汙染的天然永續\r\n產地直送，如何訂購: 五柳園休閒農場', '05-271-9500', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19131/480x360_202103079.jpg', '嘉義市', '東區', 23.48969824871362, 120.48877716064453),
(74, '琉璃茶享', '琉璃茶享茶便當系列:內包含3款阿里山特色茶-冷韻烏龍、甘醇金萱、蜜香紅茶，以原片包裝的長條形茶包，適合冷泡及熱泡，是外出旅行及上班族的不二選擇', '05-256-1523', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19133/480x360_202103080.jpg', '嘉義縣', '竹崎鄉', 23.46530077658772, 120.68995624780655),
(75, '古法燻羊肉', '古法燻羊肉採用在地放野山羊、13種珍貴中藥材及台灣菸酒公司米酒一起放入大甕中，經一天一夜用稻殼燜燻而成，羊肉口感獨特，入口即化。\r\n產地直送，如何訂購 : 王家燻羊肉', '06-594-1393', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19135/480x360_202103081.jpg', '臺南市', '龍崎區', 23.005962148216614, 120.3888487815857),
(76, '鴨爸爸的蛋-鴨爸蛋捲(原味)', '台南鴨莊自家生產新鮮鴨蛋 搭配天然奶油 低筋麵粉 砂糖 沒有防腐劑 香濃味美。\r\n產地直送，如何訂購: 台南鴨莊休閒農場', '06-690-1101', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19137/480x360_202103082.jpg', '臺南市', '官田區', 23.1735158, 120.3382861),
(77, '筍漬-醬筍', '農場女主人蔡媽手作的農情伴手禮，區別於一般醃醬菜的重鹹口味，只有甘甜復古滋味，循古法製作，融合了健康概念的筍漬，醬菜也可以很食尚。\r\n產地直送，如何訂購: 大坑休閒農場', '06-594-1555', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19139/480x360_202103083.jpg', '臺南市', '新化區', 23.0072204, 120.3904996),
(78, '無糖愛文芒果乾', '採用自家有產銷履歷的愛文芒果為原料，並用手工低溫烘焙，保留芒果的原汁原味，且用手工的善意呼應食材真正的價值\r\n產地直送，如何訂購: 小崇ㄉ果園', '0980-036-320', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19142/480x360_202103084.jpg', '臺南市', '玉井區', 23.1205738, 120.4636415),
(79, '足達桑竹炭防潮除臭包', '竹材取材自龍崎在地筍園第二年生竹母，為有利竹筍生長而伐下之廢棄竹材，絕對不經化學使用過程，既環保又為安心竹材。為本會自產竹炭，生產過程皆經標準程序，所有加工都由農會自行作業，因此可反映實惠價格。\r\n產地直送，如何訂購: 龍崎區農會 (longcifa.org.tw)', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19143/480x360_202103085.jpg', '', '', 0, 0),
(80, '裏 台味', '明信片式的旅行感受分享，龍眼花茶獨特性高，並深度連結在地的「與山放伴」價值。\r\n產地直送，如何訂購: 仙湖休閒農場', '06-686-3635', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19145/480x360_202103086.jpg', '臺南市', '東山區', 23.2659335, 120.4822898),
(81, '元氣飲', '500斤虱目魚骨+8隻全雞(雞隻選用牧大養雞場，動物用藥0檢出，無抗生素及成長激素)熬製3天2夜，萃取虱目魚精華，溫潤順口，鋁箔包裝加熱方便，浸泡熱水即可飲用。術後、產後、哺乳期間及成長發育中的小朋友最佳天然補充品。\r\n產地直送，如何訂購: 田媽媽長盈海味屋', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19147/480x360_202103087.jpg', '', '', 0, 0),
(82, '芭樂葉桂桑桑茶', '本農場有機芭樂客戶常談到自己或家人有血糖困擾，經查台灣目前有 200萬人有這個困擾。經有關中醫及學術界專家提供資訊將芭樂葉桑葉及肉桂葉結合有這方面養生功能，這些本農場都有種植，故江浙有機栽種的材料研發成袋茶包方便隨身攜帶使用，達養生效果。\r\n產地直送，如何訂購：乾坤有機生態農場', '0937-692-657', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19149/480x360_202103088.jpg', '屏東縣', '萬巒鄉', 22.55917161715641, 120.62750101089476),
(83, '鳳梨豆鼓醬', '單一果園 自產自製自銷 使用鴻旗有機鳳梨 獨特客家手工釀製 少許鹽 慢慢甕釀製\r\n產地直送，如何訂購 : 鴻旗有機休閒農場', '08-796-7301', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19151/480x360_202103089.jpg', '屏東縣', '高樹鄉', 22.7952705, 120.6111629),
(84, '阿信經典禮盒', '以一壺迷人香氣、不含糖、無咖啡因的可可茶，佐以健康烘烤可可杏仁果，嚴選阿信經典商品，一次品嚐精緻可可的美好\r\n產地直送，如何訂購 : 阿信經典禮盒', '08-886-9696', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19153/480x360_202103090.jpg', '屏東縣', '恆春鎮', 21.9973517, 120.7048353),
(85, '天使之約花禮禮盒', '天使花園從事專業文心蘭及熱帶蘭花生產25年，希望讓花成為一種媒介，在人、空間和時間之間傳遞情感、建立關係，將大自然給予的禮物傳遞給每個人，所以將外銷日本、法國的技術運用在花禮盒上，讓台灣民眾也能與所愛的人共享美好。讓伴手禮不僅僅侷限在食品類，用不同以往的花卉禮盒帶來不同的感動。用舞動著生命美妙的檸檬綠文心蘭、熱情的火焰美人腎藥蘭、象徵勇敢的萬代蘭，注入一股溫暖幸福的力量，帶來一份花卉的驚喜饗宴。', '08-780-1699', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19155/480x360_202103091.jpg', '屏東縣', '竹田鄉', 22.5646808, 120.5136058),
(86, '黃金果禮盒', '頂級黃金果，友善自然農法種植，營養成分極高，意象福氣，是健康伴手禮的好選擇\r\n產地直送，如何訂購: 不一樣鱷魚生態休閒農場', '0911-746-860', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19157/480x360_202103092.jpg', '屏東縣', '潮州鎮', 22.56269878679782, 120.535147190094),
(87, '微棲所', '每一個人心中都有一座秘密花園(或森林)，共享柚園對環境的美好傳遞對生態的愛與用心，你照顧植物，植物也照顧你。將「柚園」生態庭園的元素(如：植物，石頭、枯枝等)，裝入瓶中，耐陰植栽，容易照顧，偶爾澆水。在家自己安靜的依照說明書DIY，享受自己獨處的時光，與心林對話。\r\n產地直送，如何訂購 :柚園生態農場', '08-721-4446', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19159/480x360_202103093.jpg', '屏東縣', '麟洛鄉', 22.66577903929531, 120.55503845214844),
(88, '蘭欣辣汗辣椒醬伴手禮盒', '多年來生產全世界各種最辣的辣椒與加工，用傳統的加鹽醬漬日曬發酵工法製成有醬香的辣醬，用機器烘乾鮮紅的辣椒乾與粉，供應市場餐廳與網購\r\n產地直送，如何訂購: 蘭欣辣汗花果休閒農場', '08-871-7370', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19161/480x360_202103094.jpg', '屏東縣', '枋寮鄉', 22.37553554186325, 120.60230433940887),
(89, '銘泉金鑽鳳梨夾心餅', '銘泉金鑽鳳梨夾心餅爽脆酥鬆的外表下，藏不住的是天然熬煮而成的金色光澤，銘泉金鑽鳳梨特有的酸甜與獨特天然奶香相輔相成，加入少許深黑蘭姆酒點綴，更襯托出鳳梨撲鼻果香，濃郁卻不失清爽的口感讓人無法忘懷口齒留香。屏東大武山下的有機鳳梨田，歷經60年的專業種，以身土不二的概念，將農場在地生產有機鳳梨好食材，堅持零添加，分享給到訪農場的遊客。可愛鳳梨造型，天然發酵奶油搭配酸甜銘泉鳳梨餡，加上精品中的深黑蘭姆酒~風味清醇，口齒留香，小巧獨立包裝，隨時享用幸福小點！來台灣屏東農場玩必買伴手禮。搭配咖啡或日月潭紅茶絕佳午茶良伴，享受午茶新食感！銘泉金鑽鳳梨內餡~18個月有機種稙，再經過8小時的熬煮呈現自然酸香甜。無鹽發酵奶油~純淨天然，絕無人工添加，新鮮的乳香味，爽口不油膩，溶點溫度低於體溫，入口即化。牙買加麥斯蘭姆酒~8年橡木桶熟成蘭姆酒風味清醇，口齒留香，是深黑蘭姆酒中的精品\r\n產地直送，如何訂購: 銘泉生態休閒農場', '08-799-0220', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19163/480x360_202103095.jpg', '屏東縣', '瑪家鄉', 22.662026800425792, 120.63174962997437),
(90, '芒果乾、黑豆、蘿蔔乾', '適地適種配合恆春季風生態種植開發農產品\r\n產地直送，如何訂購: 根源民宿自然生態旅遊休閒農場 | Facebook', '08-889-7858', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19165/480x360_202103096.jpg', '屏東縣', '恆春鎮', 22.051521232957576, 120.72405517101288),
(91, '蜜汁燻茶鵝', '燻茶鵝風行全台各地，而以下營薰茶鵝最有名，在民國七十九年八月，製作出全台第一隻蜜汁燻茶鵝。田媽媽鵝肉料理餐廳18年來堅持以新鮮鵝肉製成蜜汁燻茶鵝，封存肉質鮮美，ㄟ營人的滋味，今年更定位品牌名稱為鵝鄉園，期待將美味鵝肉推出更上一層樓\r\n產地直送，如何訂購: 下營田媽媽鵝鄉園餐廳 (facebook.com)', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19167/480x360_202103099.jpg', '', '', 0, 0),
(92, '菱角五穀肉粽、菱角蓮子五穀素粽', '為了符合現代人的飲食養生觀念，菱成的菱角五穀肉粽與菱角蓮子五穀素粽採用高纖維的雜糧與糯米結合，讓營養與口感兼具。', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19169/480x360_2021030101.jpg', '', '', 0, 0),
(93, '九品蓮花茶蓮花好星情', '喝個蓮花茶，你的人生即將開始改變 最適合一週七天都能喝的茶 再忙再累也要補足正能量 消除體內的不安 煩躁與負面情緒 至少我們還能擁抱自己的身心靈 溫和安定的蓮花茶能讓人卸下身心壓力，放鬆享受淡淡的茶香與氣息。靜聽花開，坐喝茶舒\r\n產地直送，如何訂購: 九品蓮花生態教育園區 (wunme.com)', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19171/480x360_2021030103.jpg', '', '', 0, 0),
(94, '愛鳳醬', '打破傳統蔭鳳梨包裝，搖身一變成為時尚伴手禮，金色的山脈，象徵山上區物產豐饒，亦有金山銀山送到家之喜氣之意，附贈多道愛鳳醬料理食譜，方便料理，罐罐真材實料，不管是小資族或媽媽族絕對是您廚房缺一不可的烹飪幫手!\r\n產地直送，如何訂購 :山上區農會', '06-578-1811', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19173/480x360_2021030104.jpg', '臺南市', '山上區', 23.10162989999, 120.3556409999),
(95, '桑椹蜜果', '一般的蜜餞都是濕濕的，也因為這樣他通常都需要比較多的糖及保存劑，有些甚至會為了增加風味而添加許多香料讓你覺得好吃，這款桑椹蜜果的做法 先使用新鮮的桑椹稍微蜜製後！直接低溫烘焙，這樣可以減少加入的糖分也能讓大家吃到新鮮桑椹的味道，由於烘焙控溫技巧的關係！他的口感吃起來有點像「軟糖」，也能吃到裡面桑椹籽「脆脆」的口感，市售真的非常少見！\r\n產地直送，如何訂購: 大樹張媽媽', '07-656-3635', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19175/480x360_2021030105.jpg', '高雄市', '大樹區', 22.747174358116073, 120.44812560081482),
(96, '暖開雞', '我們的滷味在製作過程中皆是選用國產雞肉，並且不添加任何人工保鮮劑，讓民眾買時可以吃得安心健康並且強調在吃進第一口時就能回憶起小時候放學回家媽媽(阿嬤)從廚房裡端出的點心，那種難以忘懷的感動，雖是冷食但心卻是溫暖的!', '07-3711331', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19177/480x360_2021030106.jpg', '高雄市', '仁武區', 22.702223897743032, 120.34944589656905),
(97, '鮮羊乳冰淇淋', '鮮羊乳霖淇淋使用人工授精楊枝所生產的100%低溫鮮奶羊乳全奶單一乳源的堅持下，讓濃濃奶香化在嘴裡就像簽了一隻羊\r\n產地直送，如何訂購 : 禾光牧場-羊咩咩的家', '07-696-1317', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19179/480x360_2021030107.jpg', '高雄市', '路竹區', 22.8903306, 120.2756294),
(98, '台灣金石咖啡濾掛式咖啡隨身包', '在地經營15年從農地耕作後製處理最終加工自行包辦也幫助在地農民推廣可為屏東咖啡的代表\r\n產地直送，如何訂購:台灣金石咖啡休閒農場', '08-783-2558', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19181/480x360_2021030108.jpg', '屏東縣', '萬巒鄉', 22.5849127, 120.617113),
(99, '青芒果棒棒冰', '-在成熟之前遇見你，有點青澀卻依舊美好-和你見面之前，必須承認我是那樣的緊張 用洗、削、泡、剖、沖、漬、凍七道手續 把自己整理成最好的樣貌 或許看起來還是有點青澀 也沒有太過華麗的添加 但是酸中帶甜，甜中有酸的滋味 不是更耐人尋味嗎？\r\n產地直送，如何訂購: 枋山觀光農園', '0935-470-498', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19183/480x360_2021030109.jpg', '屏東縣', '枋山鄉', 22.263342895876047, 120.66105008125305),
(100, '磚砌龍鳳-旺來酥', '三溪沖積的沃土與水質水源保護區的優良條、加上南國的艷陽豐日和長達20個月呵護，讓高樹鄉的「金鑽」鳳梨，不僅僅是14-16的高甜度，更有著香氣濃郁與風味十足的特性，透過一雙雙自信且不畏命運的小手，手工捏製，完成了一個個香酥不膩的旺來酥，不僅展現著手藝、也是向著希望的疾聲吶喊~', '0937-665688', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19185/480x360_2021030110.jpg', '屏東縣', '高樹鄉', 22.85102052678838, 120.62931418418884),
(101, '蓮花雞湯', '蓮花雞湯採用自家生態有機蓮花池蓮花製作蓮花雞湯,花蓮羅山放山雞熬煮,自然而成的甘甜滋味,加上紅棗黃耆枸杞等中藥材,不添加人工添加物,且擁有SGS嚴格把關,品質有保障', '03-882-1873', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19191/480x360_2021030113.jpg', '花蓮縣', '富里鄉', 23.205299657514622, 121.27225041389465),
(102, '布農炮仔雞湯禮盒系列', '雞仔住在山中，族人為了保護小雞，就用沖天炮喊驅趕調皮的猴仔們，因此被稱為「炮仔雞」。 放山的炮仔雞，天然熬煮無味素，雞湯濃郁滑順，喝得到山林的野生與奔放。\r\n產地直送，如何訂購: 布農炮仔雞湯禮盒系列 | 線上購物 | 布農部落休閒農場 Bunun Tribe Leisure Farm', '08-956-1211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19193/480x360_2021030114.jpg', '臺東縣', '延平鄉', 22.905841725183507, 121.09098270535469),
(103, '水晶冰卷、小魚干貝醬（日式原味、和風沙茶）、小管醬', '水晶冰卷為來自無汙染的澎湖海域之大尺寸小管，加以汆燙製成。當漁船捕撈漁獲後會將小管放置於活魚艙，使之保持活體狀態，而後汆燙完需經一道冰鎮程序，再急速冷凍、真空包裝等程序。如此一來，讓您將之稍微退冰後，打開包裝並切片即可給予最特別的您品嘗到來自澎湖最新鮮、最甜美的澎湖水精靈－水晶冰卷！ 小魚干貝醬、小管醬之原料－丁香小魚、小管乾、蝦米等食材選自無汙染的澎湖海域。元貝田媽媽傳承美食經典工夫，存封大自然的海味，精心煨炒獨家醬汁，散發出細膩綿密、香濃醇厚的口感，最佳下酒、烹煮料理、私房小菜極品。\r\n產地直送，如何訂購: 元貝田媽媽 x 元貝漁場', '06-993-2305', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19195/480x360_2021030115.jpg', '澎湖縣', '白沙鄉', 23.64964150101733, 119.6092700958252),
(104, '星月灣澎湖高麗菜酸', '看過30幾斤的高麗菜嗎?澎湖特有栽培的(烏崁種)高麗菜像桌面那樣.越大越好吃頭家嬤利用自家雞肥及農牧人家的羊肥.捨棄化肥無毒栽種更加健康.古法醃製不含畫添加物\r\n線上訂購:星月灣海田料理餐廳', '06-998-3455', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19197/480x360_2021030117.jpg', '澎湖縣', '西嶼鄉', 23.614564525123814, 119.51455593109131),
(105, '文山包種茶粽', '「文山包種茶粽」是道地的北部粽，不但名字含有「包中」之寓意，餡料包括獨家滷汁慢火燉煮的瘦肉、農家自行炮製的蘿蔔乾、精選香菇、土豆等，以及當地茶農自產自銷的高品質包種茶末，保留茶葉中珍貴的維生素，讓茶粽吃起來多了清香味，不油膩，健康又美味。', '02-2665-6041', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19199/480x360_20210303(1).jpg', '新北市', '坪林區', 24.9347019, 121.7067455),
(106, '紫蘇梅', '每一顆梅子都是製程的期待每一個步驟都是認真的對待每一個客戶都是健康的交代\r\n產地直送，如何訂購: 田媽媽桑園工坊', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19201/480x360_202103068(1).jpg', '', '', 0, 0),
(107, '烏龍茶梅酥', '餅皮加入烏龍茶粉，以南投Q梅為內餡，烏龍茶粉的清香搭配Q梅的清爽口感，這是屬於竹山的味道。\r\n產地直送，如何訂購:田媽媽幸福田心', '04-9265-8350', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19203/480x360_202103069(1).jpg', '南投縣', '竹山鎮', 23.762702008880524, 120.72248574346304),
(108, '茶葉禮盒', '高山茶種植於海拔一千公尺以上，其氣候終年雲霧繚繞，氣溫涼爽，日夜溫差大，日照較短，使得茶樹生長速度較慢，芽葉成份中兒茶素類等苦澀成分低，茶胺酸等甘味成份含量提高，且芽葉柔軟、葉肉厚，果膠質含量高，因此茶湯色澤翠綠鮮活，滋味醇厚，喉韻強勁，香氣淡雅持久，口感清香順口、回甘，別有一股高山韻味，喉韻無窮。\r\n產地直送，如何訂購: 阿里山民宿-阿里山龍雲農場', '05-256-2216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19205/480x360_2021030100(1).jpg', '嘉義縣', '竹崎鄉', 23.48721860682342, 120.70360064506531),
(109, '活體蛤蜊', '哈哈漁場處於 彰化名列台灣文蛤前三大產區，而位於斗苑休區、漢寶專業養殖區的「哈哈漁場」，也以養殖 文蛤著稱，並混養白蝦、虱目魚、黃錫鯛等魚蝦。漁場在主人陳明瞭的精心規劃下，來到這裡不但 可以下水「摸蛤仔」，還有「漁夫牽魚」、「收蛇籠捕蝦」等特色活動可以參加，藉由親自操作、 切實了解漁夫工作以及各種不同的捕魚工具如何利用。此外，在陳明暸親切活潑的帶領及講解下， 大家還能在充滿歡笑的輕鬆氣氛中進一步了解當地河口生態，並就地享用「尚青」魚場、採買新鮮 的活力蝦、元氣蝦等魚場特產，開心感受「一日漁夫」的生活！ 明瞭哥原本是做工廠的，後來中年的時候在思考要轉職，當時就決定回來家鄉做養殖業，起先 他先跟別人租養殖魚塭，慢慢拓展到有自己的魚塭，在做養殖魚塭的同時，他也思考要如何將這裡 的生態環境以及漁業概念傳遞下去，就先開啟作海岸教育的活動（講解生態等等），大約六年前左 右才開始規劃比較完整的一套體驗活動，明瞭哥採取的方式，適用較天然且貼近自然的魚塭做生態 講解， 並且實地教大家如何牽罟、摸蛤仔，還可以體驗 DIY 做魚丸、魚鬆等，可以說是最好的教育 場所。\r\n \r\n產地直送，如何訂購: https://www.facebook.com/hahafish168', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19210/480x360_哈哈漁場.jpeg', '', '', 0, 0),
(110, '風將烏魚(子)禮盒', '本產品由福樂休閒漁村自家養殖的烏魚加工製作生產:1.養殖過程都經過定期送檢2.製作出的產品皆經過ETC檢驗合格', '03-556-2690', '', '新竹縣', '竹北市', 24.8624071, 120.9427021),
(111, '偷偷醬', '商品介紹\r\n採用安心草莓製作出全家都適合的草莓醬；有小孩愛的減糖甜草莓醬、媽媽愛的低糖酸草莓醬、爸爸愛的驚艷辣草莓醬；滿足全家需求。堅持無人工添加物、無添加果膠、無香精、無防腐劑、符合380項農藥檢查，生菌檢驗合格。美味之外更堅持安心！\r\n產品故事\r\n連果子狸偷都愛吃的草莓就是最好的認證！所以以果子狸為草莓代言人；以品質純凈甜水鴛鴦湖水源灌溉、室內網室栽種，精心挑選草莓品種，不使用化肥，增添益生菌栽種，讓每位朋友吃的安心\r\n品牌故事\r\n在地種約40年，堅持無農藥栽種、添加益菌種植，夜晚更有果子狸結群偷吃，讓大自然能以友善循環共生\r\n遊程規劃\r\n半日遊：莓圃位處台北的白石湖休閒農業區山區，週遭鄰捷運15分鍾，擁有全台最大的聖王公廟，被視為漳州人的守護神，再步行穿越白石湖吊橋一覽群山風光，探索天然湧泉古錐碑湧水、漫步在同心池裡，在到莓圃採安心草莓，品嚐草莓披薩及草莓大餐\r\n購買專線：02-27951108\r\n聯絡信箱：s382.garden@gmail.com\r\nFB：莓圃休閒農園\r\nIG：莓圃休閒農園 餐廳', '02-2795-1108', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19212/480x360_1-莓圃休閒農園-偷偷醬(4).jpg', '臺北市', '內湖區', 25.1025774, 121.5886641),
(112, '風味典藏禮盒', '商品介紹\r\n紅茶加入鐵觀音的烘焙特色，新開發出炎韻焙香紅，具有淺焙咖啡的烘焙香，超越市售紅茶的甘甜柔順口感\r\n產品故事\r\n茶職人追求比完美多一分，用鐵觀音的烘焙工法.提升紅茶風味層次，焙火韻味及紅茶香，刺激度下降更甘甜。\r\n品牌故事\r\n好茶是天地人的結合.張恩沛.政治大學神經科學所校友，生長在茶農世家，2014年張恩沛放棄穩定的生技業工作，離職後走回習茶之路。\r\n遊程規劃\r\n半日遊：貓空纜車站-茶路古道-鴻智茶場-茶園導覽--製茶程序解說導覽-品茶師體驗/一日遊:貓空纜車站-炭窯導覽-豬舍導覽-樟樹步道導覽-彩雲亭明鏡池賞花-水牛公園-光羽鹽套餐-鴻智茶場-製茶過程導覽-品茶師體驗-茶藝師茶席體驗\r\n購買專線：0918-108470、02-23935810\r\n聯絡信箱：wlh0000@gmail.com\r\nFB：鴻智茶場 Taipei Ancient Tea Road Space', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19214/480x360_2-鴻智茶場-風味典藏禮盒.jpg', '', '', 0, 0),
(113, '蜂蜜禮盒組', '商品介紹\r\n國產蜂蜜，特色蜂蜜、綠色保育及有機標認證休閒農場產出\r\n品牌故事\r\n農場主人帶著蜜蜂四處逐花而居，過著遊牧式的生活，養蜂人對四季的掌握、對環境的了解，在台灣不同的地點採集不同的蜂蜜，透過蜜蜂將自然的味道採集成蜂蜜呈現給消費者\r\n遊程規劃\r\n早上10點入園，10:15園區生態導覽，10:50蜜蜂生態導覽，12點用餐，13.30蜂蠟蠟燭DIY，14:00自由活動15:00回程\r\n \r\n購買專線：02-26363742\r\n聯絡信箱：ian218040@gmail.com\r\n官網：牧蜂農莊\r\nFB：牧蜂農莊', '02-2636-3742', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19216/480x360_3-牧蜂休閒農場-蜂蜜禮盒組.jpg', '新北市', '三芝區', 25.1991895, 121.465923),
(114, '手染方巾茶包組', '商品介紹\r\n從地方特色出發的天然染巾，採用在地農產茶葉和柚子葉為染材，適合隨身攜帶的手帕，純天然材質無負擔，染布花樣凸顯個人生活品味。\r\n產品故事\r\n古意手染真巾采，乃蘭陽天然手染技術製作，採用友善耕作無毒的茶柚農產為染材，染出獨一無二的色彩，帶有淡淡的茶柚香氣。冬山紅茶採用小葉茶品種，特有的製茶技術及自然氣候環境，培育出帶有花香味的冬山天然好茶。\r\n品牌故事\r\n104年成立的古意生活藝術館，是一個集&ldquo;古厝文化、民俗家居、鄉村人文&rdquo;為一體的集合空間，其位於宜蘭縣冬山鄉中山休閒農業區，原型為宜蘭傳統一條龍六開間老厝，經主人李日成夫婦改造，成就如今煥發人文生命力的古意館舍。 館舍名有思古之意，想要帶給人們的，是老空間、老家具的在地文化融合，是蘭陽特有的人情味，是返璞歸真的生活文化脈絡，是在地人文藝術的展演平台，也是友善小農、社區居民、藝文團體一同實踐地方永續發展的共好生活價值。\r\n遊程規劃\r\n一日遊：古意生活藝術館手染布巾/餐飲+仁山植物園（或新寮瀑布）踏青+茶園採茶體驗\r\n購買專線：03-9580990\r\n聯絡信箱：antiqueinn.bnb@gmail.com\r\nFB：古意生活藝術館', '0921-100-818', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19218/480x360_4-古意生活藝術館-手染方巾茶包組.jpg', '宜蘭縣', '冬山鄉', 24.6228431, 121.754128),
(115, '椿風滿麵', '商品介紹\r\n用苦茶油代替沙拉油，口味獨特，方便拌飯拌麵，兩款皆純素可以食用，露營夥伴更加喜愛，只要一罐，皆可料理不一樣菜色。\r\n產品故事\r\n家家戶戶廚房必備快速上菜拌醬，吃好油排壞油，輕鬆快速無負擔\r\n品牌故事\r\n好樂農莊是以友善土地、食農教育為出發點，結合桌遊與烹飪等體驗活動，期望以寓教於樂的方式，讓更多的民眾認識食物從產地到餐桌的歷程。第二代經營者賴建宏與江嘉維夫妻，繼承創辦人賴朝才的理念，採用山中流金龍眼品系小果茶籽，生產冷壓純鮮榨苦茶油。「苦茶油」是一種香醇味甘，氣味芳香的本產高級植物性油脂，華人食用苦茶油已經有超過兩千年的歷史，本草綱目、天工開物等古書皆有記載，坊間也流傳許多建胃強身、美髮護膚、坐月子等用途，加上近年消費者開始重視油品的食用安全，因此煎、煮、炒、炸、涼拌都合適的苦茶油逐漸在市場上展露優勢。 早期臺灣社會將苦茶苷拿來洗碗盤及沐浴，好樂農莊延續古人智慧，並透過技術改良創新，打造多元的苦茶清潔產品，希望將臺灣特有的苦茶文化永續傳承。我們除了分享美好的景色事物外，更希望能成為繁忙都市人心中的一片淨土，讓大家能遠離塵囂，放鬆心情、重新喚起人們對飲食生活與健康、農業、親子及自然環境的重視。\r\n遊程規劃\r\n半日遊：軟埤坑天梯健行與歷史文化導覽，午餐享用當地農村午餐，手作苦茶油DIY.一日遊：農場闖關食農實景體驗，農村廚房體驗親自下廚，體驗現榨苦茶油，手作體驗。\r\n購買專線：0910-989477\r\n聯絡信箱：amie0917@ymail.com\r\n官網：好樂農莊\r\nFB：好樂農莊\r\nIG：好樂農莊/禪林休閒農場', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19220/480x360_5-好樂農莊休閒農場-椿風滿麵.jpg', '', '', 0, 0),
(116, '籤詩福椒餅', '商品介紹\r\n黃金鵝油僅取至鵝隻臀部兩側油脂，一隻鵝僅能取出約500克鵝油，精製提煉後僅能產出約100~120公克的黃金鵝油，檢驗中反式脂肪酸的&ldquo; 0 &rdquo;檢出，表現出黃金鵝油的健康取向，使用黃金鵝油製作福椒餅，讓您放心呷零食呷健康。 福椒餅是大人們追劇配啤酒、好友聚會「紲喙」的好夥伴。 孩子們吃甜點不如吃鹹餅乾，特製微微微辣福椒餅，讓孩子們也可以一口接一口，減少蛀牙的機率。 籤詩餅裡的籤詩，希望也可以為人們帶來好心情。\r\n產品故事\r\n以金蔥鵝油為主要設計常溫產品，讓遊客可以延長帶回家的時間，健康的黃金鵝油每隻鵝只能取出121g，不飽合脂肪酸為動物油脂之冠，反式脂肪酸未檢出，可安心食用。\r\n品牌故事\r\n創意鵝肉料理．盡在鵝鄉園 鵝鄉園創立於1979年，最初主要以飼養與平衡鵝肉市場為主，後加入傳統鵝肉料理市場，成立了「下營鵝肉專賣店」，于2003年加入農委會田媽媽輔導體系，開創出有別於傳統鵝肉的鵝肉創意料理始端。 2020年由農委會輔導成立「鵝鄉園」品牌，以推廣在地鵝肉特產為理念，結合「下營香」在地民俗文化設計鵝鄉園logo，以傳統中帶點現代文化設計，加深顧客對鵝鄉園的印象。\r\n遊程規劃\r\n「鵝鄉園」周遭景點，下營武承恩公園、白鵝生態教育農場、新營糖廠五分車、柳營酪農區、德元埤荷蘭村、六甲落羽松、官田水雉復育區、麻豆總爺文化糖廠、烏山頭水庫&hellip;&hellip;等等多處休閒景點，上午時間可安排至白鵝生態教育農場參觀了解鵝的生態，體驗鵝蛋彩繪DIY活動，中午時間到「鵝鄉園」享用鵝肉特色餐點，選購鵝系列伴手禮回家贈送親朋好友，如金蔥鵝油平安麵、籤詩福椒餅、蜜汁燻茶鵝禮盒、香滷鵝翅．腳．胗真空包系列&hellip;&hellip;等等。\r\n購買專線：06-6792318\r\n聯絡信箱：cjoy670711@gmail.com\r\n官網：鵝鄉園\r\nFB：下營田媽媽鵝鄉園餐廳', '06-679-2318', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19222/480x360_6-下營田媽媽鵝鄉園餐廳-籤詩福椒餅.jpg', '臺南市', '下營區', 23.240076853121177, 120.27444988489151),
(117, '金蔥鵝油平安麵', '商品介紹\r\n黃金鵝油僅取至鵝隻臀部兩側油脂，一隻鵝僅能取出約500克鵝油，精製提煉後僅能產出約100~120公克的黃金鵝油，檢驗中反式脂肪酸的&ldquo; 0 &rdquo;檢出，表現出黃金鵝油的健康取向，讓您食好油呷健康。\r\n產品故事\r\n平安麵故事定位：\r\n1.結合下營的在地文化意象，期盼引領出思念故鄉的滋味。 下營北極殿玄天上帝廟，為下營當地重要的信仰中心，距今已有三百多年的香火歷史，每三年便有一次遶境，當地稱之為「下營香扛大轎」。運用上帝公的龜、蛇、犁轎等元素，將信仰文化融入包裝，拌上下營四寶之一~鵝油，呈現出最道地的下營香，讓大家平安、健康、歡喜、呷百二。\r\n2.愛鵝樂~「呷台灣五穀，思念丹麥香」 台灣肉鵝品種來自 丹麥 ，屋簷下飼養的白鵝昂頭思念丹麥的草香，低頭呷台灣的五穀雜糧， 鵝肉肉質鮮嫩甜美，鵝蔥油拌麵，品嚐故鄉的滋味。\r\n品牌故事\r\n創意鵝肉料理．盡在鵝鄉園 鵝鄉園創立於1979年，最初主要以飼養與平衡鵝肉市場為主，後加入傳統鵝肉料理市場，成立了「下營鵝肉專賣店」，于2003年加入農委會田媽媽輔導體系，開創出有別於傳統鵝肉的鵝肉創意料理始端。 2020年由農委會輔導成立「鵝鄉園」品牌，以推廣在地鵝肉特產為理念，結合「下營香」在地民俗文化設計鵝鄉園logo，以傳統中帶點現代文化設計，加深顧客對鵝鄉園的印象。\r\n遊程規劃\r\n「鵝鄉園」周遭景點，下營武承恩公園、白鵝生態教育農場、新營糖廠五分車、柳營酪農區、德元埤荷蘭村、六甲落羽松、官田水雉復育區、麻豆總爺文化糖廠、烏山頭水庫&hellip;&hellip;等等多處休閒景點，上午時間可安排至白鵝生態教育農場參觀了解鵝的生態，體驗鵝蛋彩繪DIY活動，中午時間到「鵝鄉園」享用鵝肉特色餐點，選購鵝系列伴手禮回家贈送親朋好友，如金蔥鵝油平安麵、籤詩福椒餅、蜜汁燻茶鵝禮盒、香滷鵝翅．腳．胗真空包系列&hellip;&hellip;等等。\r\n購買專線：06-6792318\r\n聯絡信箱：cjoy670711@gmail.com\r\n官網：鵝鄉園\r\nFB：下營田媽媽鵝鄉園餐廳', '06-679-2318', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19224/480x360_7-下營田媽媽鵝鄉園餐廳-金蔥鵝油平安麵.jpg', '臺南市', '下營區', 23.240076853121177, 120.27444988489151),
(118, '泥火山豆腐乳', '商品介紹\r\n鹹度經過無數次的試驗,韻味香濃的酒釀味，從唇齒間占據整個口腔，鹹的恰恰好入嘴鬆軟，再挑剔的味蕾，都只剩投降的份兒。加上無任何添加物，說是五星級的豆腐乳一點也不為過！泥豆府的豆腐乳，將有機的自產黃豆花蓮一號自然攤曬在暖陽下，米粕在一旁恣意地發酵，給予食材自由的結果，造就豆腐乳獨特的綿密口感 經過發酵製成的豆腐乳，不管是偏甜、辣味，都能與威士忌交融出特有風味。在此推薦可用高纖麥餅當作小湯匙，挖一小口豆腐乳佐威士忌，彷若起司的creamy口感與麥餅脆香，在威士忌帶來的尾韻中更能讓人細細品嚐豐厚滋味。\r\n產品故事\r\n羅山地區的居民早在日治時期， 因當時缺乏日常用品及生活物資，生活困苦在偶然間發現泥火山鹵水可代替石膏製作豆腐，但是新鮮豆腐的保存相對不易，於是有了豆腐乳的生產 將石磨與豆腐結合透過手寫字傳達出手作的溫度與親切感,一杯威士忌或麵包，配上一小匙的豆腐乳，是生活中的極簡美好！\r\n品牌故事\r\n羅山地區的居民早在日治時期， 因當時缺乏日常用品及生活物資，生活困苦在偶然間發現泥火山鹵水可代替石膏製作豆腐，但是新鮮豆腐的保存相對不易，於是有了豆腐乳的生產 將石磨與豆腐結合透過手寫字傳達出手作的溫度與親切感,一杯威士忌或麵包，配上一小匙的豆腐乳，是生活中的極簡美好！\r\n遊程規劃\r\n羅山有機村導覽(百年土角厝、羅山村大魚池、泥火山、羅山瀑布) &rarr;客家炒米香DIY（雞鳴園）&rarr;製作泥火山豆腐（羅山大自然體驗農家）\r\n購買專線：03-8821352\r\n聯絡信箱：mayaboa18@gmail.com\r\nFB：羅山大自然體驗農家', '03-882-1705', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19226/480x360_8-羅山大自然工作坊-泥火山豆腐乳.jpg', '花蓮縣', '富里鄉', 23.2068861, 121.2707853),
(119, '龍膽膠原蛋白飲', '商品介紹\r\n採用自家生態養殖龍膽石斑魚鱗新鮮萃取，無額外添加物，成分單純，無負擔。龍膽石斑不僅為珍貴的高膠原蛋白物種，且魚鱗蛋白分子小更好吸收。解凍即可直接飲用，適合全年齡層大小朋友。\r\n產品故事\r\n我們的魚塭內有超過20種魚種，從淨化功能的貝類到大型魚類龍膽石斑，形成一個自然的食物鏈，達到真正的生態養殖。且以自然微生物和益生菌自行發酵飼料，改善魚的免疫力。其中元氣飲、朝虱目想魚乾味、龍膽一夜乾，更是獲得海宴獎(水產界最高榮譽)的商品。\r\n品牌故事\r\n我們的魚塭內有超過20種魚種，從淨化功能的貝類到大型魚類龍膽石斑，形成一個自然的食物鏈，達到真正的生態養殖。且以自然微生物和益生菌自行發酵飼料，改善魚的免疫力。其中元氣飲、朝虱目想魚乾味、龍膽一夜乾，更是獲得海宴獎(水產界最高榮譽)的商品。\r\n遊程規劃\r\n田邊賞鳥，東隆宮文化中心參觀（不定期舉辦展覽活動），水晶教堂，北門遊客中心。\r\n購買專線：0917-549736\r\n聯絡信箱：s1072303@gm.pu.edu.tw\r\nFB：田媽媽 長盈海味屋', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19228/480x360_9-長盈海味屋-龍膽膠原蛋白飲.jpg', '', '', 0, 0),
(120, '巧克力禮盒', '商品介紹\r\n榮獲2018、2020年世界巧克力錦標賽亞太區2金2銀的原味黑巧及阿里山哇沙米巧克力超級組合禮盒，分享給您最在乎的人。\r\n產品故事\r\n來自屏東大武山下，以喜樂的心，堅持有機栽種，孕育出的世界頂級可可豆所打造的巧克力，呵護您最在乎的人。\r\n品牌故事\r\n一對夫妻來到好山好水的屏東大武山下石獅公故鄉，用喜樂的心，堅持有機栽種，孕育出世界頂級的可可豆。採tree to bar 巧克力製程特色，本著天然不添加的養生概念初衷，將自產可可歷經約1.5個月的繁雜製程工序，精煉出一片片味美香醇的黑巧克力，呵護您最在乎的人。\r\n遊程規劃\r\n走讀大路關客庄尋獅趣、屏北幸福185\r\n購買專線：0987-464633\r\n聯絡信箱：ckh560418@gmail.com\r\nFB：喜樂可可咖啡園\r\nIG：喜樂可可咖啡園', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19230/480x360_10-喜樂可可咖啡園-巧克力禮盒.jpg', '', '', 0, 0),
(121, '可可豆茶', '商品介紹\r\n以獲頒2019年世界可可豆錦標賽CoEx全球最佳前50及全球20強A級可可豆，直接經初級加工碾碎製成可可豆茶，與您分享。\r\n產品故事\r\n來自屏東大武山下，以喜樂的心，堅持有機栽種，孕育出的世界頂級可可豆所打造的巧克力，呵護您最在乎的人。\r\n品牌故事\r\n一對夫妻來到好山好水的屏東大武山下石獅公故鄉，用喜樂的心，堅持有機栽種，孕育出世界頂級的可可豆。採tree to bar 巧克力製程特色，本著天然不添加的養生概念初衷，將自產可可歷經約1.5個月的繁雜製程工序，精煉出一片片味美香醇的黑巧克力，呵護您最在乎的人。\r\n遊程規劃\r\n走讀大路關客庄尋獅趣、屏北幸福185\r\n購買專線：0987-464633\r\n聯絡信箱：ckh560418@gmail.com\r\nFB：喜樂可可咖啡園\r\nIG：喜樂可可咖啡園', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19232/480x360_11-喜樂可可咖啡園-可可豆茶.jpg', '', '', 0, 0),
(122, '蘭欣辣汗3罐組辣拌醬', '商品介紹\r\n以微辣羊角椒、炒食用青龍辣椒、中辣朝天椒結合在地鳳梨、芒果、農場薑黃與海味櫻花蝦、魩仔魚、蝦仁等，分別產出味道鮮美的辣拌醬與麻辣醬，可直接當菜餚或簡易拌炒食用，很符合現在生活簡單、方便、快的需求。\r\n產品故事\r\n低辣當菜餚、麻辣薑黃當運動流汗。\r\n品牌故事\r\n辣的驚喜：中央山脈大漢山與台灣海峽之間的平原，終日吹送溫熱的海風，加上日光照耀乾燥溫暖的環境，孕育出獨特的辣椒農場主人用傳統的加鹽醬漬，日曬發酵工法,製成有醬香的辣醬，每年結合在地小農當令水果，並添加當地新鮮魚獲，手工調製富含果香與鮮味的低辣拌醬，不僅是味蕾的驚喜，更是來自南台灣獨特的滋味。\r\n遊程規劃\r\n枋寮兩天一夜小旅行\r\nDay1 蘭欣辣汗花果休閒農場 - 芳興莊園 - 阮家天空餐廳 - 蘭欣辣汗花果休閒農場\r\n11:00~12:00 蘭欣辣汗花果休閒農場（方案二選一） \r\n12:00~ 14:00 蘭欣辣汗花果休閒農場採時蔬+火鍋 \r\n大人300  \r\n14:00~15:00 Check In 整理 \r\n15:00~17:00 1.蓮霧生長過程導覽解說 2.蓮霧雪Q餅製作 3.果乾試吃品嚐 4.谷哥評論五顆星，加贈清涼可口蓮霧冰沙一瓶 5.多採的部份另外計費\r\n17:30~19:00 阮家天空餐廳晚餐\r\n19:30~20:30 蘭欣辣和花果休閒農場夜間生態導覽＋觀星\r\nDay2 饗魚樂 - 平田永序有機芭樂農場\r\n10:00~12:30 饗魚樂 2.5小時＿含用餐 20人成團\r\n13:00~17:00 平田永序-有機芭樂農場\r\n購買專線：08-8717370\r\n聯絡信箱：a0919171042@gmail.com\r\n官網：蘭欣山莊\r\nFB：蘭欣辣汗花果休閒農場', '08-871-7370', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19234/480x360_12-蘭欣辣汗花果休閒農場-蘭欣辣汗3罐組辣拌醬.jpg', '屏東縣', '枋寮鄉', 22.37553554186325, 120.60230433940887),
(123, '台灣の梅禮盒組', '購買專線：04-92791949\r\n聯絡信箱：hsinifa@gmail.com \r\n採用台灣南投縣信義鄉產出之優質青梅，包裝採易開罐設計，取用方便，開封後可完全密封。口感Q彈，酸甜綿密，感受健康的傳統工法手藝。全採用天然蔗糖，完全不加任何人工色素及甘味劑。', '0975-801-959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18122/480x360_11-103-10-南投縣-信義鄉農會-愛旦 台灣梅禮盒組-情境(400X320)-20151027111505.jpg', '南投縣', '信義鄉', 23.68870099999, 120.861791),
(124, 'HAKA好醬', '商品介紹\r\n我們的產品著重在使用在地最天然最安全 ,完全草生栽培自然生長的好物,加工過程只添加最天然的鹽和糖及現榨的檸檬汁本著幫助在地老農的好物尋找出路,創造價值,也希望好物可以與大家共分享\r\n品牌故事\r\n職人匠心 客庄好醬 客庄職人承襲客家人的傳統祕方與工法， 用勤奮堅持的心 ， 將天然食材醃製釀造， 延長食用價值， 用醬食留住季節的恩典,享受土地的豐盛, 以做好醬,留好樣為使命，知足常樂。\r\n遊程規劃\r\n彩繪 手拉坏 蛋捲 藍染等 diy  餐點  鐵路自行車各個行程自由搭配\r\n購買專線：03-7878916\r\n聯絡信箱：MA41921@GMAIL.COM\r\n官網：雙潭休閒農業區\r\nFB：三義鄉雙潭休閒農業區', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19236/480x360_13-雙潭休閒農業區-HAKA好醬.jpg', '', '', 0, 0),
(125, '海藻多醣飲', '商品介紹\r\n海藻多醣飲是多醣體高含量的休閒食品，跳脫填，以親近小孩的包裝讓長青者與小孩都方便輕鬆攝取海藻多醣體營養多醣體營養\r\n產品故事\r\n以台灣的海底龍鬚菜作為原料，經過6洗6曬工時去做前置處理，結合當地社區海藻復養，以不抽地下水的有閃養殖方式飼養出正統台灣優質海藻。\r\n品牌故事\r\n在風和日麗，北緯23.5度，位於鰲鼓的向禾休閒漁場以紅樹林自然生態，輔以低碳低電方式養殖海藻，並混養低密度的魚蝦蟹貝螺形成食物鏈，不用任何藥物搭配水質管理系統，養殖著高營養的海藻。\r\n遊程規劃\r\n魚塭運動會：划船、摸文蛤、釣魚、烤蚵蚵\r\n購買專線：05-3600959\r\n聯絡信箱：yolo@yolofun.com.tw\r\n官網：向禾休閒漁場', '05-360-0959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19238/480x360_14-向禾休閒農場-海藻多醣飲.jpg', '嘉義縣', '東石鄉', 23.507773, 120.148785),
(126, '金極烏魚子蛋黃酥', '商品介紹\r\n藍鯨水產 自然源選~擁有60公頃烏魚專業養殖區的水月休閒農業區，每年11月產量十餘萬尾優質魚類；座落於園區內的藍鯨水產，多年來，始終堅持嚴選在地食材，以最佳的製作比例研發美味的當季好物。堅持不使用化學添加物、不含泡打粉、乳化劑、香精色素等廉價速成的化學添加物，手工精準傳遞食材特質\r\n產品故事\r\n在地養殖漁業區自產的「烏魚子-烏金」 \r\n&diams;   嚴選竹北濱海養鴨人家「鹹鴨蛋-蛋黃」 \r\n&diams;   創造新視覺及味覺的衝擊設計，鹹香營養-金極烏魚子蛋黃酥\r\n品牌故事\r\n經典蛋黃酥與鮮香烏魚子的美味融合，包裝以黑底的方式呈現，利用簡約的金色線條來襯托出優雅、高貴的氣息，在送禮時更能凸顯出時尚品味，用簡約的圖騰設計來呼應出中秋年節的氛圍，再搭配上具有象徵和平、快樂、長壽、幸運的梅花，不管是佳節或是平時要送禮，都有祝福的意涵\r\n購買專線：03-5565577\r\n聯絡信箱：sasha512588@hotmail.com\r\n藍鯨魚寮FB', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19240/480x360_16-藍鯨水產企業社-金極烏魚子蛋黃酥.jpg', '', '', 0, 0),
(127, '親親米香', '商品介紹\r\n輕親米香 翻轉傳統米香的口味  大塭休閒農場 採用宜蘭在地元素 蘭陽平原的純淨水源稻米 太平洋海水滋養出的海鮮 鮮蝦  小魚  烏魚子 佐以三星蔥入味 您從沒吃過的海鮮米香 鹹甜鹹甜  就是要讓您一口接一口 用舌尖來記憶 親子一起同樂共學 美好的旅行時光\r\n產品故事\r\n大塭休閒農場傳承兩代專業養殖，以太平洋的豐沛，滋養出大塭純淨鮮甜魚蝦，屢屢翻轉漁業新體驗，遠近馳名備受好評。現因廣大饕客需求，應將新鮮魚蝦製作成伴手哩，全新口感送禮自用兩相宜，讓您輕鬆帶回不一樣的宜蘭豐沛海味，品嘗回味與孩子的親子共學，與於和你相遇。\r\n品牌故事\r\n大塭休閒農場在2020年成立次品牌-輕親魚朵:是一個提供親子體驗休閒的漁業生產園區，也已經在上半年已經打開知名度及穩定成 長中，繼而延績這個品牌概念，我們推出了米香系列的伴手禮，則，命名為「輕親米香」，本次開發三種海味米香分別為蝦米香、魚米香、烏魚子米香。\r\n購買專線：03-9870558\r\n聯絡信箱：sobrinaa22@gmail.com\r\n官網：大塭休閒農場\r\nFB：大塭休閒農場', '03-987-0558', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19242/480x360_17-大塭休閒農場-親親米香.jpg', '宜蘭縣', '礁溪鄉', 24.819497045332145, 121.79645597934723),
(128, '有機手採玉露綠茶／有機烏龍茶', '有機手採玉露綠茶\r\n施行有機自然農法耕作，特選小葉種茶葉，人工採收一心二葉最嫩部位，全程低溫萎淍，含豐富兒茶素及茶多酚，夏天非常適合冷泡茶。\r\n有機烏龍茶\r\n有機烏龍茶以中發酵中焙火傳統方式製作，濃厚「果香」、口感內斂滑順甘香耐泡，沖泡後葉片展開葉中間呈綠色、葉緣呈紅色，有『綠葉紅鑲邊』美稱\r\n購買專線：0911-231454\r\n官網：玉露茶園\r\nFB：玉露茶園(玉露製茶廠)', '03-980-1111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19244/480x360_3-宜蘭縣-玉露茶園-有機手採玉露綠茶-_(2).jpg', '宜蘭縣', '大同鄉', 24.66665, 121.5886883),
(129, '古米傳奇', '以自家榮獲2018世界巧克力錦標賽亞太區銀牌的66%原味黑巧克力風味結合在地原住民部落的馬告及台灣油芒，使巧克力除本身風味外，增添了香茅、檸檬、及爆米花的香氣口感，也讓消失60年的原民穀物重啟新頁。', '0987-464-633', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19308/480x360_古米傳奇巧克力禮盒照片.jpg', '屏東縣', '高樹鄉', 22.7604991, 120.6142116),
(130, '第一鰻波~胭脂鰻', '第一鰻波的鰻魚外銷日本多年並獲得好口碑，透過研究養殖技術，以益生菌改良水質並配合產銷履歷制度，嚴格管理養殖過程。鰻魚含有維生素A、E、EPA 、DHA及豐富的鈣與鋅及膠原蛋白，營養價值豐富，本產品精選肉質肥美、口感細緻入口即化的幼齒鰻魚製成，故取名【胭脂鰻】。', '0963-120-203', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19250/480x360_2.第一鰻波工坊-第一鰻波~胭脂鰻.jpg', '雲林縣', '口湖鄉', 23.6178327, 120.1630864),
(131, '綠川黃金蜆精錠\n後山洄甘苦瓜原片茶\n立川嚴淬滴魚精', '綠川黃金蜆精錠：立川漁場利用無污染的天然湧泉，活水養殖出高品質的黃金蜆。蜆以濾食綠藻為主，終其一生過濾了近一萬公升的湧泉菁華，富含豐富的蛋白。綠川蜆錠以蜆精乾燥濃縮而成，每罐蜆精約等於3顆蜆錠，營養價值不變且攜帶更方便。\n後山洄甘苦瓜原片茶：花蓮2號山苦瓜富含維生素A、B群、維生素C、E、菸鹼酸、葉酸、膳食纖維及三帖類等營養成分，可以促進新陳代謝、幫助調節生理機能的調節。\n立川嚴淬滴魚精：以台灣東部後山天然湧泉養殖的活力鯛為原料，淬煉過程嚴格控管火侯，全程絕不添加任何一滴水，滴滴珍貴。滴魚精含有極高的膠原蛋白及牛磺酸，是忙碌生活中方便補充的營養聖品。', '0800-007111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19252/480x360_3.立川漁場-綠川黃金蜆精錠、後山洄甘苦瓜原片茶、立川嚴淬滴魚精.jpg', '花蓮縣', '壽豐鄉', 23.864724954942012, 121.52217864990233),
(132, '桑椹果醬', '選用自家生產的桑椹為原料，將桑椹打碎加入微量的糖，經過四個小時熬香以及高温殺菌製成，真材實料，沒有添加果膠、澱粉、防腐劑、香料、色素或任何化學成分，是一款低糖好吃的天然桑椹果醬。', '07-656-3635', '', '高雄市', '大樹區', 22.747174358116073, 120.44812560081482),
(133, '青梅竹馬禮盒', '購買專線：04-92791949\r\n聯絡信箱：hsinifa@gmail.com \r\n台灣の梅系列-古味梅、陳年梅、紫蘇梅\r\n全採用台灣南投縣信義鄉產出之優質青梅。採易開罐設計，取用方便，開封後可完全密封。口感Q彈，酸甜綿密，感受健康的傳統工法手藝。全採用天然蔗糖，完全不加任何人工色素及甘味劑。', '0975-801-959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18714/480x360_20200328-11.jpg', '南投縣', '信義鄉', 23.68870099999, 120.861791),
(134, '松子蘿勒醬', '採用自家場域栽種生產的有機九層塔，以末端嫩葉加入頂級橄欖油及松子、起司等上等原料，手工製成松子蘿勒醬，絕無添加任何防腐劑，是塗抹麵包、炒菜、拌麵拌飯、沾醬的料理好幫手。', '08-776-1151', '', '屏東縣', '萬丹鄉', 22.5968275, 120.5144039),
(135, '有機東方美人茶、有機烏龍茶', '有機東方美人茶：經手工摘採一心二葉，以傳統技術進行揉捻、解塊、烘乾製成毛茶，再經分級、精製、焙火、包裝而成。茶葉白毫肥大，葉身呈白、綠、紅、黃、褐五色相間，有濃濃蜂蜜香及熟果味。\n有機烏龍茶：採傳統製作中發酵中焙火、重「果香」的特色為原則。口感內斂滑順，甘香耐泡，滋味甘滑醇厚，是令人懷念的古早味。烏龍茶經沖泡後，葉片展開，可見葉中間呈綠色，葉緣呈紅色，因此有綠葉紅鑲邊的美稱。', '03-980-1111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19260/480x360_7.玉露茶園-有機東方美人茶、有機烏龍茶.jpg', '宜蘭縣', '大同鄉', 24.66665, 121.5886883),
(136, '春鳳蜜荔蛋捲禮盒組', '本產品開發設計與大樹休閒農業區結合，選用在地玉荷包荔枝、鳳梨為食材，配以黑芝麻與白芝麻烘製，包裝設計則融入鳳梨、玉荷包荔枝、黑白芝麻及產業文化故事等元素，讓農遊伴手不只是食品，而是從商品內容到包裝設計都能凸顯大樹特有鄉村風味之農遊伴手。', '無', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19262/480x360_8.川醮企業社-春鳳蜜荔蛋捲禮盒組.jpg', '高雄市', '', 22.6959848011, 120.4333011975),
(137, '甘梅湯', '「甘梅湯」是傳統的消暑酸甜飲料，手工碳焙、古法精釀，選用烏梅、冰糖、山楂、甘草等材料，熬煮出酸甜口感與特有氣味；夏天冰飲生津止渴、消暑清涼，冬季建議溫熱飲用，潤喉爽聲、去油解膩，是一年四季皆可飲用的良品。', '0933-522-530', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19264/480x360_9.耄饕客棧-甘梅湯.jpg', '南投縣', '竹山鎮', 23.7635541, 120.687042),
(138, '枇杷花烏龍', '枇杷花烏龍：來自國姓鄉百大青農友善耕作的枇杷花，清香高雅，配以同樣是百大青農種植、香氣馥郁高山烏龍茶，香氣撲鼻，入喉之後風韻綿延。\n玫瑰花紅茶：仁愛鄉賽德克族東岸部落的玫瑰花，特選帶特殊莓果甜味的大花月季品種，搭配溫潤的合歡山高山烏龍紅茶，品茶之時彷彿置身在浪漫玫瑰莊園裡。\n薰衣草紅茶：當仁愛鄉天然栽培、芳香馥郁的薰衣草，遇上了合歡山高山烏龍紅茶，其茶香及口感層次變化豐富。', '0919-743-732', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19266/480x360_10.小半天風味餐坊-玫瑰花紅茶、薰衣草紅茶、枇杷花烏龍.jpg', '南投縣', '鹿谷鄉', 23.731745, 120.7491051),
(139, '知子茶', '知子茶是以手工將梔子花與烏龍茶拌合窨製之特有茶品，同時具有烏龍茶的水甜回甘以及梔子花淡雅香氣，入口時香氣與茶水的結合香郁恰到好處，是屬於清香型、水色輕柔、花香調的烏龍茶品。', '03-324-1888', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19268/480x360_11.竹峰茗茶休閒農場-知子茶.jpg', '桃園市', '蘆竹區', 25.08246031660114, 121.34680509567261),
(140, '天菊花茶(天菊+菊葉)', '茶包袋為PLA材質(玉米纖維)，加上菊花葉，性理調合，層次豐富。以菊葉來中和菊花的涼性， 菊花香氣中帶有青草的自然味，是清香護眼的天然飲品。', '0939-027-111', '', '屏東縣', '高樹鄉', 22.7880015, 120.570933),
(141, '四重溪可以居溫泉Q米', '選用高雄147號稻米，堅持友善種植，澆灌四重溪流域擁有豐富礦物質的地下溫泉水及牡丹水庫的天然潔淨山泉水，加上南臺灣特有落山風的吹拂，掃除了病蟲害的煩惱，秧苗在群山環繞下，自然孕育出香Q飽滿，營養好吃的優質溫泉Q米。', '0919899333', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31842/480x360_souvenir-image-wpnwncu6gkybdqwwwephma.jpg', '屏東縣', '', 22.10054917737, 120.7586133812),
(142, '蘋果辣椒醬', '以溯源生產的辣椒為主食材、蘋果為基底、採純天然發酵手法將蘋果及生辣椒在室溫中運用糖酵解反應，轉化為天然醣類，讓口感更為甘甜，香氣更濃郁。', '02-2795-1108', '', '臺北市', '內湖區', 25.1025774, 121.5886641),
(143, '透紅佳人蓮霧果露', '產自國境之南屏東佳冬、吹著海風長大的蓮霧，與口感綿密的糖漿交織成濃郁香甜的鮮果露，是一款品嘗時幸福感倍增的完美甜品。', '08-866-0012', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31869/480x360_souvenir-image-ga9ab4e5iugazzizpkm0pq.jpg', '屏東縣', '佳冬鄉', 22.412898, 120.5567282),
(144, '穀物蜜紅豆', '選用「高雄8號」品種特定栽種的紅豆，經大寮農會及食品公司多年研發出來的「穀物紅蜜紅豆」，有別於市場現有商品，口味獨特，令人回味無窮，攜帶方便，可以即時享用。', '07-781-1141', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19278/480x360_16.高雄市大寮區農會-穀物蜜紅豆.jpg', '高雄市', '大寮區', 22.601793, 120.3975389),
(145, '紅棗即飲醋', '使用台灣公館栽種的無毒紅棗，遵循古法釀造，無添加任何香料或化學成分，於天然環境中歷時靜製發酵而成，色香味俱佳。', '03-722-5211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31871/480x360_souvenir-image-lkbpthmiy0edbjn9xasqdw.png', '苗栗縣', '公館鄉', 24.50464069999, 120.8273643999),
(146, '飛天豬金磚肉鬆禮盒', '嚴選台中大安區產銷履歷、肉質結實有彈性的豬後腿肉，以現代化設備，遵循古法技術精心調理製作，展現高優質鮮美風味。包裝落實環保概念，以輕巧、精緻、尊榮之金磚風格呈現，象徵「百事大安、好事湧進、生生不息」，送禮、自用都相宜。', '04-2671-2229', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19282/480x360_18.臺中市大安區農會-飛天豬金磚肉鬆禮盒.jpg', '臺中市', '大安區', 24.3463873, 120.5870304),
(147, '麻辣小魚分享包', '虎霸王麻辣小魚花生，採用國產履歷特大顆花生及澎湖小魚乾為原料，口感香辣獨特，通過農藥及黄麵毒素檢測，取得履歷認證，品質安全，是泡茶休閒的好零嘴。', '05-632-3511', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19286/480x360_20.雲林縣虎尾鎮農會-麻辣小魚分享包.jpg', '雲林縣', '虎尾鎮', 23.709292, 120.4439359),
(148, '鬼頭刀干貝醬', '鬼頭刀干貝醬以鬼頭刀為原料，以黃金比列添加在地新鮮原料，獨特的調味及烹調方式，將鬼頭刀鮮味昇華而出，製作出獨特口味的鬼頭刀干貝醬。', '089-855008', '', '臺東縣', '成功鎮', 23.099351, 121.3794969),
(149, '冷泡輕享茉莉烏龍', '以安全農法栽種的芬芳茉莉，搭配阿里山特有的回甘香潤烏龍，以繁複工法製成口感細緻的茶飲；包裝輕巧攜帶方便，讓您在生活中輕鬆品嘗無毒茉莉的清香純韻。', '04-786-7821', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19290/480x360_22.彰化縣花壇鄉農會-冷泡輕享茉莉烏龍.jpg', '彰化縣', '花壇鄉', 24.02599, 120.537346),
(150, '柿餅禮盒', '採用室內「冷熱風乾燥法」歷時七天製作而成，品質好、衛生佳，不添加任何防腐劑和糖分，金黃色的柿餅口感香Q，色澤澄黃鮮美，餅心還保留濃郁柿蜜汁，產品採單顆包裝，精緻、美觀、衛生，是送禮自用的最佳伴手禮。', '05-259-2360', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31822/480x360_souvenir-image-5dlfzz6y30uys3gy02qakg.jpg', '嘉義縣', '番路鄉', 23.46359299999, 120.5542659999),
(151, '赤尾青蝦醬', '使用梓官在地現撈赤尾青蝦，直送漁會ISO22000加工廠加工處理，口味獨特、料多實在且香氣十足，方便料理，提鮮增香，無論拌飯、拌麵或炒菜都十分適合。', '07-610-7551', '', '高雄市', '梓官區', 22.727833, 120.2546205999),
(152, '美濃米夾心餅(鮮採海苔口味)', '清甜的米餅，搭配香濃酥脆的可口海苔，香鹹中帶有米的香氣，口感令人驚艷。本產品無麩質，無添加，自然清爽，是嘴饞時刻的最佳休閒零食！', '07-6831624', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19296/480x360_25.美濃區農會-美濃米夾心餅(鮮採海苔口味).jpg', '高雄市', '美濃區', 22.9025301, 120.541316),
(153, '柴燒桂圓', '堅持遵循手工古法以龍眼木材歷時48小時烘乾焙製而成。三斤新鮮龍眼僅能製成一斤龍眼乾，而三斤龍眼乾經過剝殼去籽才撥得一斤桂圓。本品果肉厚實甜蜜，帶股淡淡的龍眼木煙燻味，無任何人工添加物，讓您吃得安心沒負擔，是自古流傳的養生珍品。', '04-873-2516', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19298/480x360_26.彰化縣社頭鄉農會-柴燒桂圓.jpg', '彰化縣', '社頭鄉', 23.896825, 120.5847081999),
(154, '池農米蛋捲禮盒', '選用本農會稻米產銷契作生產之好米(台梗2號)，先將飽滿的米粒研磨製成米穀粉，拌入低油減糖蛋捲糊後，再以手工慢火煎焙層層捲起，口感紮實香酥不腻，咀嚼後能嚐出池上好米的香氣，是休閒零嘴的好夥伴，更是農遊特色伴手好禮，。', '08-986-4880', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19300/480x360_27.台東縣池上鄉農會-池農米蛋捲禮盒.jpg', '臺東縣', '池上鄉', 23.08902709999, 121.2278084999),
(155, '100%鬼頭刀魚鬆', '盛夏時節，溫暖的黑潮帶來追逐飛魚的飛烏虎，也就是鬼頭刀。成功鎮農會所產出的100%鬼頭刀魚鬆，使用本地新鮮鬼頭刀製成，屬於刺比較少的魚種，肉質鮮甜營養價值高、脂肪低，口感香酥不膩，無論中式、西式料理皆可搭配。', '08-985-1005', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19302/480x360_28.成功鎮農會-100_鬼頭刀魚鬆.jpg', '臺東縣', '成功鎮', 23.1031364, 121.3784766999);
INSERT INTO `gift` (`id`, `name`, `feature`, `tel`, `picurl`, `city`, `town`, `lat`, `lng`) VALUES
(156, '三月半芋香茶豆', '芋香茶豆為毛豆的兄弟，品種不同，因本身莢較深色且豆仁為茶褐色，故命名為茶豆。三月半茶豆莢於清晨採收，低溫殺菌、急凍鎖鮮，茶豆的芋香包裹在豆莢裡，入口後散溢在口中，溫潤鮮滑、層次豐厚、餘味悠久，令人回味。', '06-589-9888', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19304/480x360_29.新市區農會-三月半芋香茶豆.jpg', '臺南市', '新市區', 23.0792851, 120.2955182),
(157, '茶詩雅集禮盒(阿里山高山烏龍茶)', '採集阿里山高海拔良質茶樹樹種生長的幼嫩茶葉，由中埔鄉製茶專家所製作，沖泡出的茶湯水色金黃且明亮清澈，滋味甘甜，喉韻圓潤，香氣清純。', '05-253-2201', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19306/480x360_30.嘉義縣中埔鄉農會供銷部-茶詩雅集禮盒(阿里山高山烏龍茶).jpg', '嘉義縣', '中埔鄉', 23.4728954, 120.5384742),
(158, '囍來了方便紅茶茶包', '茶品久泡不苦澀，其外包裝結合環保理念，使用環保布袋，曾為多家廠商客製伴手禮，推廣友善土地之綠色產品給更多消費者。\r\n主要販售地點：台中市北屯區軍和街401 號', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19349/480x360_4.淘圃國際有限公司-囍來了方便紅茶茶包.jpg', '', '', 0, 0),
(159, '山豬迷路', '購買專線：04-92791949\r\n聯絡信箱：hsinifa@gmail.com \r\n會讓山豬迷路的信義酒莊榖類精釀，故事來自日據時代受到強制遷村到山下的信義地利部落。據說當時部落裡有一位相當擅長釀酒的伍嬤（ wu ma）奶奶，由於日本人限制私釀，所以伍嬤（ wu ma）奶奶都會利用黑夜將自家窗戶緊密遮住，進行榖釀的最後蒸餾作業，以提供族人於慶典中飲用。待作業完畢，再利用深夜將蒸餾完的酒渣，運至山谷中丟棄。適巧倒完之後，出外覓食的山豬受到酒香的吸引，蜂擁而至，開懷品嚐起酒香四溢的酒渣。或許是不勝酒力也可能是意猶未盡，山豬們竟然循著味往伍嬤 （ wu ma）奶奶家中前進。', '0975-801-959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18716/480x360_20200328-13.jpg', '南投縣', '信義鄉', 23.68870099999, 120.861791),
(160, '烏魚子乾拌麵', '李記烏魚子來自雲林西岸最美麗的鄉鎮-口湖鄉，是台灣烏魚子主要產地。早期幾乎家家戶戶都曬著烏魚子以及扁魚，隨著人口外移，生產 力的老化，因為對故鄉的情感、親情渴望以及傳 承台灣特有美食的責任，努力做出自有品牌傳承烏魚子的美味，實現百年文化、百年企業的理想，認真寫著雲林海線的美麗故事!', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19378/480x360_11.jpg', '雲林縣', '北港鎮', 23.57121361545, 120.1757824624),
(161, '糖漬橘瓣', '桶柑來自生態共生的果園，園內處處可見蝴蝶飛舞，瓢蟲免食，是動物安心居住的家，酸甜濃郁的鮮果，連松鼠和白鼻心也聞香來採食，做成果乾，一打開包裝濃郁的柑橋香味就撲鼻而來，垂涎欲滴，保存自然酸甜好滋味，糖漬橘瓣略帶嚼勁，吃完齒間留香，饕客贊不絕口，除了美味安心亦具友善環境，是經濟活動和生態保育共存的最佳代表。\r\n主要販售地點：台中市石岡區大智街2-1號', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19380/480x360_12.jpg', '', '', 0, 0),
(162, '信義風情禮盒組', '購買專線：04-92791949\r\n聯絡信箱：hsinifa@gmail.com \r\n忘記回家－開懷梅露，又稱勇士的血液，因為它像獵槍一般陪伴勇士接受時間磨練及大自然的洗禮和體力的考驗與狩獵完成後族人的讚詠與榮耀。高品質黃熟梅果混合上等砂糖存封一年，再經昇華取其精華之50度部份結合高品質黃熟梅果加入上等冰糖及高質米酒糖經半年存封而成調和而成，造就出25度高質開懷梅露。 長老說話─橡木桶梅露-信義鄉農會頂級梅露之一，以進口橡木桶存封經長時間存封於恆溫環境之下，再裝瓶上市，口感甜滑香醇，且少了市面上一般梅露的燥味，非常適合開懷暢飲。', '0975-801-959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18124/480x360_123-098 信義風情禮盒組_20120821 (400X320)-20151027112135.jpg', '南投縣', '信義鄉', 23.68870099999, 120.861791),
(163, '菱角豬腳', '本會所生產之菱角仁，乃由產銷履歷班員供應，使用台灣國產畜產品豬腳，用傳統烹調方法，符合媽媽的味道。嚴選官田特優菱角與肉質豐潤的豬腳相融合，散發誘人的香氣，湯頭帶有淡淡的菱角清香，提供豐富的營養價值。這道美食既能滿足味蕾的享受，也能為身體帶來營養的補充。', '06-579-1221', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19341/480x360_20.官田區農會-菱角豬腳.jpg', '臺南市', '官田區', 23.1933615, 120.3183362),
(164, '歡囍幸福婚宴酒禮盒', '是南投縣信義鄉農會嚴選在地優質青梅釀造而成的梅酒，溫潤的口感與酸甜討喜的滋味，常讓人忽略其酒精濃度達 12。 【農業易遊網提醒您：飲酒過量有礙身體健康，喝酒不開車】', '0975-801-959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18125/480x360_124-099 歡囍幸福婚宴酒禮盒_20101015 (400X320)-20151027112304.jpg', '南投縣', '信義鄉', 23.68870099999, 120.861791),
(165, '天菊禮盒-初曉', '茶包袋為PLA材質(玉米纖維)，加上菊花葉，性理調合，層次豐富。以菊葉來中和菊花的涼性，是不想添加枸杞時的好選擇。 菊花香氣中帶有青草的自然味，是常用手機人士的養眼好飲品。', '0939-027-111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19332/480x360_1.天菊農場-天菊禮盒-初曉.jpg', '屏東縣', '高樹鄉', 22.7880015, 120.570933),
(166, '雨沐小米酒', '雨沐小米酒是於2014年國宴招待外賓指定用酒，也是全台唯一個是國宴小米酒。台東縣金峰鄉盛產小米，而排灣族在製作傳統小米酒時會加入小米，別於其他族群會加入糯米、白米，堅持傳統製作過程全以糯小米製成甜美的酒飲，另外在包裝上設計相關原住民元素，且用手提方式，不僅不失高質感，也常被作為送人的伴手好禮。', '08-977-2240', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19356/480x360_5.臺東金峰小米學堂-雨沐小米酒.jpg', '臺東縣', '金峰鄉', 22.525845, 120.926781),
(167, '洛神蜜餞', '洛神花有植物界紅寶石之稱，富有極高的花青素及營養價值。因應現代人在食用上以健康議題著想，用天然衛生方法製成，不含添加物及防腐劑。洛神蜜餞外層無裹糖，為把屬於洛神花的味道展現出來，美味又養生，方便攜帶食用，口感酸甜有層次，讓人吃起來健康無負擔。', '08-977-2240', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19360/480x360_6.臺東金峰小米學堂-洛神蜜餞.jpg', '臺東縣', '金峰鄉', 22.5257, 120.92678),
(168, '好樂農莊苦茶油香菇拌醬', '以苦茶油為基底，搭配產休區農友種植辣椒及臺中新社香菇，開發出苦茶油辣醬和苦茶油香菇醬。真材實料，方便又快速享受美味，不論是小家庭或辦公室都是必備的萬用調味料。辣醬麻而不辣的口感，讓人吃了會上癮，而香菇醬濃郁口感則適合搭配拌飯、拌麵或佐青菜食用，口感一級棒。這款禮盒不僅能滿足自己的味蕾，也是送禮自用兼備的好選擇。', '0910-989477', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19376/480x360_10.好樂農莊-好樂農莊苦茶油香菇拌醬(1).jpg', '臺中市', '東勢區', 24.19196742294, 120.8410153072),
(169, '饗米(法國松露風味/日本芥末風味/三星蔥風味)', '選用美濃稻米產銷專業區所生產的高雄147米，精心研磨成米穀粉，製作成脆口香酥的米食小點心，共有三種風味：濃郁香醇的松露、嗆辣清爽的芥末、鹹香有味的三星蔥，臺灣在地好米與特色風味的融合，讓人忍不住一口接一口。', '07-6831624', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19354/480x360_15.美濃區農會-饗米(法國松露風味日本芥末風味三星蔥風味).jpg', '高雄市', '美濃區', 22.9025301, 120.541316),
(170, '初霧純米吟釀', '特別嚴選霧峰栽種之益全香米，汲取清淨甘甜的埔里山泉名水，提升香米 之附加價值，成功釀造台灣最高級的初霧清酒。純米吟釀為本會酒莊第一支釀造之清酒，國際酒類競賽(布魯塞爾世界酒類評鑑金牌獎、法國巴黎國際酒類評鑑銀牌獎、德國國際烈酒評鑑金牌、 銀質獎)獲得佳績好評如潮。', '04-2339-3404', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19337/480x360_22.霧峰區農會-初霧.jpg', '臺中市', '霧峰區', 24.0651751, 120.679522),
(171, '巨峰葡萄禮盒', '產品簡介：\r\n草屯巨峰葡萄產自九九峰下平林社區，乾淨水源來自合歡山脈，由於日照充足且溫差大，獨特氣候孕育的葡萄具濃郁果香，果肉更彈牙，甜度平均18度以上。\r\n▶訂購網站(糧源九九)', '04-9233-3151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/32143/480x360_souvenir-image-9iwe7dk55u-gam6zdrmolg.jpg', '南投縣', '草屯鎮', 23.97787569999, 120.6819778999),
(172, '栗子南瓜禮盒', '產品簡介：\r\n南投草屯擁有得天獨厚的氣候與土壤，農民們堅持使用傳統耕作方式，確保每一顆栗子南瓜的品質與口感，讓您品嚐到最自然的風味。肉質細膩，甜度高，口感滑順，無論蒸、煮、烤皆可，適合各種料理需求。\r\n▶訂購網站(糧源九九)', '04-9233-3151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/32144/480x360_souvenir-image-vth5tuju1euvq4twnji74g.jpg', '南投縣', '草屯鎮', 23.97787569999, 120.6819778999),
(173, '苦茶油禮盒', '產品簡介：\r\n選用台灣本土的小果，透過先進的冷壓技術低溫萃取，保留了天然的香氣與豐富的營養成分。苦茶油富含單元不飽和脂肪酸和維他命E，無論是用於調味、烹調，還是日常保健，都是健康的選擇。讓您在享受美味的同時，安心選擇自然健康的生活方式。\r\n▶訂購網站(糧源九九)', '04-9233-3151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/32151/480x360_souvenir-image-ddjdw8weeeybxlucmbei9q.jpg', '南投縣', '草屯鎮', 23.97787569999, 120.6819778999),
(174, '健康心薏禮盒', '產品簡介：\r\n精選自優質的台中3號紅薏仁，保留了天然的營養與風味。這款養生粉富含高纖維、維他命及礦物質，並有助於美容養顏。添加於熱水或牛奶調製成香醇飲品，皆能為您的每一餐增添健康與美味。\r\n▶訂購網站(糧源九九)', '04-9233-3151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/32146/480x360_souvenir-image-8rdl9l4kveiwqw8q7ncojw.jpg', '南投縣', '草屯鎮', 23.97787569999, 120.6819778999),
(175, '豐源九九蜂蜜米禮盒', '產品簡介：\r\n荔枝龍眼蜜採用荔枝花與龍眼花交替期採集的蜂蜜，完美平衡了龍眼的濃郁及荔枝的清香。生態米栽種於生態豐富且無汙染的區域，取自合歡山山麓的純淨水源，選用最受國人喜愛的米種「台稉九號」，米粒飽滿，晶瑩剔透，口感甘甜Q黏，白米及糙米兩種選擇，自由搭配，美味又健康。\r\n「豐源九九」蜂蜜米禮盒，生態米選用小包裝，包裝外型輕巧，方便攜帶，分量剛好，適合現代小家庭的精緻化需求,送禮自用兩相宜。\r\n▶訂購網站(糧源九九)', '04-9233-3151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/32147/480x360_souvenir-image-8prn9jmviu-bqh9a-hugow.jpg', '南投縣', '草屯鎮', 23.97787569999, 120.6819778999),
(176, '豐源九九籽有米好禮盒', '產品簡介：\r\n這款精緻禮盒是您送禮的不二之選，內含兩包生態米，每包380公克，以及一瓶150毫升的台灣籽苦茶油。生態米選用台稉九號，源自合歡山麓的純淨水源，確保其品質卓越。此白米煮熟後口感Q黏，令人回味無窮。苦茶油則選用台灣小果苦茶籽製成，由於台灣本土苦茶籽產量稀少，因此更加珍貴。將香噴噴的米飯淋上苦茶油，瞬間提升餐桌上的美味風采。這組禮盒不僅展現了台灣農產的精華，更是您表達心意的最佳選擇。無論是送禮或自用，皆能感受到來自台灣土地的溫暖與用心。選擇這款禮盒，讓每一餐都充滿幸福與驚喜。\r\n▶訂購網站(糧源九九)', '04-9233-3151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/32148/480x360_souvenir-image-oc22v3oxxegkcny9v7vdqw.jpg', '南投縣', '草屯鎮', 23.97787569999, 120.6819778999),
(177, '生態雙米禮盒', '產品簡介：\r\n內含台稉九號米與台農77號米各一包，皆來自草屯優渥的地理條件與純淨水源，孕育出美味可口的稻米。台稉九號米香氣四溢，口感滑順Q黏，適合各種料理；而台農77號米則粒粒分明，含特殊香氣，無論煮飯或做粥，都能展現米的天然風味。這款禮盒不僅是品味的選擇，更是健康的象徵，讓您在每一餐中都能感受到自然的呵護與安心。\r\n▶訂購網站(糧源九九)', '04-9233-3151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/32149/480x360_souvenir-image-jrsszmqu-egjo6z74tuhsg.jpg', '南投縣', '草屯鎮', 23.97787569999, 120.6819778999),
(178, '米最珍貴禮盒', '產品簡介：\r\n米最珍貴禮盒專為貴賓所設計，內含有兩包台稉九號米。草屯地區富含得天獨厚的優質水源孕育出香氣四溢、口感極佳的美味稻米，台稉九號米的滑順與彈性，讓每一口都充滿了豐富的層次感，彷彿在舌尖上舞動，帶來無與倫比的美食享受。這款米最珍貴禮盒不僅是對美味的追求，更是對生活品味的詮釋。無論是商務贈禮還是特殊場合，這份用心的選擇將成為您心意的最佳傳遞者，讓每一份關懷在舌尖上優雅綻放。\r\n▶訂購網站(糧源九九)', '04-9233-3151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/32150/480x360_souvenir-image-dlb8uuogpum-ask5f9ks9a.jpg', '南投縣', '草屯鎮', 23.97787569999, 120.6819778999),
(179, '南投茶經禮盒', '台灣最大的茶葉生產區─南投縣，邀請您品嚐傳遞南投八大茶區最珍貴的茶禪文化。', '04-9225-1170', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18128/480x360_130-099 南投茶經禮盒_20101003 (400X320)-20151027113333.jpg', '南投縣', '南投市', 23.9222475, 120.6723086),
(180, '棗茶兒-金棗濃糖果汁', '購買專線： 03-9605388*212\r\n聯絡信箱：kiwee0823@hotmail.com\r\n「棗茶兒」為阿來伯系列產品第一彈，百分之百使用宜蘭在地生產「金棗」，歡迎來香格里拉農場與阿來伯一起追逐夢想。', '03-951-1456', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18067/480x360_014-101 棗茶兒-金棗濃糖果汁_20121003 (400X320)-20151023180652.jpg', '宜蘭縣', '冬山鄉', 24.632506, 121.729009),
(181, '美人腿湯麵', '購買專線： 04-92423828\r\n聯絡信箱：pulidistillery@gmail.com\r\n茭白筍是埔里的農特產，以潔白無晳的外形博得「美人腿」之譽，透過特殊冷凍乾燥技術，保留原有營養成分，沖泡後口感鮮嫩爽脆，美人腿泡麵共推出牛肉、肉燥及素食等三種口味，方便又營養。', '04-9298-6420', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18132/480x360_139-100 美人腿湯麵_20111031 (400X320)-20151027144124.jpg', '南投縣', '埔里鎮', 23.963721, 120.9641679999),
(182, '菇鄉香酥禮盒', '購買專線：04-92898453\r\n聯絡信箱：serviceyuchi@hotmail.com.tw\r\n來自優質的香菇栽培場，嚴選外貌佳、顆粒較大的香菇，是頂級的爽脆咬勁。本產品無添加任何化學物質，保證新鮮、自然、美味，更是貼心的伴手禮。', '04-9289-5505', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18136/480x360_144-099 菇鄉香酥禮盒_20101014 (400X320)-20151027144637.jpg', '南投縣', '魚池鄉', 23.89618859999, 120.9350395999),
(183, '日月潭典藏茶包', '購買專線：04-92898453\r\n聯絡信箱：serviceyuchi@hotmail.com.tw\r\n魚池農會典藏禮盒以「農委會茶業改良場魚池分廠製茶工廠」的紅茶傳奇；時間軸從畫面左方的台灣紅茶之父─新井耕吉郎以貓囒山常見樹種，象徵921重生力量的橄欖樹為中心，有點出回顧與展望的心情。日月潭典藏茶包，包裝採用原色牛皮紙兼具美觀及環保，舊有包裝為裝禮盒包裝，體積大攜帶不便且成本高，消費者購買意願低，經改變包裝後為3包裝禮盒，型態體積減少60包裝成本減少20將可提高消費者購買意願。', '04-9289-5505', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18137/480x360_145-100 日月潭典藏茶包_20111019 (400X320)-20151027144720.jpg', '南投縣', '魚池鄉', 23.89618859999, 120.9350395999),
(184, '日月潭茶之捲禮盒', '由阿薩姆茶末製成紅茶蛋捲，慢慢咀嚼，淡淡的茶香迴盪在唇齒之間，再搭配淡淡麥芽香的阿薩姆紅茶，最大的享受莫過於此。', '04-9289-5505', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18139/480x360_147-101 日月潭茶之捲禮盒_20121005 (400X320)-20151027144905.jpg', '南投縣', '魚池鄉', 23.89618859999, 120.9350395999),
(185, '貴妃枇杷、宋媽媽葡萄', '農場內枇杷甜度都在12度以上，食後具有清甜的風味。每年葡萄產期有兩季，口感Q、香氣濃厚、甜度都在19~20左右。此外葡萄園以人工除草，不使用除草劑。', '06-726-4625', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18142/480x360_153-099 貴妃枇杷、宋媽媽葡萄_20101011 (400X320)-20151027145406.jpg', '南投縣', '國姓鄉', 23.99595689999, 120.8515059999),
(186, '大城土豆麥酥', '購買專線：04-8944565*307\r\n聯絡信箱：wekchang@hotmail.com\r\n傳承大城鄉的老農夫不服輸的精神的「土豆麥酥」，取自大城鄉最好的土豆，最健康的小麥絕對讓您吃出「不會輸」的感覺!', '04-894-4565', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18147/480x360_160-103 大城土豆麥酥_20141127 (400X320)-20151027161033.jpg', '彰化縣', '大城鄉', 23.8531731, 120.3229006),
(187, '阿春姐古早味雞精', '購買專線：04-92522215\r\n聯絡信箱：yang04190518@yahoo.com.tw\r\n一隻成熟的古早雞，歷經十二個小時隔水慢火精煉，完全不加一滴水，利用高溫將整隻雞之精華一滴一滴的逼出。用心的經過去油、過濾、去渣等多道工序，讓純淨的營養，不油不膩。', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18148/480x360_161-099 美春姐古早味雞精_20101011 (400X320)-20151027161119.jpg', '', '', 0, 0),
(188, '阿春姐荔枝酥餅', '購買專線：04-92522215\r\n聯絡信箱：yang04190518@yahoo.com.tw\r\n以農場著名產品荔枝乾作為酥餅原料，棗紅色的內餡飽滿厚實，融合荔枝特有的果香及餅皮的奶香，整體味道非常豐富。小巧造型一口恰恰好，讓人越吃越順口。', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18149/480x360_162-100 阿春姐荔枝酥餅_20111023 (400X320)-20151027161209.jpg', '', '', 0, 0),
(189, '米樂禮禮盒', '在青青的田中央看見了微笑的稻子，農友以快樂用心的專業精神，細心灌溉，將最好的都給上，因此也種出了會微笑的稻子，那麼田中農會用無比歡欣的態度，從農友手中接到快樂的稻子，將之傳送與人們與消費者，讓禾樂禮成為快樂的傳播者。 台梗九號米：用清純的濁水溪灌溉，用富有黑色黏質的壞土栽培，每一粒米都身懷飽滿，口感香Q，還有在田中這片青青土地所沾染的幸福滋味。\r\n牛奶手打麵：與田中乳牛產銷班合作依循曝晒日照方式，手工製造，麵條Q彈，每一口都有酪農的心意。 古早味麵線：純天然、古報曝晒乾燥製成，純手作，獨門甩、拉、彈巧勁運用，口感嚼勁十足，百分之百原味天然，給你帶點陽光般的新好滋味。 胡麻油：遵循古法釀造，回歸自然，香濃淳、營養成分高，是健康調味、烹調時的最佳幫手。', '04-874-9211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18151/480x360_165-098 米樂禮禮盒_20091016 (400X320)-20151027161541.jpg', '彰化縣', '田中鎮', 23.85965819999, 120.5863643),
(190, '穀意禮', '台稉九號米：用清純的濁水溪灌溉，用富有黑色黏質的壤土栽培，如同自己的孩子般細心照料呵護，每一粒米都身懷飽滿香Ｑ口感。還有，在田中這片青青土地所沾染的幸福滋味，濁水溪流載了農家生活與土地情感，黃土沙泥涵養了田中生命悸動，豐沃了對故鄉風味的無窮思念！玄米醋：100純發酵釀造有益人體新陳代謝。嚴選「台灣米倉」濁水溪平源、八卦山脈穀物，田中稻米產銷專區台稉九號米，秉持傳統古法釀造，二年以上多重靜置發酵，酵素豐富無添加任何香料及化學合成品，入口回甘。', '04-874-9211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18152/480x360_167-100 穀意禮_20111019 (400X320)-20151027161915.jpg', '彰化縣', '田中鎮', 23.85965819999, 120.5863643),
(191, '芭樂玄米茶', '購買專線：04-8752376\r\n聯絡信箱：da8752376@yahoo.com.tw；da8747498@gmail.com\r\n精選芭樂小果、一花二葉的嫩葉，搭配擁有產銷履歷驗證的台梗九號米製成風味特殊的芭樂玄米茶。茶汁初入口，盡生芭樂清香；飲下入喉，深刻感受玄米獨有的烘炒米味。可重複沖泡，浸泡越久越甘醇。', '0958-893-396', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18159/480x360_179-100 芭樂玄米茶_20111023 (400X320)-20151027172455.jpg', '彰化縣', '溪州鄉', 23.83316715125864, 120.54958820343018),
(192, '陽光芭樂果乾', '購買專線：04-8752376\r\n聯絡信箱：da8752376@yahoo.com.tw；da8747498@gmail.com\r\n採用｢草生栽培｣法的鮮甜芭樂，手工製作，經過冰鎮、冷藏的方式處理，口感特別Q甜、有嚼勁，絕無添加色素、香精、防腐劑、甜味劑，為天然的果乾。', '0958-893-396', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18160/480x360_180-103 陽光芭樂果乾_20141127 (400X320)-20151027172540.jpg', '彰化縣', '溪州鄉', 23.83316715125864, 120.54958820343018),
(193, '溪州黑鑽紫黑米溪州紫黑米系列-貢米禮盒', '本紫黑米產自彰化縣溪州鄉濁水溪沿岸，泥土含有多種礦物元素，無農藥無化學肥料，是特別栽種的人氣產品，可天天食用，香氣逼人、好食又美味。', '04-889-5610', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18164/480x360_189-103 溪州黑鑽紫黑米_20141127 (400X320)-20151027173311.jpg', '彰化縣', '溪州鄉', 23.85153659999, 120.4949632),
(194, '大安好酒禮盒', '九降風白蘭地-萃取金香葡萄所釀製的，釀製後做二次蒸餾，再經由橡木桶內熟成，吸收橡木桶獨特沉穩熟香。口感之濃郁、柔滑順喉、芳香甘冽不可言喻，層次豐富，後勁十足痛快，久久難忘。 竽燒酎-沿襲日本燒酌製作流程，再依芋頭的的特性加以改良，並運用酒本身的溫度，將天然芋頭香氣封存於酒中。', '04-2671-2229', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18166/480x360_193-101 大安好酒禮盒_20121004 (400X320)-20151028094238.jpg', '臺中市', '大安區', 24.3463873, 120.5870304),
(195, '建國百年頭等倉米寶島雙星伴手禮', '本會所生產頭等倉米是採用台梗九號（蓬萊品種），使用第一頂級好米，還有台灣造型保鮮盒，可當收納盒或微波盒是多功能用途。', '04-2302-3560', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18167/480x360_195-100 建國百年頭等倉米寶島雙星伴手禮_20111018 (400X320)-20151028094421.jpg', '臺中市', '西區', 24.14655949999, 120.6632846),
(196, '幸福穗稻伴手米禮盒頭等倉-幸福穗稻伴手米禮盒', '嚴選台灣中部頂級好米-越光米，用心栽培，粒粒飽滿，邀您細嚐滿滿的幸福味。', '04-2302-3560', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18168/480x360_196-103 幸福穗稻伴手米禮盒_20141127 (400X320)-20151028094533.jpg', '臺中市', '西區', 24.14655949999, 120.6632846),
(197, '純南瓜粉', '宜蘭旺山休閒農場，主要栽種各式各樣品種南瓜，瓜果隧道中充滿驚奇，總是吸引著許多遊客駐足觀賞，南瓜粉純淨天然，沖泡飲用或是煮濃湯使用，健康又美味。', '0932-088-992', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18071/480x360_021-103 純南瓜粉_20141127 (400X320)-20151023181340.jpg', '宜蘭縣', '壯圍鄉', 24.7266496, 121.807281),
(198, '黃金活力地瓜餐包', '購買專線：04-26623111*263 \r\n聯絡信箱：n8045.lfm@msa.hinet.net \r\n黃金活力地瓜餐包是使用沙鹿品質優良的紅土地瓜，香甜又鬆軟，搭配14種蔬菜穀物，便利、高纖、口感滑順，只要沖入熱開水，輕輕攪拌，即可享用，沖調鮮奶或牛奶，更為香純美味。', '04-2662-3111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18173/480x360_202-101 黃金活力地瓜餐包_20121004 (400X320)-20151028113409.jpg', '臺中市', '沙鹿區', 24.2360788, 120.5589866999),
(199, '韭讚の麵', '清水區農會精心製作的「韭讚麵」是將韭黄及韭菜打汁後經過濾直接和入麵粉，讓麵條有天然食物纖維，吃起來健康且更具韭香，Q彈爽口，幸福入味。', '04-2623-2101', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18174/480x360_203-101 韭讚 麵400X320-20151028113927.jpg', '臺中市', '清水區', 24.2668384, 120.5747573),
(200, '菇賞禮盒', '香菇採收後馬上嚴選分級，經過24小時70℃烘乾後，封存濃郁菇香，再分裝成家庭號「小菇意」；鮮菇爭鮮加工成香菇酥或香菇蘇打餅，讓人得以品味菇香、滿足味蕾。', '04-2581-1511', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18176/480x360_206-100 菇賞禮盒_20111023 (400X320)-20151028114407.jpg', '臺中市', '新社區', 24.2421857, 120.8072061),
(201, '將軍花翎百年紀念酒', '用臺灣最好的霧峰香米、最甘甜的埔里礦泉和日本嚴選麴菌，釀造全國唯一台式頂級清酒。讓每個人在微醺飄然中，看見霧峰鄉的壯闊山林，感受文化經典的優雅華美。', '04-2339-3404', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18177/480x360_210-100 將軍花翎百年紀念酒_20111018 (400X320)-20151028115403.jpg', '臺中市', '霧峰區', 24.0651751, 120.679522),
(202, '金棗健康家族', '金柑又稱金橘或金棗,果實呈金黃色,果肉酸中帶甜.礁溪鄉地層結構特殊,全年氣候溫和,雨量充沛,是著名著溫泉風景區.適合金棗的種植,所生產的金棗質佳味美,有金棗的故鄉美譽.礁溪鄉農會為發展多元產品,委由專業認證的杏輝藥廠研究開發「金棗軟糖」保留金棗的健康元素與美味分享給大家。', '03-988-2033', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18072/480x360_022-097 金棗健康家族_20081220 (400X320)-20151023181423.jpg', '宜蘭縣', '礁溪鄉', 24.82079209999, 121.7705359999),
(203, '台農鮮奶酪', '購買專線：04-26880713\r\n聯絡信箱：star@farm.org.tw\r\n台農鮮奶酪以新鮮生乳煮沸殺菌製作而成，且因未加防腐劑，採定時定量供應，來到本牧場一定要嚐鮮一下!', '04-2687-2724', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18179/480x360_212-103 台農鮮奶酪_20141127 (400X320)-20151028115657.jpg', '臺中市', '外埔區', 24.329301830008905, 120.62045350670815),
(204, '香草系列產品', '西洋香草經過多年的推廣，已經逐漸成為國人健康與休閒生活的一部分。雲林土庫鎮農會香草產銷第一班藉由產學合作研發各種香草產品，在市場上廣受好評，其中又以，「奇香妙草─香草醋系列」最受到消費者的青睞。 香草醋是將香草透過蘋果發酵醋的萃取香味，再包上蜂蜜的甜美，調配出薰衣草、玫瑰、檸檬草、迷迭香、紫蘇等多種口味的香草醋，稀釋飲用，冷熱皆宜，不但風味絕佳，更有調理身體平衡，增加活力的功能。「奇香妙草─香草醋系列」產品包裝精美，送禮也十分大方。', '05-662-9111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18180/480x360_213-093 香草醋系列_20150913【From Ezgo】400X320-20151028115751.jpg', '雲林縣', '土庫鎮', 23.67755049999, 120.3898291),
(205, '莿桐說的蒜-蒜泥厲害', '莿桐的黑葉丫蒜頭，香辣夠味，莿桐蒜醬更是麵包的好佐料，健康、安全，只在莿桐說的蒜。', '05-584-6161', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18187/480x360_224-103 莿桐說的蒜-蒜泥厲害_20141127 (400X320)-20151028134210.jpg', '雲林縣', '莿桐鄉', 23.7569755, 120.502628),
(206, '杏鮑菇脆片', '購買專線：05-3799174*120\r\n聯絡信箱：chee01@yahoo.com.tw\r\n候鳥，季季來來回回，終在千里之外的東石尋得最佳棲息地，度過每個冷冽的寒冬。杏鮑菇生長在高溼氣山區，94年東石鄉農會，改建原有的雜糧廢棄倉庫，成為環控式低溫及RO逆滲透的培養環境，量身訂做一個屬於杏鮑菇的家。東石是候鳥避寒的家，也是杏鮑菇成長的家，一個最幸福的所在。', '05-379-9174', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18190/480x360_228-098 杏鮑菇脆片_20091021 (400X320)-20151028134734.jpg', '嘉義縣', '東石鄉', 23.4606346, 120.212115),
(207, '溫泉米粉', '口感香Q、綿密好吃，以冰水研磨法一貫作業完成，食用安全、衛生。', '03-988-2033', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18074/480x360_024-098 溫泉米粉_20091016 (400X320)-20151026094931.jpg', '宜蘭縣', '礁溪鄉', 24.82079209999, 121.7705359999),
(208, '湯戀組合禮盒', '著名的溫泉鄉，好山好水的地理環境，以溫泉的圖像結合「湯」的概念，營造礁溪鄉溫泉蔬果的特色，充滿活力與生機，代表礁溪鄉農特產品獨創的品牌標誌。', '03-988-2033', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18075/480x360_025-099 湯戀組合禮盒_20101015 (400X320)-20151026095102.jpg', '宜蘭縣', '礁溪鄉', 24.82079209999, 121.7705359999),
(209, '回味玉井-愛文芒果禮盒', '玉井好山、好水的自然環境條件，加上充足陽光與特殊氣候與緯度和芒果達人的經驗傳承，讓玉井芒果集聚『香、濃、Q、甜、美』風味於一身，令人回味與懷念的滋味，讓玉井擁有聞名中外「芒果之故鄉」美譽。', '06-574-2216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/31821/480x360_souvenir-image-fjqiy2a9quchlfms_ncu2q.png', '臺南市', '玉井區', 23.17685709999, 120.4852097),
(210, '玉井鮮芒果多千層蛋糕熱情小子-芒果多千層', '新鮮香濃味美在地愛文芒果，融合香草甜點與層層岩燒蛋皮，濃滑奶香、果香在嘴裡交融得恰到好處，迴盪舌尖，層次豐富味美濃郁，猶如跳著華麗圓舞曲甜蜜戀人，令人陶醉其中，是仲夏季節最令人心動幸福美食與必選清涼有勁的甜蜜伴手。', '06-574-2216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18198/480x360_243-103 玉井鮮芒果多千層蛋糕_20141127 (400X320)-20151028150509.jpg', '臺南市', '玉井區', 23.17685709999, 120.4852097),
(211, '胡麻油', '購買專線：06-5922121\r\n聯絡信箱：joy3397@hotmail.com\r\n台南市安定區於日治時代名為直加弄，後改為安定社，台灣光復後更名為安定區，追溯到古曾文溪逐年河道移位，年年河水氾濫，大水自山上沖刷肥沃土壤，淤積到下游河段。 安定區正處於曾文溪畔，年第年年有沃土覆蓋，適合種植胡麻，產量大品質好，利潤較其他作物佳，農民皆以自產自銷的型態發展沿襲至今。', '06-592-2121', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18199/480x360_246-098 國產優質胡麻油_20091016 (400X320)-20151028150700.jpg', '臺南市', '安定區', 23.1219137, 120.2373331),
(212, '玉井之門水果乾系列', '「玉井之門」創立於民國八十九年，創辦人本著客家人淳樸務實的精神，以夫家玉井為名，聯合了台南玉井地區50位農民所創立，玉井的特產為始，讓農民苦心栽種的水果，除了以新鮮水果的方式販售外，還能有另一種不同的品嚐方式，以最簡單無防腐添加物的加工方式，保有水果的原味，製作成各類水果乾，讓大家嚐到不同的美味，以期將產地的農特產推薦給台灣消費者，甚至行銷到全世界。 　\r\n「玉井之門」專門製造天然有機蜜餞，使用更天然更衛生的製作方式，讓蜜餞不但保有原本的美味，也變得很健康，其中以芒果乾為的主打產品，其他熱賣產品包括黑糖芒果乾、水蜜桃乾、鳳梨乾、芭樂乾、情人果乾、蕃茄乾、楊桃乾、葡萄乾、無花果乾、茶葉梅、甜乾梅等數種，送禮自用兩相宜。', '06-577-1086', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18201/480x360_玉井之門水果乾系列 (400X320)-20151028152337.jpg', '臺南市', '南化區', 23.0565007, 120.5054228),
(213, '溫泉糙米茶', '購買專線：03-9882033*110\r\n聯絡信箱：john.4h@gmail.com\r\n溫泉糙米茶採用在地的溫泉糙米與天然漢方-決明子加以組合，到礁溪泡湯旅遊之後，再喝上一杯「溫泉糙米茶」充滿幸福快樂的感覺！', '03-988-2033', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18076/480x360_026-101 溫泉糙米茶_20121004 (400X320)-20151026095154.jpg', '宜蘭縣', '礁溪鄉', 24.82079209999, 121.7705359999),
(214, '善緣女兒禮', '以芝麻提煉而成的胡麻油，油質清澈、風味獨特，且品質優良有保障，並且富含對人體有益的不飽和脂肪酸，有助於保持健康及抗老。而芝麻麵口感Q彈，散發淡淡清香，以乾炒或湯食方式烹煮皆宜。', '06-583-6111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18208/480x360_260-100 善緣女兒禮_20111018 (400X320)-20151028154440.jpg', '臺南市', '善化區', 23.132757, 120.2976479999),
(215, '南澳椴木香菇伴手禮盒', '百分百椴木香菇產自好山好水南澳地區，當地具有多個部落，且每個部落皆有其落部特色，是來到宜蘭南澳鄉旅遊最佳的伴手選擇。', '03-990-5181', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18077/480x360_028-103 南澳椴木香菇伴手禮盒_20141127 (400X320)-20151026113518.jpg', '宜蘭縣', '蘇澳鎮', 24.6404134, 121.8317068999),
(216, '關廟鳳梨麵', '採用在地新鮮台3號鳳梨，遵循嚴謹製麵流程，堅持不填加任何防腐劑，人工打理製作，微黃色的麵體散發鳳梨清香，口味獨特，耐煮不爛。', '06-596-6278', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18216/480x360_273-100 關廟鳳梨麵_20111018 (400X320)-20151028171127.jpg', '臺南市', '關廟區', 22.9624989, 120.3284754),
(217, '手作果干', '大坑休閒農場的阿公阿嬤自己生產水果，每季盛產時，就會將水果烘烤乾以保存作為點心，歡迎客人到大坑作客，希望能將阿公阿嬤的果干，用最善意的方式讓大家延續旅遊的記憶。', '06-594-1555', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18219/480x360_277-103 手作果干_20141127 (400X320)-20151028171801.jpg', '臺南市', '新化區', 23.0072204, 120.3904996),
(218, '有機梅精', '購買專線：07-6754123\r\n聯絡信箱：q2221942@yahoo.com.tw；cs.in@msa.hinet.net\r\n高雄縣梅樹種植面積達兩千公頃，種植面積居全台之冠，梅子品質十分良好，製作出優良養生之梅精。', '07-675-1001', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18706/480x360_20200328-07.jpg', '高雄市', '甲仙區', 23.083031, 120.588494),
(219, '茶籽油禮盒', '購買專線：02-2783-6121*13\r\n聯絡信箱：rickyeh99@gmail.com\r\n南港因當地海拔約200至300公尺，土壤富含岩礫，適合茶樹的生長，本茶籽油精選高品質茶籽，經過日曬、脫殼、炒熟、壓榨、過濾、沉澱、等複雜步驟煉製而成，是茶籽油首選。', '', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18078/480x360_1-區農會-茶籽油禮盒(400X320)-20151026134152.jpg', '', '', 0, 0),
(220, '蒲燒鰻', '選用上乘等級鰻魚搭配獨特醬汁，肉質細膩軟嫩，味道鮮美，入口即化，製作過程講求真材實料，不添加化學物品調味，含有豐富的蛋白質、維生素A、E與多量的鈣，營養價值豐富。', '07-699-5528', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18228/480x360_293-100 蒲燒鰻_20111031 (400X320)-20151028180752.jpg', '高雄市', '湖內區', 22.9085617, 120.21207),
(221, '芒果冰淇淋', '使用高優質枋山芒果，加上濃醇的奶香，入口即化的綿密感，一種協調味道在口中讓味蕾陶醉，享受冰冰甜甜的美麗境界！', '0935-470-498', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18233/480x360_300-101 芒果冰琪淋_20121004 (400X320)-20151029112251.jpg', '屏東縣', '枋山鄉', 22.263342895876047, 120.66105008125305),
(222, '旗魚鬆', '購買專線：08-8328064\r\n聯絡信箱：tk.sam168@yahoo.com.tw\r\n東港旗魚白中透肉色、味道甘美，是養分極高的魚類，在台灣及日本常是壽司與生魚片中的珍品。東港區漁會用心製作嚴選新鮮旗魚入口即能感受香酥鮮美的原始海洋風味。本產品係選用新鮮旗魚肉特製而成，不添加任何防腐劑及色素，是佐飯的上上之選。您充分體驗海味故鄉「東港」的懷古鮮味。', '08-832-3121', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18236/480x360_303-097 旗魚鬆_20090106 (400X320)-20151029112446.jpg', '屏東縣', '東港鎮', 22.4677798, 120.4417895),
(223, '稻鴨香伴手禮', '以源自大武山水源保護區之山泉水孕育，採用老祖先最具智慧的耕作方式─稻鴨共作以達到病蟲害防治，米粒大、外觀晶瑩剔透、口感好、風味佳。', '08-796-7301', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18239/480x360_307-099 稻鴨香伴手禮_20101011 (400X320)-20151029112746.jpg', '屏東縣', '高樹鄉', 22.7952705, 120.6111629),
(224, '桑椹原汁', '購買專線：08-9781531\r\n聯絡信箱：taimali03@taimalifarmer.com.tw\r\n濃縮原汁無添加防腐劑，可稀釋7-10倍，不需冷藏，期限2年。', '08-978-0400', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18696/480x360_20200328-02.jpg', '臺東縣', '太麻里鄉', 22.604987, 121.001855),
(225, '袋茶', '購買專線：08-9781531\r\n聯絡信箱：taimali03@taimalifarmer.com.tw\r\n純粹洛神花粉碎包裝、無任何添加物、熱開水沖泡後加糖調勻滋味酸甜可沖泡2次，期限2年。', '08-978-0400', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18698/480x360_20200328-03.jpg', '臺東縣', '太麻里鄉', 22.604987, 121.001855),
(226, '釋迦、洛神休閒食品', '釋迦、金針、洛神、桑葚是太麻里地區主要特產，特製成可口的小點心，每一種口味皆散發其香醇風味，經典好滋味。', '08-978-0400', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18242/480x360_316-097 釋迦、洛神休閒食品_20081230 (400X320)-20151029113106.jpg', '臺東縣', '太麻里鄉', 22.604987, 121.001855),
(227, '洛神元氣餅', '為薄餅狀，吃起來除了有奶香還有洛神的酸甜口感，好吃清爽不油膩。', '08-978-0400', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18243/480x360_317-098 洛神元氣餅_20091016 (400X320)-20151029113141.jpg', '臺東縣', '太麻里鄉', 22.604987, 121.001855),
(228, '臺灣臺東好茶', '紅烏龍係結合烏龍茶與紅茶之特色所創造的一種新興特色 茶，屬重發酵、著重烘焙的半球形烏龍茶，茶湯水色琥珀橙 紅如紅茶，滋味則如烏龍茶，具熟果香，醇厚圓滑、耐泡， 不但適合一般熱水沖泡，冷泡更顯其特色，廣獲市場好評。', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18252/480x360_329-100 臺灣臺東好茶_20111031 (400X320)-20151029114106.jpg', '臺東縣', '', 22.7555108, 121.142776),
(229, '洛神花軟Q糖', '購買專線：08-9513111*15\r\n聯絡信箱：lucky.a168@msa.hinet.net\r\n「洛神軟Q 糖」採台東火紅盛開的洛神花萼製作，保留了其原始的酸味及香氣，以優良的技術開發洛神花新口味，符合小朋友、長青者及各種年齡層的消費者的口感，是適合全家人的休閒零嘴。', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18253/480x360_330-101 洛神花軟Q糖_20121016 (400X320)-20151029114149.jpg', '臺東縣', '', 22.7555108, 121.142776),
(230, '平溪原生種山藥', '「平溪山藥」經過嚴格篩選之優良品種來復育，每年的產季於9月中旬至12月期間，由於培植不易，所以更顯格外珍貴，口感Q、耐烹煮、風味極佳，可料理多道山藥佳餚。', '02-2495-1051', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18081/480x360_034-101 平溪原生種山藥_20121004 (400X320)-20151026113907.jpg', '新北市', '平溪區', 25.0253945, 121.7393406999),
(231, '瑞穗柚花茶', '每年三月間是文旦柚樹開花的時節，當柚花遇到瑞穗有名的天鶴茶，就成了香氣迷人的上品柚花茶。運用了一年才綻開一次的柚花與茶葉共同烘焙而成的花茶，不但可 以喝到茶葉的甘醇，回甘時還帶有誘人的天然芳香。', '03-887-2226', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18267/480x360_352-099 瑞穗柚花茶_20101014 (400X320)-20151029142559.jpg', '花蓮縣', '瑞穗鄉', 23.496431, 121.381484),
(232, '伊糯千金', '黑糯米性溫，在本草綱目中記載：『溫養五臟益脾胃、補中益氣、縮小便』。適量食用，益養肝血、補腎 氣、預防骨質疏鬆、延緩人體老化，所以也有『貢米』的稱號，阿美族人將之視為老人的養生米，且任何人皆可食用，更是婦女朋友的養生美容聖品。', '03-865-3101', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18268/480x360_354-101 伊糯千金_20121004 (400X320)-20151029142746.jpg', '花蓮縣', '壽豐鄉', 23.8453491, 121.4976064),
(233, '醋老爺健康日記', '用本土100天然純蜂蜜作基底與純淨雪山泉水釀造，加上首創的「雙層三階多菌相工法」完全發酵。每天一小瓶果醋來寫自然養生日記，你今天「醋」進健康了嗎？', '03-922-0601', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18271/480x360_361-099 醋老爺健康日記_20101014 (400X320)-20151029150526.jpg', '宜蘭縣', '員山鄉', 24.746018, 121.726996),
(234, '紅棗干', '購買專線：037-231626\r\n聯絡信箱：kungnon.food@msa.hinet.net\r\n公館得天獨厚，具有特殊氣候與土質，為全台唯一生產紅棗地區，是紅棗的家！每年七、八月間觀光果園開放遊客採果，紅棗富含的營養成份，是提供藥膳之聖品。', '03-722-5211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18272/480x360_364-102 紅棗干_20150913【From Ezgo】400X320-20151029150854.jpg', '苗栗縣', '公館鄉', 24.50464069999, 120.8273643999),
(235, '紫香御品-香芋片', '採用大甲芋頭產銷班所生產的特級新鮮芋頭，透過製程的改良讓芋頭香氣能保存於芋片內，酥脆的口感及濃郁的芋頭香，讓您一口接一口。', '04-2686-3990', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18275/480x360_373-102 紫香御品-香芋片_20150913【From Ezgo】400X320-20151029161823.jpg', '臺中市', '大甲區', 24.334949, 120.615858),
(236, '茶香冰淇淋', '測試產品說明', '02-2665-7227', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18946/480x360_54020201209.jpg', '新北市', '坪林區', 24.936102, 121.7117779999),
(237, '棗紅公館蜜餞系列', '棗紅公館蜜餞系列嚴選在地紅棗、紫蘇、洛神花、梅、金桔製造，不添加色素、防腐劑、漂白劑、甘味劑等，讓您食的安心。', '03-722-5211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18282/480x360_389-103 棗紅公館蜜餞系列_20141127 (400X320)-20151029163821.jpg', '苗栗縣', '公館鄉', 24.50464069999, 120.8273643999),
(238, '鳥兒歡唱-養生禮盒組', '來自原鄉的紅藜、小米、樹豆，以自然的耕作方式栽培，營養豐富，邀您享用大地母親的恩賜。', '089-513111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18283/480x360_390-103 鳥兒歡唱-養生禮盒組_20141127 (400X320)-20151029163858.jpg', '臺東縣', '卑南鄉', 22.6955793, 121.022134),
(239, '燒仙草禮盒', '以即溶燒仙草熱情好客的特性，搭配新時代健康養生「磁炭杯」， 具抗菌、抗臭、釋放微量負離子及將水活化等效能，能讓受禮者充分感受到所傳達的友誼、愛和溫暖，在寒冬中泡杯熱騰騰的燒仙草，會讓食客感到無比的滿足與喜悅。', '03-587-4277', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18285/480x360_392-102 燒仙草禮盒_20150913【From Ezgo】400X320-20151029164252.jpg', '新竹縣', '關西鎮', 24.8032728, 121.1601904),
(240, '洛神花蜜餞', '購買專線：08-9513111#15\r\n聯絡信箱：lucky.a168@msa.hinet.net\r\n「山海經」是中國最早介紹有關於自然界礦物質的一本著作，在此「山海」與台東天然的山、海景相呼應，期許心靈、生理養生觀念，可以沿著人體的經脈運行，採台東火紅盛開的新鮮洛神花萼製作，保留了其原始的酸味及香氣，味道酸甜似初戀的滋味，風味絕佳，希望傳遞台東優質農產的特色。', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18702/480x360_20200328-05.jpg', '臺東縣', '', 22.7555108, 121.142776),
(241, '金碧椪柑', '石岡鄉以其得天獨厚的土壤、氣候、陽光，共同滋養著纍纍結實的椪柑，「金碧椪柑」乃此中嚴挑精選的頂級產品，果形完整豐滿，色澤澄黃亮麗，入口甜中帶酸甘醇多汁，而象徵財源滾滾、大吉大利的「金碧」之名，在椪柑熟成的年節期間更增添圓滿的喜氣。', '04-2572-1246', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17721/480x360_石岡鄉農會_金碧椪柑-20151216180802.jpg', '臺中市', '石岡區', 24.275887, 120.776317),
(242, '蔥滿好禮', '購買專線：03-9893170\r\n聯絡信箱：lihunggg@ms39.hinet.net\r\n台語俗諺「鼎底若有蔥、無肉嗎也香」，蔥滿好禮嚴選符三星頂級青蔥，纖維柔嫩，蔥白香甜，也適合拌麵、炒飯、配菜、燒肉、沾醬等，方便做菜一罐搞定，是家庭主婦的最愛。', '03-989-3170', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18694/480x360_souvenir-image-m8paxieggeazadhtslngnw.jpg', '宜蘭縣', '三星鄉', 24.667903, 121.652142),
(243, '三好禮', '包種茶油富含蛋白質、單元不飽和脂肪酸、維生素A、E等，對於高溫的穩定性和營養價值均優於一般市面上販售的沙拉油，不僅可養身保健，亦是美容良方。麵線以茶及山藥入味，採用多加水手打麵製麵方法，經低温高濕長時間熟成乾燥，麵線不會龜裂，品質安定，口感滑溜彈性足，湯麵、冷麵或乾拌皆宜。', '02-2665-7227', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18085/480x360_042-100 三好禮_20111018 (400x320)-20151026114239.jpg', '新北市', '坪林區', 24.936102, 121.7117779999),
(244, '文山包種茶', '清香芬芳的包種茶，香氣幽雅飄逸，滋味甘潤，入口生津；東方美人茶特殊的熟果香和蜜香，茶湯濃厚香醇，是讓英國伊莉莎白女王念念不忘的茶葉。製成茶包，方便您隨時快速享用兩種特色茶，是味蕾的雙重滿足。\r\n訂購專線：02-26657227', '02-2665-7227', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18720/480x360_a8112ee8-bc19-45b0-a3c2-809bf9f33a8a.jpeg', '新北市', '坪林區', 24.936102, 121.7117779999),
(245, '阿里山高山金萱茶', '來自阿里山高海拔優良茶區，嚴選柔軟、芬芳、醇化而不苦澀的茶菁，並經農會茶師嚴格監製及認證，成品的外觀色澤鮮活翠綠，水色金黃，耐沖泡，滋味甘醇滑軟，香氣淡雅，一向都是享譽國際，無論自用或饋贈皆相宜的極品茶。\r\n訂購專線：05-2360311#23', '05-236-0311', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17464/480x360_1-20151016173019.jpg', '嘉義市', '西區', 23.4718835, 120.431524),
(246, '金舞滂米', '阡陌交接的稻田，幻化成時尚的線條符號，黃色代表稻穗的成長，橙、紅色表現豐收的喜氣，不只滿足現代人的視覺期盼，也重新定位新概念的米食文化！', '03-379-1881', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18086/480x360_051-099 金舞滂米_20101003 (400X320)-20151026114532.jpg', '桃園市', '蘆竹區', 25.0047037, 121.2587779),
(247, '梨山雪梨', '產期：11月~翌年2月\r\n規格：一盒2粒\r\n雪梨喜愛海拔高、氣候冷的生長環境，是梨山特選之果。在黃褐色粗皮包覆下，更加襯顯出梨肉的潔白細緻，削皮時豐盈的果汁隨即涓滴而出，果肉清脆甘美且甜度均勻，滿是梨滋味，更具有綿密近乎入口即化的上乘品質，此等梨中極品豈容錯過！', '04-2594-1518', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17569/480x360_03台中市和平區農會_雪梨-20151217182407.jpg', '臺中市', '和平區', 24.174661, 120.885178),
(248, '總統梨禮盒 (豐水梨、新興梨)', '產期：5月~10月\r\n規格：高接梨(豐水梨、新光梨)6粒裝(7台斤)\r\n臺中東勢被譽為「高接梨的故鄉」，東勢梨農首先運用嫁接技術，將溫帶梨之花穗嫁接於平地之橫山梨，使平地亦能生產高品質的溫帶梨，以豐水、新興梨及雪梨為大宗，收成之果實無論外型與甜度皆為上品，深受消費者喜愛，堪稱梨中精品。', '04-2587-2151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17572/480x360_04東勢區農會_總統梨-20151217182757.jpg', '臺中市', '東勢區', 24.2534386, 120.829146),
(249, '皇梨精美禮盒', '產期：12月~ 翌年2月\r\n規格：高山雪梨( 皇梨)1 粒裝(2 台斤)\r\n臺中東勢被譽為「高接梨的故鄉」，東勢梨農首先運用嫁接技術，將溫帶梨之花穗嫁接於平地之橫山梨，使平地亦能生產高品質的溫帶梨，以豐水、新興梨及雪梨為大宗，收成之果實無論外型與甜度皆為上品，深受消費者喜愛，堪稱梨中精品。', '04-2587-2151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17573/480x360_05看不出哪裡_皇梨-20151217183440.jpg', '臺中市', '東勢區', 24.2534386, 120.829146),
(250, '客家擂茶', '購買專線：03-5802207、03-5801073\r\n聯絡信箱：b3003@bps.org.tw\r\n擂茶（又名三生湯）是客家人招待貴客及飲用的茶點。擂為研磨之意，以陶製擂缽將茶葉，花生，芝麻等原料研磨後沖入沸水調勻後飲用。客家擂茶，茶味純，香氣濃，清涼解暑，不僅能生津止渴亦可當保健飲料，也是非常傳統的客家美食。', '03-580-2207', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18088/480x360_056-101 客家擂茶_20121005 (400X320)-20151026114801.jpg', '新竹縣', '北埔鄉', 24.700475, 121.055203),
(251, '小番茄', '臺南市安南區土壤含鹽份較高，所種植之小蕃茄甜度更高，栽種在溫室中的小番茄，被農民像照料小孩般悉心呵護關懷著，果蒂新鮮，果色鮮紅均勻，果實豐滿光滑、皮薄多汁、糖酸比適中，甜中帶甘，深受消費者青睐。', '06-632-4151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17493/480x360_11台南地區農會_小番茄-20151217185529.jpg', '臺南市', '新營區', 23.31088189999, 120.3208439999),
(252, '金棗鮮果禮盒', '金柑俗名「金棗」，果實飽滿多汁，果皮香甜可食，酸中帶甜的金柑鮮果，除了是令人忍不住憶起戀愛般滋味的「戀人果」，也是春節送禮的好伴手，「吃金棗，年年好」、「金玉滿堂，棗到幸福」，是不是格外應景呢？', '03-988-2033', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17494/480x360_12礁溪農會_礁溪金棗02-20151217190141.jpg', '宜蘭縣', '礁溪鄉', 24.82079209999, 121.7705359999),
(253, '黑紫玉 (葡萄)', '晶瑩透亮的葡萄如同珍珠瑪瑙般神祕而高貴，好的葡萄色澤紫中帶黑，摸起來堅實；聞起來芳香四溢；吃起來更是圓潤多汁。水里葡萄均採套袋管理，在獨特的氣候與技術管理下，孕育出香、Q、甜且帶有果粉，品質絕佳的葡萄，誠摯向您推薦水果中的寶石&mdash;黑紫玉。', '04-9277-2108', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17495/480x360_13日光鮮果--08水里黑紫玉葡萄-20151217190448.jpg', '南投縣', '水里鄉', 23.8171897, 120.8540962),
(254, '蜜棗精品禮盒', '高樹蜜棗栽培於荖濃溪下游受茂林山下西北風吹襲，不易致病蟲害，由純淨優質水源灌溉滋養，在重重自然環境保護與高樹鄉親的愛心培育之 下，青翠的蜜棗有著驚人的碩大果實，脆甜多汁的果肉，咬下一口，好似春天就在嘴裡迸發，是您不得不嚐鮮的經典饗宴！', '08-796-0815', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17496/480x360_17蜜棗-20151217192226.jpg', '屏東縣', '高樹鄉', 22.824074, 120.601138),
(255, '富貴圓滿禮盒', '「不起眼的農產品，經過設計包裝，就能成為精緻禮盒，為農民帶來合理的利潤。」社頭是傳統農業鄉鎮，每年7 月~ 8 月是龍眼產，從山腳路沿線瀰漫烘焙龍眼乾的淡淡香氣與白煙，農民守在磚造「焙灶」工寮內，守著爐火耐心烘焙，饒富人文歷史風味。為協助農民增加收益，農會以桂圓肉、桂圓麵和蜂蜜組成「富貴圓滿」、「富貴樂」與「富貴良緣」3 種大紅禮盒，是送禮的絕佳首選。\r\n訂購專線：04-8735437', '04-873-2516', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17497/480x360_219社頭鄉農會_富貴圓滿禮盒-20151218161636.jpg', '彰化縣', '社頭鄉', 23.896825, 120.5847081999),
(256, '富貴樂禮盒', '「不起眼的農產品，經過設計包裝，就能成為精緻禮盒，為農民帶來合理的利潤。」社頭是傳統農業鄉鎮，每年7 月~ 8 月是龍眼產，從山腳路沿線瀰漫烘焙龍眼乾的淡淡香氣與白煙，農民守在磚造「焙灶」工寮內，守著爐火耐心烘焙，饒富人文歷史風味。為協助農民增加收益，農會以桂圓肉、桂圓麵和蜂蜜組成「富貴圓滿」、「富貴樂」與「富貴良緣」3 種大紅禮盒，是送禮的絕佳首選。\r\n訂購專線：04-8735437', '04-873-2516', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17505/480x360_220社頭鄉農會_富貴樂禮盒-20151218162105.jpg', '彰化縣', '社頭鄉', 23.896825, 120.5847081999),
(257, '濁水溪米醬禮盒', '日光眷顧下的秧苗，在濁水溪水悠悠的灌溉及農夫辛勤的呵護下，終能結成美麗的稻穗；而這一片片金黃飽滿，彷彿知道自己承載了滿滿的心意，總是隨風輕輕的不住點頭言謝，期待著收成，向您傳遞一份愛的穀粒。\r\n訂購專線：05-5863621#166', '05-586-3621', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17534/480x360_217西螺鎮農會_濁水米禮盒-20151218164141.jpg', '雲林縣', '西螺鎮', 23.8003737, 120.4546302),
(258, '菇菇茶集', '咕嚕咕嚕......趕緊泡上一壺心愛的18 號紅茶，與三五好友分享幸福的「菇菇茶集」，小心翼翼地啃著紅茶酥餅、香菇脆片，留下滿口的茶香，溫軟著疲憊的肚皮，讓一整個下午充滿濃濃幸福味。', '04-9289-5505', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17536/480x360_213魚池鄉農會_姑菇茶集-20151218164812.jpg', '南投縣', '魚池鄉', 23.89618859999, 120.9350395999),
(259, '有福同享組合禮盒', '遵循古法慢火煙燻炭焙，過程繁複勞累，用汗水的鹹化成龍眼的甜；搭配純天然香濃味美的龍眼蜜，交織成一種味道難以忘懷，一份感動無可取代，這份心意就從「有福同享」間蔓延，箇中辛酸只有農民才懂。\r\n訂購專線：049-2693934', '04-9269-1111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17537/480x360_212中寮農會_有福同享禮盒(大)-20151218165048.jpg', '南投縣', '中寮鄉', 23.8787397, 120.7678090999),
(260, '胡麻油禮盒', '「麻油」，它曾伴隨著我們的先人，是他們日常生活中最佳的營養補給品，如今雖然搾油方式或多或少有所改變，但依然是民眾之所愛。善化農會的麻油風味香、濃、醇，品質極佳，一直以來深受許多消費者的喜愛與肯定，相信也是您平日烹調用油的好選擇。', '06-583-6111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19314/480x360_4.png', '臺南市', '善化區', 23.132757, 120.2976479999),
(261, '龍井傳香米禮', '看天吃飯，農業本如此，沒有華麗的陪襯，簡單、樸實之中，隱藏著生活的智慧與軌跡。在品味真實原味的同時，感受根源於大地的食材誠實，心靈彷彿也受到「純‧ 真」的洗禮，那不只是天賜，是汗水種出的美味。', '04-2639-6511', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17538/480x360_210龍井區農會_傳香米禮1-20151218170048.jpg', '臺中市', '龍井區', 24.180667, 120.529265),
(262, '羅漢門龍眼乾', '說到龍眼乾，大家的味蕾一定會帶到濃濃的碳香味，「羅漢門」龍眼乾烘焙，需先去除枝葉放入窯中烘培後，以人工撥殼取出龍眼肉，太陽下曬數日，才能製成香Ｑ甜的龍眼肉。放入嘴內，每次的咀嚼呈現出不同層次的風味，那滋味無法去形容，只有試過的人才知道。', '07-667-2161', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17539/480x360_209內門區農會_龍眼乾-20151218171431.jpg', '高雄市', '內門區', 22.94175459999, 120.4613715),
(263, '山形玫瑰花瓣醬', '「遍尋世界玫瑰花種類，終於找到顏色深紅，花型優美，口感甜香的品種。」農會與農民契作，用無毒栽培，種植出最天然的玫瑰花。每日所摘採新鮮花瓣有多種食用方式，冷熱飲可隨季節調整，可沖泡出真正香氣濃郁的「花」茶，而做成果醬、沙拉醬、優格更是別具風味，歡迎您來品嚐！', '04-9298-6420', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17540/480x360_208百未--埔里農會--07山行玫瑰花瓣醬-20151218171557.jpg', '南投縣', '埔里鎮', 23.963721, 120.9641679999),
(264, '幸福梅好禮盒', '梅子從5 分熟的青綠果採到金黃軟綿的完熟果，口感各有風情。雖然梅子雖不能像水果一樣直接吃，但卻能變身各式各樣的製品。南投縣農會沿襲老祖母料理加工梅子的傳統手法，製成多款梅製品，酸酸甜甜的好滋味，讓人就像談了場戀愛一般，心情都快樂了起來！', '04-9225-1170', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17542/480x360_207百味--南投縣農會--06幸福好梅禮盒-20151218172034.jpg', '南投縣', '南投市', 23.9222475, 120.6723086),
(265, '柴之燒炭焙龍眼乾禮盒', '當龍眼採收季節，經驗老道的師傅，以龍眼樹枝為柴薪，遵循古法、乾柴慢火、煙燻如霧、耐心的將龍眼粒翻焙一遍又一遍，經歷三天兩夜的烘焙，造就完美風味。', '04-9269-1111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17543/480x360_205百味--中寮農會--05柴之燒禮盒-20151218172229.jpg', '南投縣', '中寮鄉', 23.8787397, 120.7678090999),
(266, '雙柚金饌禮盒', '麻豆文旦是最講究的水果。文旦迷們計較的不僅是文旦的產地與外型，還要精挑是否來自麻豆區的特別角落。臺灣文旦柚多以鮮食為主，但農會精選優質麻豆文旦，利用高效能萃取技術及低溫真空濃縮技術製成果茶，用酸甜口感感動每一位遊客，為地方特色產品打造一個新舞臺。', '06-571-5790', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17507/480x360_203麻豆農會_文旦柚茶02-20151218174449.jpg', '臺南市', '麻豆區', 23.176068, 120.2465369999),
(267, '龍眼干', '炎炎夏日，龍眼樹上結實纍纍的果實，在豔陽照射下，如金黃色般的珍珠，充滿著生命力。東山區的龍眼乾以土窯古法烘焙，採收後經過生火慢燻、脫粒、翻焙，去殼去籽，才能做出被稱為「福肉」的龍眼乾。關於東山龍眼的故事還很多，不妨走訪東山，來趟農趣體驗之旅。', '06-680-0814', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17544/480x360_201至202東山區農會_龍眼乾(綜合)-20151218181529.jpg', '臺南市', '東山區', 23.1781599, 120.483966),
(268, '芒果干', '購買專線：06-5731716、06-5731251\r\n聯絡信箱：ching200283@yahoo.com.tw\r\n左鎮是農業小鄉，有著許多不為外鄉民眾週知的新鮮一面，來到左鎮，別忘了區內正夯的伴手禮- 芒果干。左鎮區農會依循古法烘製芒果干，一顆芒果只取4 片果肉的作法，所製作出來的芒果干口感不死甜，反而品嚐得到芒果干特有濃郁香氣與酸甜滋味。', '06-573-1046', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17545/480x360_200百味--左鎮農會--台灣芒果干-20151218175141.jpg', '臺南市', '左鎮區', 23.0585278, 120.4077237),
(269, '瓊福滿溢', '瓊，瓊玉。即為美好的意思，以客家人此辛勤栽種的優良好米，加上番茄所製作的番茄米粉，都是在地人的誠意獻禮，希望收到好米與米粉的人，都吃到這份用心與美味，並得到美好福分就像泉水倒到杯子裡滿出來一樣。', '03-592-1173', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18090/480x360_058-101 瓊福滿溢_20121007 (400X320)-20151026114907.jpg', '新竹縣', '芎林鄉', 24.7742887, 121.0804279),
(270, '太麻里釋迦', '有「日昇之鄉」美名的太麻里，依山傍海，溫暖乾燥的氣候、及排水良好的砂質土壤，讓這裡產出的釋迦個頭碩大，籽小肉多，令人驚歎。撥開樸素暗綠的外表，太麻里大目釋迦白晳宛如奶色，果實飽滿紮實 ，肉香甜Q 彈，營養價值極高，還能有效補充體力，是活力滿載的水果！', '08-978-0400', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17574/480x360_10日光鮮果-05太麻里釋迦-20151217184559.jpg', '臺東縣', '太麻里鄉', 22.604987, 121.001855),
(271, '綺羅香蓮霧', '「綺羅香」乃古代高級珍貴絲織品散發著清香之意，枋寮地區農友細心呵護的黑珍珠蓮霧，型如吊鐘、肉質細膩、色澤紫紅、甜美多汁，故以此為名。蓮霧產期經多年的調節，由夏季水果成為冬季珍品，臺灣幾乎整年都可以吃到蓮霧，「綺羅香蓮霧」，如此百分百完美改造的臺灣之光，洋溢著南臺灣熱情的人情味。\r\n訂購專線：08-8713031', '08-878-5121', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17577/480x360_16枋寮地區農會_黑珍珠蓮霧-20151219135959.jpg', '屏東縣', '枋寮鄉', 22.3627913, 120.5965769),
(272, '台灣蜜棗', '新年想要「棗」幸福？「棗」圓滿？請用大社出品汁多爽口棗禮盒。由農會派專員自產銷班130 公頃蜜棗果園中，精選甜度高、肉質細緻、汁多、皮薄及脆度高者包裝成禮盒，為年節送禮最佳首選。', '07-351-1111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17578/480x360_18大社區農會_台灣蜜棗-20151217192443.jpg', '高雄市', '大社區', 22.7296147, 120.346602),
(273, '番石榴', '國產水果拔仔最熱評，拔仔產地直銷享新鮮，帝王、珍珠、紅心皆可口，大社精選最可靠，由農會派專員自產銷班200 公頃芭樂果園中，精選甜度高、肉質細緻、汁多、皮薄及脆度高者包裝成禮盒，不分時節全年度為您傳達心意。', '07-351-1111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17579/480x360_19大社區農會_番石榴-20151217192656.jpg', '高雄市', '大社區', 22.7296147, 120.346602),
(274, '富貴良緣禮盒', '「不起眼的農產品，經過設計包裝，就能成為精緻禮盒，為農民帶來合理的利潤。」社頭是傳統農業鄉鎮，每年7 月~ 8 月是龍眼產，從山腳路沿線瀰漫烘焙龍眼乾的淡淡香氣與白煙，農民守在磚造「焙灶」工寮內，守著爐火耐心烘焙，饒富人文歷史風味。為協助農民增加收益，農會以桂圓肉、桂圓麵和蜂蜜組成「富貴圓滿」、「富貴樂」與「富貴良緣」3 種大紅禮盒，是送禮的絕佳首選。\r\n訂購專線：04-8735437', '04-873-2516', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17516/480x360_221精選--社頭--富貴良緣禮盒-20151218162253.jpg', '彰化縣', '社頭鄉', 23.896825, 120.5847081999),
(275, '穀意禮', '「季節的更替，時光的流轉，眼見又是稻穗金黃熟成時，作田莊稼漢的辛勞，是嘸人完全知影耶！」儘管稻米名氣不若池上米、西螺米響亮， 田中鎮農會仍積極推廣農民辛苦栽種的優質好米；引領民眾認識稻穗的故鄉，感受農民的辛勤耕耘； 以獨特的故事行銷手法，走出「田中米」的自我特色。', '04-874-9211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17531/480x360_218田中鎮農會_穀意禮1-20151218163126.jpg', '彰化縣', '田中鎮', 23.85965819999, 120.5863643),
(276, '有機原釀禮饌醬油組', '滿州原生種小黑豆耐鹽又耐風，顆粒雖小，但豆味濃郁厚實。不必噴灑農藥，就能健康成長。農會看好「食安商機」，研發醬油、蔭油膏、豉鼓等產品，推出「原釀禮饌」有機黑豆醬油，味道芳香甘醇，強調古法釀造、無添加。\r\n訂購專線：08-8801849', '08-880-1849', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17532/480x360_215滿洲鄉農會_原釀禮饌-20151218163802.jpg', '屏東縣', '滿州鄉', 22.0195408, 120.83847),
(277, '一提兩麵', '集集山蕉是日據時代專供日本天皇品嚐的貢品。來到集集，除了拜訪古蹟景點，帶個伴手禮自然是少不了的。「一提兩麵」禮盒，內有千山香蕉麵、養生紅龍麵及鄉村蔬菜醬，口感香Q、口味獨特且包裝精美。', '04-9276-1700', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17535/480x360_214集集鎮農會_一提兩麵-20151218164526.jpg', '南投縣', '集集鎮', 23.8269924, 120.783617),
(278, '南投梅宴甜菊梅禮盒', '梅子從5 分熟的青綠果採到金黃軟綿的完熟果，口感各有風情。雖然梅子雖不能像水果一樣直接吃，但卻能變身各式各樣的製品。南投縣農會沿襲老祖母料理加工梅子的傳統手法，製成多款梅製品，酸酸甜甜的好滋味，讓人就像談了場戀愛一般，心情都快樂了起來！', '04-9225-1170', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17541/480x360_206百味--南投縣農會--06南投梅宴甜菊梅禮盒-20151218171832.jpg', '南投縣', '南投市', 23.9222475, 120.6723086),
(279, '玄海御饌-海鮮禮盒', '寶島臺灣四周環海，魚類資源豐富，玄海御饌收錄全臺各地漁貨之大成，嚴選臺南白蝦、午仔魚，永安石斑魚，沿海土魠魚，以及遠洋漁船捕獲的白鯧、軟絲，共六項上等鮮味，經急速冷凍處理，將最原始的鮮甜美味牢牢封印，是臺灣海洋鮮味的頂級饗宴。\r\n訂購專線：02-89853993', '02-8985-3966', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17546/480x360_199玄海御饌-20151218175432.jpg', '新北市', '三重區', 25.0668779, 121.4878575),
(280, '金鑽烏魚子禮盒', '鹿港老街擁有悠久歷史人文風華，街頭巷弄之間常見散發耀眼金色光芒的烏魚子。鹿港烏魚子以傳統古法製成，風味獨特口感絕佳，嚴選成熟烏魚卵製成，飽滿對稱，金黃透澄，古樸木盒外觀宛如鹿港般迷人，有著說不完的動人故事。', '04-778-8945', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17547/480x360_198彰化漁會_金鑽烏魚子禮盒1-20151218175705.jpg', '彰化縣', '鹿港鎮', 24.0587671, 120.434389),
(281, '彰漁蒲燒鰻禮盒', '鹿港地區有鰻魚王國的美稱，出產的鰻魚質地鮮嫩風味絕佳，營養價值極高。十多年來用心堅持品質，精選優質成鰻製成日式浦燒風味，通過HACCP、CAS 等標章認證，以真空包裝加熱即可食用，安全衛生，是年節送禮、犒賞家人的最佳選擇。', '04-778-8945', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17548/480x360_197上選鮮味--13彰化區漁會--蒲燒鰻禮盒-20151218175911.jpg', '彰化縣', '鹿港鎮', 24.0587671, 120.434389),
(282, '淡水魚藏吻魚雙醬', '波瀾壯闊的淡水，澄黃夕陽美景引人入勝，沿海物產豐盛，小星鑽－魩仔魚肉質鮮嫩美味，為頂級上品，富含蛋白質與鈣質，是補充營養的最佳來源。魚藏吻魚雙醬以魩仔魚做基底，分別加上東港櫻花蝦及精選干貝製作而成，口感更加豐富，滿足味蕾享受，吃起來輕鬆無負擔。', '02-2805-5000', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17566/480x360_195淡水區漁會_淡水魚藏雙醬-20151218180522.jpg', '新北市', '淡水區', 25.183685, 121.411379),
(283, '8 樂鮮果禮盒', '彰化縣社頭鄉由於產區的土質、氣侯極為適合芭樂果樹的生長，為全臺灣最早、最專業、最主要的芭樂產區之一，一年四季均有生產，肉質細緻、甜度高、脆度夠，歷年參加全國芭樂評鑑皆獲團體第一名，絕對是誠意足、份量夠的送禮選擇。\r\n社頭鄉農會長期輔導鄉內果農，加入吉園圃生鮮蔬果產銷班，以自然農法栽種安全、高品質的芭樂，每年並舉辦芭樂品質競賽，鼓勵果農栽種高優質的鮮果，歷年參加全國芭樂評鑑皆獲團體第一名，社頭農會評選出外型、品質、甜度均優異的鮮果，以八顆為單位組成「芭樂鮮果禮盒」，將嚴選果品做差異化行銷，提升果農的收益。', '04-873-2516', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17594/480x360_27日光鮮果-19社頭8樂鮮果-20151218221210.jpg', '彰化縣', '社頭鄉', 23.896825, 120.5847081999),
(284, '特選春 (冬) 茶 (原味)', '除與特選春(冬)茶(中度焙火)相同的原料外，茶葉細嫩、緊結、勻整、墨綠有光澤。茶湯水色澄黃、透明、亮麗。香氣具清香、幽雅、甜和、略帶花香。滋味香甜、甘醇、滑潤、爽口。', '04-9275-1962', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19326/480x360_特選春_(冬)_茶_(原味).jpg', '南投縣', '', 23.74097, 120.757563),
(285, '文山包種茶 (明月等級)', '文山包種茶以採摘軟嫩茶菁精心焙製而成，為條索狀，色澤墨綠鮮麗，茶湯蜜黃碧綠，入口醇郁濃厚，芳香撲鼻，回味無窮，乃茶中極品。明月、清風級茶葉，香味幽雅、味醇甘潤，是自用、送禮最佳的伴手禮。', '02-2968-5191', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17644/480x360_51新北市農會_文山包種茶-明月等級-20151218234728.jpg', '新北市', '板橋區', 25.010467, 121.460372),
(286, '石門鐵觀音極上品禮盒', '石門鐵觀音茶年年入選百大精品，湯色金黃明亮、香氣沈穩、茶湯滑順，入口生津，精緻小巧，攜帶便利。產品包裝設計與概念，融合飲茶浪漫情懷及品茗意境，徜徉湖光水色，清閒優雅渾然忘我，展現茶與生活之藝術。', '02-2638-1005', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17646/480x360_54石門區農會_鐵觀音1斤-20151218235250.jpg', '新北市', '石門區', 25.2909969, 121.5647637999),
(287, '淡水魚藏吻魚鮮醬', '波瀾壯闊的淡水，澄黃夕陽美景引人入勝，沿海物產豐盛，小星鑽－魩仔魚肉質鮮嫩美味，為頂級上品，富含蛋白質與鈣質，是補充營養的最佳來源。魚藏吻魚雙醬以魩仔魚做基底，分別加上東港櫻花蝦及精選干貝製作而成，口感更加豐富，滿足味蕾享受，吃起來輕鬆無負擔。\r\n訂購專線：02-28055000', '02-2805-5000', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17549/480x360_196淡水區漁會_淡水魚藏2-20151218180402.jpg', '新北市', '淡水區', 25.183685, 121.411379),
(288, '一口包烏魚子', '烏魚子是臺灣最具歷史紋理的美食之一，相傳數百年前的先民，因追捕烏魚來到臺灣定居，歷經多年演變，臺灣烏魚子已是全球聞名的MIT 美食代表。野生烏魚子的品質風味遠勝養殖烏魚子，富有嚼勁的彈牙感，香醇Q 實的味道讓許多饕客流連忘返愛不釋手。古法手工精製，營養價值極高，加上一口吃的輕便包裝，深受民眾喜愛。', '07-610-7551', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17653/480x360_190梓官漁會_一口包烏魚子-20151218182254.jpg', '高雄市', '梓官區', 22.727833, 120.2546205999),
(289, '龍膽石斑系列禮盒', '龍膽石斑為深海魚，是石斑魚種類中體型最大的，有「斑王」的稱號。龍膽石斑為高經濟食用魚，富含營養，是宴客餐桌上常見佳餚。具產銷 履歷與SGS 檢驗通過，適合三五好友或小家庭聚餐食用，在家裡也能品嚐頂級的海鮮美味。', '07-610-7551', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17654/480x360_191梓官漁會_龍膽石斑系列戀戀蚵仔寮-20151218182611.jpg', '高雄市', '梓官區', 22.727833, 120.2546205999),
(290, '極醬 XO 醬禮盒', '有人說，所謂XO 醬的名稱來源是因為製作時所使用的材料都是高級食材，與白蘭地干邑中的頂級酒XO 一樣屬於最高等級，所以稱為醬中的XO。東港鎮XO 醬以嚴選海鮮食材為基底，飽飽一罐中富含鮮甜的海洋風味及豐富的海鮮餡料，當湯匙一口挖起，吃進嘴滿滿的鮮味及海洋風味，彷彿沐浴在海風中，堪稱XO 醬界的第一把交椅！誰能抗拒！', '07-610-7551', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17457/480x360_188梓官漁會_極醬-20151218183114.jpg', '高雄市', '梓官區', 22.727833, 120.2546205999),
(291, '漁婦佳餚', '魩仔魚粥為臺灣飯桌上常見佳餚，是大海賜予人們的禮讚。嚴選產地新鮮現撈魩仔魚，與在地優質好米，搭配鮮採蔬菜如香菇、玉米等，使用溫火熬煮而成，新鮮營養的便利粥，風味絕佳入口即化，十足人情味的漁村文化特色包裝，是老少咸宜的產品。', '07-610-7551', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17456/480x360_187梓官漁會_漁婦家餚-20151218183449.jpg', '高雄市', '梓官區', 22.727833, 120.2546205999),
(292, '二尖山高山茶', '南投中寮鄉二尖茶區， 海拔高度1,000~1,200 公尺，經年雲霧繚繞，土壤肥沃雨量適中，優異條件的地理環境，造就出具香、醇、濃、韻、美獨到特色二尖山高山茶。二尖茶以其嫩葉製成，成品外觀油墨亮綠、水色蜜黃澄清、滋味清香甘醇，令人回味無窮。\r\n訂購專線：049-2693934', '04-9269-1111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17656/480x360_35中寮鄉農會_二尖茶-20151218230935.jpg', '南投縣', '中寮鄉', 23.8787397, 120.7678090999),
(293, '合歡山霜韻茶', '生長於合歡山麓的烏龍茶，歷經春雨、夏風、秋霜、冬雪，在手工精摘、火文細焙的呵護下，茶葉出落得色澤翠綠鮮活，茶湯清澈明亮而金黃，滋味滑軟甘醇，香氣持久渾厚，葉肉厚，果膠質含量高，耐沖泡，入喉後茶味綿延彌久，「霜韻茶」實至名歸。', '04-9292-0480', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17657/480x360_36仁愛鄉農會_霜韻茶03-20151218231119.jpg', '南投縣', '仁愛鄉', 24.00481419999, 121.0889033999),
(294, '大禹嶺朱鵲紅茶', '山林間的紅寶石─朱鵲紅茶，座落於山之巔，雲霧瀰漫的大禹嶺茶區，是國內目前海拔高度最高的紅茶。香氣甜潤裡帶秀雅花香，滋味醇郁中隱現甘韻，茶葉外觀條索勻整，茶湯呈美麗的酒紅色，就如同披著一身朱紅亮麗外衣、棲身高海拔山區的酒紅朱雀般珍貴稀有，名茶與珍禽相互輝映。', '04-9292-0480', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17658/480x360_37仁愛鄉農會_朱鵲紅01-20151218231307.jpg', '南投縣', '仁愛鄉', 24.00481419999, 121.0889033999),
(295, '柿餅加工系列產品-柿糬', '來自新埔新鮮柿餅，製作而成的柿糬。本產品採用在地製作柿餅，結合客家麻糬，以花生及山藥為內餡,純手工製作，一入口有柿餅的自然香甜，QQ軟軟的麻糬,蘊藏著濃郁花生或山藥的口味，是一種古早味新感受。', '03-588-4351', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18091/480x360_061-096 柿餅加工系列產品-柿糬_20071226 (400X320-01)-20151026115414.jpg', '新竹縣', '新埔鎮', 24.8310298, 121.0874593),
(296, '杉林溪烏龍茶', '杉林溪茶，一心二葉，片片手採，只挑鮮嫩綠芽，葉芽柔軟，葉面厚實。由於生長環境高寒，終年雲霧籠罩，土壤肥沃雨量適中，造就茶色翠綠清透，呈現淡雅的天然果香，又終年生長杉樹間，以杉木為鄰 ，故其味道有苦中帶甘之口感，益添清雅悠遠。', '04-9264-2186', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17659/480x360_39竹山鎮農會_杉林溪烏龍茶-20151218231716.jpg', '南投縣', '竹山鎮', 23.756698, 120.6846309999),
(297, '杉林溪烏龍茶 (新品種)', '杉林溪茶，一心二葉，片片手採，只挑鮮嫩綠芽，葉芽柔軟，葉面厚實。由於生長環境高寒，終年雲霧籠罩，土壤肥沃雨量適中，造就茶色翠綠清透，呈現淡雅的天然果香，又終年生長杉樹間，以杉木為鄰 ，故其味道有苦中帶甘之口感，益添清雅悠遠。', '04-9264-2186', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17639/480x360_40竹山鎮農會_杉林溪新品種烏龍茶-20151218231848.jpg', '南投縣', '竹山鎮', 23.756698, 120.6846309999),
(298, '玉山高海拔優良茶', '高山冷涼、平均日照短，特殊的地理條件，是培植烏龍茶的絕佳環境，凌駕群峰的「玉山高山茶」，葉色澤鮮活翠綠、香氣輕揚悠雅，久聞不厭，沖泡後的茶湯更是溫潤滑順、喉韻生津，讓人口齒留香。', '0975-801-959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17640/480x360_41信義鄉農會_玉山烏龍茶2-20151218232346.jpg', '南投縣', '信義鄉', 23.68870099999, 120.861791),
(299, '南投茶宴', '南投縣高山林立，有著豐盛、多樣的茶葉製品，設「南投茶宴」以堂皇隆重款待消費者，提供境內優質的、可以驕傲地亮出產品履歷條碼「身分證」的茶葉。高山茶色澤翠綠鮮活，水色蜜綠顯黃，滋味甘醇滑軟，香氣淡雅，歡迎來找茶！\r\n訂購專線：049-2251170', '04-9225-1170', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17641/480x360_42南投縣農會_南投茶宴-20151218232534.jpg', '南投縣', '南投市', 23.9222475, 120.6723086),
(300, '日月潭典藏禮盒', '在湖光山色，霧氣瀰漫猶如畫境般美景的日月潭，踏訪紅茶的故鄉。挑TEA，就是要有挑剔的精神，紅玉、阿薩姆、紅韻、藏芽&hellip;&hellip;，變化萬千的是茶韻，始終不變的是對日月潭禮盒對紅茶品質的堅持。\r\n訂購專線：049-2898453', '04-9289-5505', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17662/480x360_43魚池鄉農會_典藏禮盒-20151218232852.jpg', '南投縣', '魚池鄉', 23.89618859999, 120.9350395999),
(301, '日月潭精選禮盒', '在湖光山色，霧氣瀰漫猶如畫境般美景的日月潭，踏訪紅茶的故鄉。挑TEA，就是要有挑剔的精神，紅玉、阿薩姆、紅韻、藏芽&hellip;&hellip;，變化萬千的是茶韻，始終不變的是對日月潭禮盒對紅茶品質的堅持。', '04-9289-5505', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17643/480x360_44魚池鄉農會_精選禮盒-20151218233023.jpg', '南投縣', '魚池鄉', 23.89618859999, 120.9350395999),
(302, '日月潭紅璽禮盒', '一次聚集日月潭紅茶4 大茶種：臺茶18 號、臺茶21 號、阿薩姆紅茶及臺灣山茶，各具不同香氣及韻味。', '04-9289-5505', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17664/480x360_45魚池鄉農會_紅席禮盒-20151218233153.jpg', '南投縣', '魚池鄉', 23.89618859999, 120.9350395999),
(303, '春 (冬) 茶', '純樸古意的茶農、綠油油的茶田、氤氳的茶香，南投可謂「茶的故鄉」。具傳統焙火熟果香、芬芳怡人，茶湯水色明亮、豔麗、琥珀色。滋味甘醇、滑潤、爽口。來自傳統凍頂茶區，焙製方法由傳統工法製作，具地方特色口味。\r\n訂購專線：049-2751962', '04-9275-1962', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17600/480x360_46鹿谷鄉農會_春(冬)茶300公克x2-20151218233323.jpg', '南投縣', '', 23.74097, 120.757563),
(304, '春( 冬) 茶', '純樸古意的茶農、綠油油的茶田、氤氳的茶香，南投可謂「茶的故鄉」。具傳統焙火熟果香、芬芳怡人，茶湯水色明亮、豔麗、琥珀色。滋味甘醇、滑潤、爽口。來自傳統凍頂茶區，焙製方法由傳統工法製作，具地方特色口味。', '04-9275-1962', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17665/480x360_47鹿谷鄉農會_春(冬)茶150公克x2-20151218233457.jpg', '南投縣', '', 23.74097, 120.757563),
(305, '特選春 (冬) 茶 (中火)', '產於鹿谷鄉海拔1500公尺高山茶區，常年雲霧繚繞，日夜溫差大，茶菁柔軟肥厚內容豐富，茶葉細嫩，緊結、勻整、墨綠有光澤。茶湯水色澄黃、透明、亮麗、香氣清雅、略帶花香。滋味香甜、甘醇、柔順、飲後口齒留香、生津味無窮。', '04-9275-1962', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19322/480x360_特選春_(冬)_茶_(中火).jpg', '南投縣', '', 23.74097, 120.757563),
(306, '特選春 (冬) 茶 (中火)', '產於鹿谷鄉海拔1500公尺高山茶區，常年雲霧繚繞，日夜溫差大，茶菁柔軟肥厚內容豐富，茶葉細嫩，緊結、勻整、墨綠有光澤。茶湯水色澄黃、透明、亮麗、香氣清雅、略帶花香。滋味香甜、甘醇、柔順、飲後口齒留香、生津味無窮。', '04-9275-1962', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19318/480x360_特選春_(冬)_茶_(中火)禮盒.jpg', '南投縣', '', 23.74097, 120.757563),
(307, '吉利/珍珠鮑魚禮盒', '臺灣東北角貢寮依山傍海景觀秀麗，為知名旅遊聖地，東臨太平洋之濱，水質清澈，盛產海鮮名聞遐邇，所孕育出的鮑魚肉質Q 彈，採收鮮活九孔鮑魚製作成開罐即食的鮑魚罐頭，美味有如生鮮現撈，貢寮鮑為品牌保證，是節慶送禮最佳選擇。', '02-2490-1105', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17567/480x360_194貢寮區漁會_貢寮鮑1-20151218180921.jpg', '新北市', '貢寮區', 25.056794, 121.926509),
(308, '鱸魚鬆隨行包', '金目鱸魚為中華料理常見高級食材，肉質細緻綿密，滑溜順口，富含營養價值，常見調理方式為燉湯或清蒸。遵循古法研製的鱸魚鬆，從鱸魚原料開始即用心嚴選通過檢驗，讓魚鬆保留新鮮的傳統風味，香氣四溢的美味來自於製造者的用心，食用方便且利於攜帶，為當地著名人氣產品。', '07-691-2020', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17568/480x360_193永安區漁會_鱸魚鬆-20151218181226.jpg', '高雄市', '永安區', 22.8175669, 120.210497),
(309, '農會牌肉酥禮盒', '近來食安問題引起恐慌，為讓大家買的放心，吃的安心，特選CAS 認證健康豬後腿肉，肉質鮮美富有彈性，不添加豆粉、防腐劑、色素及糖精等人工化學物，加入特調醬料細心醃製，並經多道精密程序處理而成，真材實料滋味絕佳，口感香酥，是饋贈親友的最佳伴手禮。\r\n訂購專線：0800-666980', '02-2968-5191', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17581/480x360_192上選鮮味--新北市農會--農會牌肉鬆禮盒-20151218181412.jpg', '新北市', '板橋區', 25.010467, 121.460372),
(310, '漁婦佳餚', '有人說，所謂XO 醬的名稱來源是因為製作時所使用的材料都是高級食材，與白蘭地干邑中的頂級酒XO 一樣屬於最高等級，所以稱為醬中的XO。東港鎮XO 醬以嚴選海鮮食材為基底，飽飽一罐中富含鮮甜的海洋風味及豐富的海鮮餡料，當湯匙一口挖起，吃進嘴滿滿的鮮味及海洋風味，彷彿沐浴在海風中，堪稱XO 醬界的第一把交椅！誰能抗拒！', '07-610-7551', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17759/480x360_189梓官漁會_漁婦佳餚--烏魚子xo醬-20151218183242.jpg', '高雄市', '梓官區', 22.727833, 120.2546205999),
(311, '花蓮鰹干貝 XO 醬', '天色未亮朦朧之際，漁夫航行於蔚藍東部海洋，隨著鰹魚動向流浪四方。鰹魚是臺灣民眾最喜愛的魚類之一，蛋白質含量極高，進行加工後可製成柴魚片或鰹魚罐頭。而鰹干貝XO 醬，僅採用油脂含量高的頂級肥美鰹魚，嚴選生魚片食用部位作為主要材料，再佐以金華火腿及干貝精製調味而成，濃郁香甜渾然天成，最能代表花蓮在地獨特風味，一入口彷彿海洋鮮味無窮無盡散播開來，不論拌飯拌麵、料理美食皆為一絕。', '03-822-3118', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17758/480x360_186花蓮區魚會_XO醬-20151218183728.jpg', '花蓮縣', '花蓮市', 23.9962107, 121.6385013),
(312, '煙仔平', '後山花蓮景觀壯麗，水質潔淨，蘊育出最佳魚種品質，鰹魚俗稱炸彈魚，為臺灣東部海域特有的迴游性魚類，夏秋之際為其盛產期，也是鰹魚最為肥美的時節。鰹魚吸收太平洋黑潮精華，富含Omega-3 脂肪酸、EPA、DHA 及多元不飽和脂肪酸，均為人體需要的天然營養元素。本商品拆封後即可使用，共有4 種口味( 蜜汁、辣味、味噌、佃煮) 供消費者選購。', '03-822-3118', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17757/480x360_185花蓮區漁會_煙仔乎-20151218183911.jpg', '花蓮縣', '花蓮市', 23.9962107, 121.6385013),
(313, '旗魚鬆', '漁夫屏氣凝神，注視著海上偌大身軀的稀客，外形雄壯的旗魚為東港著名特產，也是遊客來到此地必嚐的美食。旗魚白中透肉色，味道甘美鮮脆口感佳，是養分極高的魚類，在臺灣及日本常是壽司與生魚片的珍品。此項產品已蟬聯7 年臺灣農漁會百大精品，受到廣大消費者喜愛，採用嚴選新鮮旗魚製作，入口即能感受香酥鮮甜的原始海洋風味。', '08-832-3121', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17585/480x360_184上選鮮味--屏東東港--旗魚鬆-20151218184050.jpg', '屏東縣', '東港鎮', 22.4677798, 120.4417895),
(314, '海龍王龍膽石斑分切禮盒', '龍膽石斑本來是深海魚，但因為臺灣具有得天獨厚的地理條件，加上多年來政府政策強力支持下，養殖魚友的養殖技術不斷有新的突破，純熟養殖技術與各種種苗培育經驗，讓這種營養價值高、經濟價值更高的魚種普及化。「海龍王龍膽石斑分切禮盒」突破大型龍膽石斑魚對於一般家庭的食用不便，分切不同部位及份量真空包裝，提供消費者新鮮、安全、方便的新選擇，也滿足每一位送禮者的心。', '08-875-1601', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17586/480x360_183上選鮮味--林邊區農會--龍膽石斑分切禮盒-20151218184331.jpg', '屏東縣', '林邊鄉', 22.432048, 120.515759),
(315, '鯖宴罐頭禮盒', '鯖魚是南方澳名產，全年都可捕獲，從十月到次年農曆年前，是鯖魚最肥美的季節。漁會嚴選南方澳漁港豐富的漁產，製作4 種不同種類之罐頭，其現撈現做之口感，並含豐富DHA、EPA，為全國頂級夢幻逸品，因鯖魚為本地最大宗漁獲，故以「鯖宴」為名，代表以鯖魚為首，其他魚類為輔，提供給消費者之味蕾享受。\r\n訂購專線：03-9972808', '03-996-2108', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18548/480x360_5b7391c97ae54aafb7c3e593b4e4ed47.jpg', '宜蘭縣', '蘇澳鎮', 24.5833868, 121.8659277),
(316, '臺灣鯛魚鬆 (脯) 禮盒', '歷經多年改良成功，肉質鮮甜沒有土味，體型碩大的臺灣鯛，被譽為臺灣國寶魚，臺南市為全國產量最大的地方，所製成的鯛魚酥香酥可口，色香味俱全，深受消費大眾喜愛，與細軟帶勁的鯛魚脯同為臺灣前進海外市場的頂級旗艦產品。', '06-632-4151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17754/480x360_178台南市農會_台灣鯛-20151218190312.jpg', '臺南市', '新營區', 23.31088189999, 120.3208439999);
INSERT INTO `gift` (`id`, `name`, `feature`, `tel`, `picurl`, `city`, `town`, `lat`, `lng`) VALUES
(317, '虱目魚酥 (脯) 禮盒', '虱目魚被臺南縣誌列為南灜第一特產，臺南可說是虱目魚的故鄉，充滿濃濃道地家鄉味與懷舊情感，肉質鮮嫩爽口，加工而成的虱目魚酥香脆甘鮮，虱目魚脯細軟帶勁，兩者口味獨特極受好評。', '06-632-4151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17755/480x360_179台南市農會_虱目魚酥-20151218190653.jpg', '臺南市', '新營區', 23.31088189999, 120.3208439999),
(318, '安農香腸禮盒', '購買專線：04-26710909\r\n聯絡信箱：yiniuniu33@gmail.com\r\n採用當天現宰的豬後腿肉為原料，分別添加葡萄酒，以及在地特色農作物，製成葡萄酒香腸、大安蔥香腸及芋頭香腸，口感Q 彈多汁有嚼勁，鮮甜美味。', '04-2671-2229', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17587/480x360_177大安區農會_安農香腸1-20151218190850.jpg', '臺中市', '大安區', 24.3463873, 120.5870304),
(319, '蜜棗', '燕巢具泥火山獨特地理環境，土質含豐富氧化鎂，所生產的蜜棗皆為網室栽培，皮薄多汁，果實脆美甘甜，鮮翠的果實在黃熟之後各有不同的 風味。「燕之巢」蜜棗皆有吉園圃安全蔬果標章，當天採收當天配送，給您最鮮甜的享受。', '07-616-2211#252', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17588/480x360_21燕巢區農會_台灣蜜棗-20151218214806.jpg', '高雄市', '燕巢區', 22.7932786, 120.362621),
(320, '珍珠芭樂', '芭樂是燕巢鄉的綠色之寶，燕巢鄉也是珍珠芭樂的夢幻產地。燕巢因經泥火山岩漿土壤沖積，土質含豐富氧化鎂及其他微量元素，造就了珍珠芭樂的獨特風味，果實飽滿脆甜，果肉細緻且肉厚籽少，甘甜中略帶爽口的迷人酸香，不愧為消費者首選。', '07-616-2211#252', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17671/480x360_22燕巢區農會_芭樂-20151218215147.jpg', '高雄市', '燕巢區', 22.7932786, 120.362621),
(321, '二崙上好禮-金蜜洋香瓜', '瓜農翻鬆泥土，蓋上黑色塑膠布挖洞，種下洋香瓜幼苗，以隧道式棚架每日保護照顧，待幼苗慢慢長大，撥開綠葉隙中見一粒粒碩大的果實，這是瓜農辛勤栽培的甜蜜寶貝。二崙鄉土壤肥沃，排水良好，頂級「金蜜」洋香瓜綿密香甜，風味絕佳，清涼入口，齒香滿溢，令人難以忘懷。', '05-598-6001', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17632/480x360_23雲林縣二崙鄉農會_金蜜洋香瓜-20151218215516.jpg', '雲林縣', '二崙鄉', 23.773172, 120.412133),
(322, '有機酪梨禮盒', '酪梨營養成份極高、富含優質油脂，有「森林的奶油」及「幸福果」美譽，是補充營營首選果品。農人善待土地，土地也會給予回報，人養地，地養人，中埔有機酪梨是溫暖的有機幸福果。', '05-253-2201', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17591/480x360_24中埔鄉農會_有機酪梨-20151218220333.jpg', '嘉義縣', '中埔鄉', 23.4728954, 120.5384742),
(323, '有機火龍果禮盒', '火龍果是很好種但很難照顧的水果，在中埔農民細心呵護下，碩大而鮮紅欲滴的有機火龍果，外型吉祥討喜，口感綿密，甜味適中而清爽，低卡又營養，深受現代人喜愛。', '05-253-2201', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17592/480x360_25中埔農會_頂級火龍果-20151218220806.jpg', '嘉義縣', '中埔鄉', 23.4728954, 120.5384742),
(324, '燒仙草', '購買專線：03-5872010、03-5875288\r\n聯絡信箱：ksn.kusi@msa.hinet.net\r\n1.內容物:仙草粉、太白粉、糖將1包即溶燒仙草倒入杯中，用少許溫開水攪拌溶解，再加入約200cc剛沸騰熱開水，迅速攪拌使之完全溶解即可，食用時可視個人喜好另行添加紅豆、花生、粉圓、八寶豆等材料。2.內容物:仙草粉將即溶仙草3g*1包溶於約1000cc開水中再添適量砂糖即可用。可做仙草茶、仙草凍、仙草冰、燒仙草、仙草雞、仙草排骨等', '03-587-4277', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18738/480x360_20200328-23.JPG', '新竹縣', '關西鎮', 24.8032728, 121.1601904),
(325, '溫室小番茄', '來自「御品紅」的新鮮溫室小番茄，披著櫻桃般血紅色的外衣，活潑的模樣代表是「在欉紅」，每一顆都是用心栽培的成果。個頭嬌小可愛，香味濃郁滿盈，皮薄肉質細緻，多汁的口感，讓人一顆接一顆塞入口停不下來！', '05-236-0311', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17593/480x360_26嘉義縣農會_番茄-20151218221024.jpg', '嘉義市', '西區', 23.4718835, 120.431524),
(326, '梨山茶', '梨山茶具有上等優渥栽種環境的條件，海拔高氣候寒冷，常年雲霧籠罩，日夜溫差大，又因吸收了週遭梨果香味，茶葉苦澀少、更添清香。 農民更堅持手工採一心二葉，採取最鮮嫩，最優質的茶葉，所製成的茶品清雅芬芳，茶湯味後甘醇，回韻悠揚持久。', '04-2594-1518', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17595/480x360_28台中市和平區農會_梨山茶-20151218221835.jpg', '臺中市', '和平區', 24.174661, 120.885178),
(327, '韻紅紅茶禮盒 (蜜香)', '木柵「韻紅紅茶｣系列是傳統與創新的完美結合，嚴選一心二葉茶菁製成，外觀條索緊結油潤，水色鮮艷紅亮，茶香優雅，滋味醇厚，喉韻渾厚，七泡有餘香！喝｢韻紅｣呼你｢穩紅｣！', '02-2939-8021', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17635/480x360_30木柵區農會_韻紅(蜜香)1-20151218222148.jpg', '臺北市', '文山區', 24.988743, 121.56723),
(328, '台中八仙山紅茶', '紅茶是一種全發酵茶，茶湯則呈深紅色，發酵作用使得茶葉中的茶多酚和單寧酸減少，產生了茶黃素、茶紅素等新的成份和醇類、醛類、 酮類、酯類等芳香物質，因此香氣撲鼻，少了苦澀味，味道更醇厚。八仙山茶區地理環境及其水質等自然條件因素，使沖泡後產生的茶香、口感相當獨特，讓人為之驚艷，加上產量稀少，實屬茶中珍品。', '04-2594-1518', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17634/480x360_29台中市和平區農會_八仙山紅茶-20151218222000.jpg', '臺中市', '和平區', 24.174661, 120.885178),
(329, '觀韻紅紅茶禮盒', '木柵「韻紅紅茶｣系列是傳統與創新的完美結合，嚴選一心二葉茶菁製成，外觀條索緊結油潤，水色鮮艷紅亮，茶香優雅，滋味醇厚，喉韻渾厚，七泡有餘香！喝｢韻紅｣呼你｢穩紅｣！\r\n訂購專線：02-29398021#201', '02-2939-8021', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17597/480x360_31木柵區農會_觀韻紅紅茶禮盒8-20151218222336.jpg', '臺北市', '文山區', 24.988743, 121.56723),
(330, '韻紅紅茶禮盒', '木柵「韻紅紅茶｣系列是傳統與創新的完美結合，嚴選一心二葉茶菁製成，外觀條索緊結油潤，水色鮮艷紅亮，茶香優雅，滋味醇厚，喉韻渾厚，七泡有餘香！喝｢韻紅｣呼你｢穩紅｣！\r\n訂購專線：02-29398021#201', '02-2939-8021', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17678/480x360_32木柵區農會_韻紅4-20151218222502.jpg', '臺北市', '文山區', 24.988743, 121.56723),
(331, '紅烏龍茶茶包', '游走在紅茶與烏龍茶之間的「紅烏龍」，品茶人喻之為柔情俠義、武功高強、深藏不露的俠骨之士。茶湯水色琥珀橙紅，明亮澄清有如紅茶的茶湯色澤，滋味醇厚而圓滑，茶質具熟果香，兼具烏龍茶的清甜和紅茶的甘醇，冷熱泡皆宜，特別是冷泡風味令人驚艷！', '08-955-1052', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17598/480x360_33鹿野農會_紅烏龍-20151218230202.jpg', '臺東縣', '鹿野鄉', 22.9116099, 121.135994),
(332, '台東好茶典藏禮盒', '一壺好茶的誕生，除了需要天然純淨的栽種環境，道道繁複而嚴謹的程序，也是關鍵要素。來自臺東太麻里、鹿野優良茶區的好茶，由茶農精耕、手採、細搓、慢焙而成，嚴選柔軟、芬芳、醇化而不苦澀的茶菁，滋味甘醇滑軟，茶湯色明亮，香氣獨到迷人。\r\n訂購專線：089-513111', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17599/480x360_34台東縣農會_台東好茶-20151218230802.jpg', '臺東縣', '', 22.7555114, 121.1427744),
(333, '杉林溪紅露/綠芽茶包禮盒', '杉林溪茶，一心二葉，片片手採，只挑鮮嫩綠芽，葉芽柔軟，葉面厚實。由於生長環境高寒，終年雲霧籠罩，土壤肥沃雨量適中，造就茶色翠綠清透，呈現淡雅的天然果香，又終年生長杉樹間，以杉木為鄰 ，故其味道有苦中帶甘之口感，益添清雅悠遠。', '04-9264-2186', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17637/480x360_38竹山鎮農會_杉林溪紅露綠芽-20151218231529.jpg', '南投縣', '竹山鎮', 23.756698, 120.6846309999),
(334, '山美茶蘊禮盒', '三峽地區山景優美，所產的茶回甘蘊香，山美茶蘊訴求慢享生活的茶禪意境，在講求寂與靜的茶禪韻律美學中，凝聚一道心靈軌跡，讓茶香洗淨滌慮，在回甘中品嚐層次的醇美，一享品味生活茶禪意境。', '02-2671-1002', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17669/480x360_53三峽區農會_山美茶蘊禮盒-20151218235114.jpg', '新北市', '三峽區', 24.93354499999, 121.3761899999),
(335, '茉莉語茶', '燒一壺水，看花瓣在水中舒卷綻放，凝神傾聽花兒低語，啜一口「茉莉語茶」，口感香潤甘甜，品味那份閒適與悠然。那是花農們懷著感恩的心，親手摘下每一朵以安全農法栽種的茉莉花，在花朵盛開之時即用乾燥處理法製得，封存保留住花香原味，在品茗者口中重新綻開&hellip;&hellip;\r\n產地直送，如何訂購茉莉語茶: 花壇鄉農會', '04-786-7821', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17680/480x360_72花壇農會_茉莉語茶-20151219002720.jpg', '彰化縣', '花壇鄉', 24.02599, 120.537346),
(336, '將軍花翎春秋又八千', '走入霧峰林家最著名的宅第宮保，不難發現其中懸掛著一塊「春秋又八千」金色祝壽牌匾，其字體出自為清未傑出書法家楊草仙的狂草書跡，筆致圓潤、靈妙奇變、情感豐富、氣勢恢宏。在地的霧峰農會酒莊特別選定霧峰最高級的香米做為原料，精餾監造的純米燒酎，其香醇順口、餘味繞香，搭配這豐富底蘊的字體，以及獨具特色的包裝，絕對是作為長輩祝壽、賀歲的好禮品。', '04-2339-3404', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17681/480x360_165霧峰農會_春秋又八千01-20151219003219.jpg', '臺中市', '霧峰區', 24.0651751, 120.679522),
(337, '大安好酒', '釀酒師研習日本製酒製作流程，再依農作物之特性加以改良，並運用酒本身的溫度，將作物天然香氣封存於酒中，淡雅的酒香伴隨著溫順口感入喉，舌尖餘韻別緻，釀造出屬於臺灣本地的燒酒。', '04-2671-2229', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17682/480x360_166大安農會_大安好酒-20151219003848.jpg', '臺中市', '大安區', 24.3463873, 120.5870304),
(338, '特級龍眼蜜', '「龍眼蜜」亦稱「龍眼花蜜」，是蜜蜂採集龍眼花朵所分泌的花蜜，質地滑潤，香味濃郁。龍眼花開季節，正是蜂農釀製「龍眼蜜」的好季節，此時來嚐嚐新鮮的蜂蜜，一口甜甜的滋味與淡淡的花果香氣，這就是幸福的味道！更重要的，它是天然的滋養食品，上天的恩賜與蜜蜂們辛勤努力的成果。\r\n訂購專線：06-6802816', '06-680-0814', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17683/480x360_167東山區農會_特級龍眼蜜-20151219004031.jpg', '臺南市', '東山區', 23.1781599, 120.483966),
(339, '慕麗達諾麗原汁', '喝一口慕麗諾麗果原汁，入口甜中帶酸，不時挑動您的味蕾。嚴選在地農民種植的新鮮諾麗果釀造、自然發酵製成，其原汁可幫助調節生理機能、養顏美容、促進新陳代謝、幫助消化，平常只要加點冰開水或果汁稀釋飲用，就可以享受到酸酸甜甜的幸福口感，果然深受女性朋友喜歡。', '06-783-2105', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17684/480x360_168學甲區農會_慕麗達諾麗原汁-20151219004259.jpg', '臺南市', '學甲區', 23.230796, 120.1823667999),
(340, '百里飄香', '以新鮮百香果原汁為釀造原料，經過優質酵母菌低溫發酵而成，再進行完全密封低溫熟成後，取其精華酒液而得的百香果釀造酒。酒液色澤澄清且略帶黃色，香氣濃郁，口感滑順，它的酸味及香氣能觸動您整個味蕾。\r\n​&bull; 榮獲2015年德國國際烈酒評鑑金質獎\r\n&bull; 榮獲2012年國內酒莊酒品評鑑銅牌獎\r\n&bull; 榮獲2010年酒莊酒品評鑑銅牌獎\r\n&bull; 榮獲2009年酒莊酒品評鑑銀牌獎\r\n&bull; 榮獲2008年酒莊酒品評鑑金牌獎\r\n&bull; 榮獲2007年酒莊酒品評鑑銀牌獎\r\n&bull; 榮獲2005年酒莊酒品評鑑銀牌獎\r\n訂購專線：049-2423828', '04-9298-6420', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17685/480x360_169南投縣埔里鎮農會 百里飄香-20151219004435.jpg', '南投縣', '埔里鎮', 23.963721, 120.9641679999),
(341, '玫瑰花語', '埔里的氣候和水質特佳，是植物生長的最佳環境。運用埔里山城優質安全的玫瑰花，以花入酒，浸漬在地獨特的紅甘蔗基礎酒裡，將花獨特的香芬釋放在酒中，與酒達到完美的結合。美麗色澤的視覺感受加上清香滑潤的口感，甜而不膩，在濃濃的甘蔗酒香中，保留著玫瑰花的香濃芬芳，一口就同時擁有浪漫與幸福的感覺。\r\n訂購專線：049-2423828', '04-9298-6420', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17618/480x360_170南投縣埔里鎮農會 玫瑰花語-20151219004632.jpg', '南投縣', '埔里鎮', 23.963721, 120.9641679999),
(342, '玫瑰賞', '埔里的氣候和水質特佳，是植物生長的最佳環境。運用埔里山城優質安全的玫瑰花，以花入酒，浸漬在地獨特的紅甘蔗基礎酒裡，將花獨特的香芬釋放在酒中，與酒達到完美的結合。美麗色澤的視覺感受加上清香滑潤的口感，甜而不膩，在濃濃的甘蔗酒香中，保留著玫瑰花的香濃芬芳，一口就同時擁有浪漫與幸福的感覺。\r\n訂購專線：049-2423828', '04-9298-6420', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17686/480x360_171南投縣埔里鎮農會 玫瑰賞-20151219004802.jpg', '南投縣', '埔里鎮', 23.963721, 120.9641679999),
(343, '羅漢門龍眼蜂蜜', '說到「龍眼的故鄉」，你不能不知道「內門」這個地方。這裡每年春天的龍眼花盛開，所散發出的濃郁花香味，蜜蜂紛紛聞香而來，其所採集的花蜜，是天然香純的龍眼花蜜，是極品中的極品。清新的植物香、口感滑潤、生津回味，無論是拿來泡茶、淋在土司或鬆餅上面，都非常美味可口，是媽媽族和小朋友們的最愛。', '07-667-2161', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17687/480x360_172內門區農會_羅漢果龍眼蜜-20151219004941.jpg', '高雄市', '內門區', 22.94175459999, 120.4613715),
(344, '2015 新竹縣評鑑龍眼蜜', '龍眼花蜜是蜂蜜之冠，富含花粉粒及活性脢，清涼退火、品味醇美，是天然營養食品，人體最佳活力的來源。農會在今年榮獲新竹縣龍眼蜂蜜評鑑特等獎及頭等獎之殊榮，蜂蜜品質優良，是蜜中之極品，深獲消費者信賴。內含多種微量礦物質，可補充體內所需之營養素，主要為鉀，其次是鈣、鎂、鈉、硫、磷等等，蜂蜜中富含類黃酮及胺基酸等有機成分，對於重視保養身體的您來說，是個最佳選擇。', '03-592-1018#9', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17688/480x360_173新竹縣農會_龍眼蜜-20151219005430.jpg', '新竹縣', '芎林鄉', 24.787296, 121.068628),
(345, '湖莓戀', '嚴選大湖草莓精心釀製而成，屬水果釀造酒類，酒精度11％，低溫釀造製程，經過時間的焠鍊，酒液呈現金黃琥珀色澤，打開酒瓶，濃濃的草 莓果香躍然而出，入口甜中帶酸，回韻中帶有苦味，挑動你的味蕾，冰鎮飲用，風味更佳。', '03-799-4800', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17690/480x360_175大湖地區農會_湖莓戀-20151219005956.jpg', '苗栗縣', '大湖鄉', 24.4289864, 120.8660828),
(346, '吉利市光波禮盒(雪梨/綠盒)', '產期：12月~翌年2月\r\n規格：\r\n	高山雪梨(雪梨)6粒裝(8 台斤)\r\n	高山雪梨5粒裝(8台斤)', '04-2587-2151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17502/480x360_007 東勢區農會_吉利市光波(綠) (400x320)-20151220004200.jpg', '臺中市', '東勢區', 24.2534386, 120.829146),
(347, '石門鐵觀音極上品禮盒', '石門鐵觀音茶年年入選百大精品，湯色金黃明亮、香氣沈穩、茶湯滑順，入口生津，精緻小巧，攜帶便利。產品包裝設計與概念，融合飲茶浪漫情懷及品茗意境，徜徉湖光水色，清閒優雅渾然忘我，展現茶與生活之藝術。', '02-2638-1005', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17647/480x360_55石門區農會_鐵觀音半斤-20151218235420.jpg', '新北市', '石門區', 25.2909969, 121.5647637999),
(348, '茶顏悅色', '清香芬芳的包種茶，香氣幽雅飄逸，滋味甘潤，入口生津；東方美人茶特殊的熟果香和蜜香，茶湯濃厚香醇，是讓英國伊莉莎白女王念念不忘的茶葉。製成茶包，方便您隨時快速享用兩種特色茶，是味蕾的雙重滿足。', '02-2665-7227', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17604/480x360_56坪林區農會_茶顏悅色方便茶飲-20151218235555.jpg', '新北市', '坪林區', 24.936102, 121.7117779999),
(349, '東方美人茶禮盒', '購買專線：03-5802207\r\n聯絡信箱：g1006@bps.org.tw\r\n北埔聞名於世的「膨風茶」，因得天獨厚環境孕育與薰陶，及小綠葉蟬的吸吮與蘊釀，使其茶湯有濃郁的花香、果香、蜜香渾然天成，非一般茶葉所能比擬之奇特風味、堪稱茶中極品。', '03-580-2207', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17650/480x360_57北埔鄉農會_東方美人茶-20151218235730.jpg', '新竹縣', '北埔鄉', 24.700475, 121.055203),
(350, '東方美人茶禮盒(4兩)', '新竹以「膨風茶」聞名於世，其外觀奇特細緻、1 心2葉，心呈白毫，有紅、白、黃、褐、綠等5色，又稱白毫烏龍或東方美人茶，因得天獨厚環境孕育及茶葉綠小蟬的吸吮與蘊釀，使茶湯富含濃郁花香、果香與蜜香，堪稱茶中極品。\r\n訂購專線：03-5800216#7', '03-580-0216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17651/480x360_58峨眉鄉農會_東方美人茶(紅)6-20151218235919.jpg', '新竹縣', '峨眉鄉', 24.6887921, 121.0195946),
(351, '東方美人茶禮盒 (半斤)', '當桐花落下，是浮塵子盡情吸食茶菁的季節，所製成的東方美人茶，是大地對新竹的恩賜，是在地對傳統的堅持，自然、甘醇、回韻留香，代表峨眉湖畔的美人，高貴大方又典雅，品嚐一抹紅的靜謐。', '03-580-0216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17652/480x360_59峨眉鄉農會_東方美人茶(白)1-20151219000053.jpg', '新竹縣', '峨眉鄉', 24.6887921, 121.0195946),
(352, '阿里山經典紅茶禮盒', '來自阿里山山脈高海拔優良茶區，分佈在北回歸線南、北50 公里內，海拔1,000-1,500 公尺間臺地上，氣候陰冷，終年雲霧籠罩，日夜溫差大，極適合茶樹培育。經茶農深耕、手採、細搓、輕揉慢烘勻焙，再經本會茶師嚴格監製及認證，確保品質，盛名享譽國際，是自用饋贈兩相宜的極品茶。', '05-261-2120', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17608/480x360_60竹崎地區農會_阿里山經典紅茶禮盒-20151219000303.jpg', '嘉義縣', '竹崎鄉', 23.5216026, 120.550813),
(353, '阿里山高山茶禮盒', '來自阿里山山脈高海拔優良茶區，分佈在北回歸線南、北50 公里內，海拔1,000-1,500 公尺間臺地上，氣候陰冷，終年雲霧籠罩，日夜溫差大，極適合茶樹培育。經茶農深耕、手採、細搓、輕揉慢烘勻焙，再經本會茶師嚴格監製及認證，確保品質，盛名享譽國際，是自用饋贈兩相宜的極品茶。\r\n訂購專線：05-2612120#201', '05-261-2120', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17609/480x360_61竹崎地區農會_阿里山高山茶-20151219000448.jpg', '嘉義縣', '竹崎鄉', 23.5216026, 120.550813),
(354, '阿里山高山茶', '到嘉義梅山來，感受被雲霧繚繞、漫步在雲端的氛圍，不僅有美景，還有好茶為伴。由茶農精耕、手採、細搓、輕揉、慢焙、所製之精緻茶葉，滋味甘醇滑軟，具獨特的香氣、喉韻及金黃茶色、耐沖泡，為梅山鄉阿里山高山茶樹立良好的信譽品質。', '05-262-2100', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17602/480x360_64梅山鄉農會_雲端上的甘露-20151219000811.jpg', '嘉義縣', '梅山鄉', 23.581617, 120.558779),
(355, '阿里山高山茶 (紅茶)', '到嘉義梅山來，感受被雲霧繚繞、漫步在雲端的氛圍，不僅有美景，還有好茶為伴。由茶農精耕、手採、細搓、輕揉、慢焙、所製之精緻茶葉，滋味甘醇滑軟，具獨特的香氣、喉韻及金黃茶色、耐沖泡，為梅山鄉阿里山高山茶樹立良好的信譽品質。', '05-262-2100', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17603/480x360_65梅山鄉農會_阿里山高山--紅茶-20151219001002.jpg', '嘉義縣', '梅山鄉', 23.581617, 120.558779),
(356, '阿里山高山茶 (梅山茶區)', '到嘉義梅山來，感受被雲霧繚繞、漫步在雲端的氛圍，不僅有美景，還有好茶為伴。由茶農精耕、手採、細搓、輕揉、慢焙、所製之精緻茶葉，滋味甘醇滑軟，具獨特的香氣、喉韻及金黃茶色、耐沖泡，為梅山鄉阿里山高山茶樹立良好的信譽品質。', '05-262-2100', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17605/480x360_66梅山鄉農會_阿里山--梅山茶區-20151219001152.jpg', '嘉義縣', '梅山鄉', 23.581617, 120.558779),
(357, '阿里山高山茶(青心烏龍)', '精選純正阿里山脈生產手採茶，茶區平均海拔1300~1500公尺,，茶葉色澤鮮活翠綠，茶色金黃明亮而且耐沖泡，飲之香氣馥郁芬芳，入口濃厚甘潤而不生澀，誠為愛好品茗者不容錯過的優質高山茶。\r\n訂購專線：05-2591360', '05-259-2360', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17606/480x360_67番路鄉農會_阿里山高山茶2-20151219001450.jpg', '嘉義縣', '番路鄉', 23.46359299999, 120.5542659999),
(358, '茉莉賞茶御品禮盒(烏龍+紅茶)', '「茉莉賞茶御品禮盒」嚴選高山烏龍茶、紅茶，加以窨合花壇鄉安全農法栽種的茉莉花，並承襲古老「窨花拼和」製茶工藝，製作工序繁複，完美調和茉莉與茶葉香氣，充分發揮「茶引花香、花增茶味」的特色。花香味清新、茶湯味鮮甜，自用安心、喝來沁心；外包裝高貴典雅，送禮送得貼心。\r\n訂購專線：04-7877558', '04-786-7821', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17611/480x360_70花壇農會_茉莉御品禮盒-20151219002230.jpg', '彰化縣', '花壇鄉', 24.02599, 120.537346),
(359, '花鳥風月', '「花鳥風月袋茶」一秉初衷嚴選臺灣好茶配上無毒茉莉，提盒結構以傳統中式茶壺為藍本，打造視覺、味覺、嗅覺都充滿茉莉的驚喜，透 過創意與設計的呈現，讓您在品味茉莉茶品的瞬間，即刻沈浸於鳥語花香的美麗世界。\r\n訂購專線：04-7877558', '04-786-7821', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17617/480x360_71花壇農會_花鳥風月(藍紅組合)-20151219002537.jpg', '彰化縣', '花壇鄉', 24.02599, 120.537346),
(360, '心意足桑葚禮盒', '嚴選新鮮桑椹，掌握最佳採收時機及在地榨汁製作，無論是添加冰糖蒸煮及濃縮精製而成的果汁，或是經過3 ~ 6 個月自然發酵和時間浸釀的果醋，營養價值都非常高，略帶酸甜的口感，嚐到的每一口都是幸福，冰鎮過後，風味更是一絕。在這個講求健康養生的年代，這類的鹼性食品可以調整體內酸鹼值、促進健康，對無肉不歡及偏酸性體質的現代人來說, 是個值得推薦的健康好飲品。\r\n訂購專線：05-3412003', '05-341-1301', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17689/480x360_174義竹鄉農會_果醋-20151219005646.jpg', '嘉義縣', '義竹鄉', 23.3384173, 120.241176),
(361, '吉利市光波禮盒(豐水梨、新興梨/紅盒)', '產期：5月~10月\r\n規格：\r\n	高山雪梨(豐水梨、新興梨)7粒裝(7 台斤)\r\n	高山雪梨(豐水梨、新光梨)8粒裝(7台斤)', '04-2587-2151', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17722/480x360_006 東勢區農會_吉利市光波(紅) (400x320)-20151220003127.jpg', '臺中市', '東勢區', 24.2534386, 120.829146),
(362, '阿里山鄒特產 (茶包組)', '嚴選來自阿里山高海拔之手採頂級烏龍，100% 在地生產，茶色翠綠清透，茶韻清揚，滋味甘醇，令人齒頰生津。卷縮的茶葉在水中緩緩舒展開來，茶香綻放，思緒也隨著輕揚&hellip;&hellip;，高山茶湯，質軟順口，香氣濃郁，值得行家細細品味。', '05-256-2502', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17704/480x360_063 阿里山鄉農會_阿里山高山茶1 (400x320)-20151220005057.jpg', '嘉義縣', '阿里山鄉', 23.4690425, 120.701457),
(363, '富麗幸福禮盒', '在旅途中，我們可以寄一張風景明信片，對友人訴說旅程的點點滴滴。富麗幸福禮盒，以明信片風格包裝的六袋好米，不只為親朋好友呈現旅途所見花蓮的風景，還有花蓮的味道。', '03-882-1705', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17612/480x360_073 花蓮富里鄉_幸福禮盒 (400x320)-20151220152232.jpg', '花蓮縣', '富里鄉', 23.1838646, 121.2969674),
(364, '味自慢頂級壽司米', '壽司好吃，不只要食材新鮮，還要搭配香甜的壽司米。要吃到如此好吃的米不用跑壽司店，橫跨四條溪流的雲林斗南鎮出產的「味自慢頂 級壽司米」，用日本先進低溫碾米設備加工，外觀清透、內容飽滿、味道香甜，讓您在家也能嚐到高級壽司店才有的風味。', '05-595-0535', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17615/480x360_089 斗南農會_味百慢01 (400x320)-20151220181419.jpg', '雲林縣', '斗南鎮', 23.6775271, 120.47588),
(365, '土地之歌有機系列', '花東縱谷，全臺灣最晚被開墾的地帶，有最少污染的水土。長在這沒有化肥與農藥的乾淨的土地上，潔淨又美味的有機臺稉74 號米，是對這塊良田福地的歌頌，象徵人與土地的共生共榮。 新鮮良米經慈心有機認證，以CAS 認證碾米廠碾製，全程低溫保存，讓響徹花東縱谷的「土地之歌」，傳到你家廚房。', '03-856-9033', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18550/480x360_c39ee100da93463d8c77254160e131d9.jpg', '花蓮縣', '花蓮市', 24.010114, 121.583013),
(366, '特等米 1.5 公斤禮盒', '池上鄉位處花東縱谷最純淨自然的稻米產區，鄉內水田土壤肥沃，溪水夾帶著豐富的有機礦物質，還有全臺唯一的內陸淡水沼澤「大坡池」調節水位。 池上米過去在日據時代是進貢日本天皇的米，故被稱為「貢米」。名聞遐邇的「池上飯包」，就是發源於此地，池上飯包所標榜的材料，正是池上米。\r\n俗話說：「天才是一分的天賦加上九十九分的努力所造就出來的。」池上鄉雖然擁有天然的稻米生長環境，與優良稻米品種的支援，但是農民種植稻米的用心與專業，才是讓池上米享譽全國的主要因素。 池上一年四季不同景觀，春天，萬物欣欣向榮的絕色之美；夏季，放眼望去是一整片綠油油的田野，隨風搖曳的稻子因為陽光照射，像是綠海波浪；秋天，大地慢慢轉換成金黃色的稻浪，稻穗飽滿隨風輕輕 擺動，一波波金黃色稻浪在你眼前翻滾黃金地毯；進入隆冬時節，田園裡蒼翠不再，伯朗大道、天堂路兩旁大片金黃油菜花盛開美不勝收，讓喧囂擾嚷心靈，得到放鬆的純粹。\r\n訂購專線：089-864880', '08-986-4880', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17705/480x360_075 池上鄉農會_特等米 (400x320)-20151220154704.jpg', '臺東縣', '池上鄉', 23.08902709999, 121.2278084999),
(367, '池上生活米禮盒-馬卡龍香米禮盒', '池上鄉位處花東縱谷最純淨自然的稻米產區，鄉內水田土壤肥沃，溪水夾帶著豐富的有機礦物質，還有全臺唯一的內陸淡水沼澤「大坡池」調節水位。 池上米過去在日據時代是進貢日本天皇的米，故被稱為「貢米」。名聞遐邇的「池上飯包」，就是發源於此地，池上飯包所標榜的材料，正是池上米。\r\n俗話說：「天才是一分的天賦加上九十九分的努力所造就出來的。」池上鄉雖然擁有天然的稻米生長環境，與優良稻米品種的支援，但是農民種植稻米的用心與專業，才是讓池上米享譽全國的主要因素。池上一年四季不同景觀，春天，萬物欣欣向榮的絕色之美；夏季，放眼望去是一整片綠油油的田野，隨風搖曳的稻子因為陽光照射，像是綠海波浪；秋天，大地慢慢轉換成金黃色的稻浪，稻穗飽滿隨風輕輕 擺動，一波波金黃色稻浪在你眼前翻滾黃金地毯；進入隆冬時節，田園裡蒼翠不再，伯朗大道、天堂路兩旁大片金黃油菜花盛開美不勝收，讓喧囂擾嚷心靈，得到放鬆的純粹。\r\n訂購專線：089-864880\r\n產地直送，如何訂購   池上鄉農會 (csfa.com.tw)', '08-986-4880', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17707/480x360_077 池上鄉農會_馬卡龍香米禮盒 (400x320)-20151220161235.jpg', '臺東縣', '池上鄉', 23.08902709999, 121.2278084999),
(368, '樂禾趣', '從上空鳥瞰桃園，可以看見數不盡的埤塘波光粼粼，百年來灌溉桃園的大地從來不變，但是田地卻是不斷求新求變，種出好吃的稻米。尤其新屋得天獨厚，兼有生氣蓬勃的漁港與肥沃的稻田。樂禾趣禮盒精選最好的「新香」米，外觀晶瑩剔透，內容結實飽滿，烹煮時散發出陣陣濃郁宜人的芋頭香，吃起來更是口感香Q 甜美。\r\n訂購專線：03-4772124#210', '03-477-2124', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17679/480x360_087 新屋鄉農會_樂禾趣 (400x320)-20151220180820.jpg', '桃園市', '新屋區', 24.9723065, 121.107596),
(369, 'CAS 首選好米', '雲林斗南鎮有四條溪流橫貫，水源充沛、土地肥沃，所產稻米為雲林之最。但是好米還要更好，農會以「田間生產紀錄」、「農業經營專 區」、「地產地銷」等嚴格的生產管控，通過 CAS 認證、ISO-22000 及 HAMLP 國際食品安全驗證，生產「CAS 首選好米」。香甜的滋味、 有彈性的口感，是斗南農民的驕傲。', '05-595-0535', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17614/480x360_088 斗南農會_cas首選好米02 (400x320)-20151220181223.jpg', '雲林縣', '斗南鎮', 23.6775271, 120.47588),
(370, '米滿良緣', '米滿良緣禮盒，有東方的如意圖騰設計。 「米滿良緣」象徵著圓滿豐足的含義，代表婚姻不只是愛情圓滿，更蘊含兩人攜手走向富足的未來。', '05-598-6001', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17616/480x360_090 在地好米--二崙農會米滿良緣 (400x320)-20151220181728.jpg', '雲林縣', '二崙鄉', 23.773172, 120.412133),
(371, '黃金囍米禮盒', '北緯 23.5&deg; 北回歸線，赤道的最北位置所經過的太保市，日夜溫差大、日照恰當、土壤豐腴，又有嘉南大圳源源不絕的灌溉，是連年豐收的沃野。太保農民守護著稻田與太陽約定，他們以金黃色的稻作立契，不讓農藥、化肥污染這片土地，替有機太保米「香 Q 好吃」掛保證。禮盒附上檜木筷子，讓吃飯時享受檜木香味混和太保米的美味，雙重享受。', '05-371-1101', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17675/480x360_091 太保農會_黃金囍米 (400x320)-20151220181936.jpg', '嘉義縣', '太保市', 23.4576038, 120.3320694),
(372, '豐和日粒禮盒', '北緯 23.5&deg; 北回歸線，赤道的最北位置所經過的太保市，日夜溫差大、日照恰當、土壤豐腴，又有嘉南大圳源源不絕的灌溉，是連年豐收的沃野。太保農民守護著稻田與太陽約定，他們以金黃色的稻作立契，不讓農藥、化肥污染這片土地，替有機太保米「香 Q 好吃」掛保證。禮盒附上檜木筷子，讓吃飯時享受檜木香味混和太保米的美味，雙重享受。', '05-371-1101', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17676/480x360_092 太保農會_豐禾日粒 (400x320)-20151220182511.jpg', '嘉義縣', '太保市', 23.4576038, 120.3320694),
(373, '溪州黑鑽紫黑米', '位在濁水溪沿岸的溪州鄉，日日受到帶有稀有礦物元素的濁水溪滋潤，各種穀物欣欣向榮、營養豐富。 在這肥沃的濁水溪岸最不可多得的，是溪州馳名的紫黑米。紫黑米含有高量的花青素、賴胺酸、蛋白質。在豐饒的濁水溪邊栽種出的黑糯米尤其營養好吃，且經重金屬污染、農藥殘留檢驗合格，是您營養補給的最佳選擇。', '04-889-5610', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17730/480x360_094 溪州鄉農會_紫黑米 (400x320)-20151220183114.jpg', '彰化縣', '溪州鄉', 23.85153659999, 120.4949632),
(374, '知竹嚐樂米', '濁水溪濁而肥沃，在下游的濁水溪沖積扇留下一片沃野，位於沖積扇西南端的竹塘鄉，百年來以得天獨厚的肥沃水源灌溉稻田，如今稻米界新星誕生於此。台稉 9 號「竹塘米」在年年稻米競賽中，為竹塘鄉民帶來榮耀，正因為吸收了濁水溪的精華，竹塘米擁有獨一無二的美味口感，快來品嚐！\r\n訂購專線：04-8972002#302', '04-897-2002', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17731/480x360_095 竹塘鄉農會_竹塘米 (400x320)-20151220183808.jpg', '彰化縣', '竹塘鄉', 23.85944119999, 120.4243546),
(375, '幸福穗稻禮盒', '沐浴溫暖陽光下，生長於大甲溪與大肚溪滋潤的土地上，加上臺中農民用心耕種，今年又是豐收的一年。滿田稻穗垂掛，頂級越光米當期新米粒粒飽滿。每口咬下，都是天地人的精華。搭配麻布袋造型的古樸編織材質外盒，再現懷舊農村風情，「幸福穂稻禮盒」給您滿滿的幸福味。', '04-2302-3560', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17732/480x360_096 台中地區農會_幸福穗稻 (400x320)-20151220184128.jpg', '臺中市', '西區', 24.14655949999, 120.6632846),
(376, '養生糙米系列(糙米粥+糙米脆片)', '濁水溪畔以產米聞名，出產的埤頭鄉【臺梗九號】糙米，富含植物纖維及營養素，具有養生保健的功能。以糙米為基底所研發而成的粥品及脆片，口感豐富、香味濃郁，是全家早餐、宵夜、點心、零嘴的好伴侶。', '04-892-2211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17749/480x360_153埤頭鄉農會_養生糙米-20151222093915.jpg', '彰化縣', '埤頭鄉', 23.8913137, 120.4634237999),
(377, '山味米禮盒', '關山鎮就像一個大公園，一邊是海岸山脈，另一邊則是中央山脈，位於兩山凹處的就是曠野平疇的關山了。 來關山，當然不能錯過品質優良的關山米，甜度、亮度、保存度都有好名聲。關山米產區，取新武呂溪來自中央山脈純淨的水源，加上設計優良的關山大圳，提供良好灌溉系統，使得關山鎮的稻米 「得天獨厚」，已成為全台稻米最優良的產區之一，像臺梗139 號、關山香米，都是近年來頗受好評的米種。\r\n「關山米」成功背景因素，最重要的還是在於人和，因為有一群致力於品質提昇的專業農友用心的付出，當然農糧單位及各農會的全力輔導，也是功不可沒。', '08-981-1022', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17661/480x360_078 關山鎮農會_山味米禮盒 (400x320)-20151220163632.jpg', '臺東縣', '關山鎮', 23.04794679999, 121.1641561),
(378, 'CAS 關山良質米', '關山鎮就像一個大公園，一邊是海岸山脈，另一邊則是中央山脈，位於兩山凹處的就是曠野平疇的關山了。 來關山，當然不能錯過品質優良的關山米，甜度、亮度、保存度都有好名聲。\r\n關山米產區，取新武呂溪來自中央山脈純淨的水源，加上設計優良的關山大圳，提供良好灌溉系統，使得關山鎮的稻米 「得天獨厚」，已成為全台稻米最優良的產區之一，像臺梗139 號、關山香米，都是近年來頗受好評的米種。 「關山米」成功背景因素，最重要的還是在於人和，因為有一群致力於品質提昇的專業農友用心的付出，當然農糧單位及各農會的全力輔導，也是功不可沒。', '08-981-1022', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17663/480x360_079 關山鎮農會_良質米 (400x320)-20151220165958.jpg', '臺東縣', '關山鎮', 23.04794679999, 121.1641561),
(379, '關山好米禮盒', '關山鎮就像一個大公園，一邊是海岸山脈，另一邊則是中央山脈，位於兩山凹處的就是曠野平疇的關山了。 來關山，當然不能錯過品質優良的關山米，甜度、亮度、保存度都有好名聲。關山米產區，取新武呂溪來自中央山脈純淨的水源，加上設計優良的關山大圳，提供良好灌溉系統，使得關山鎮的稻米 「得天獨厚」，已成為全台稻米最優良的產區之一，像臺梗139 號、關山香米，都是近年來頗受好評的米種。 「關山米」成功背景因素，最重要的還是在於人和，因為有一群致力於品質提昇的專業農友用心的付出，當然農糧單位及各農會的全力輔導，也是功不可沒。\r\n訂購專線：089-814903', '08-981-1022', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17666/480x360_080 關山鎮農會_好米禮盒 (400x320)-20151220170604.jpg', '臺東縣', '關山鎮', 23.04794679999, 121.1641561),
(380, '關於這裡米禮盒', '關山鎮就像一個大公園，一邊是海岸山脈，另一邊則是中央山脈，位於兩山凹處的就是曠野平疇的關山了。 來關山，當然不能錯過品質優良的關山米，甜度、亮度、保存度都有好名聲。關山米產區，取新武呂溪來自中央山脈純淨的水源，加上設計優良的關山大圳，提供良好灌溉系統，使得關山鎮的稻米 「得天獨厚」，已成為全台稻米最優良的產區之一，像臺梗139 號、關山香米，都是近年來頗受好評的米種。 「關山米」成功背景因素，最重要的還是在於人和，因為有一群致力於品質提昇的專業農友用心的付出，當然農糧單位及各農會的全力輔導，也是功不可沒。', '08-981-1022', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17667/480x360_081 關山鎮農會_關於這裡米禮盒 (400x320)-20151220170856.jpg', '臺東縣', '關山鎮', 23.04794679999, 121.1641561),
(381, '夢田之宴花漾禮盒', '太平洋邊、冬山河與蘭陽溪間，宜蘭五結鄉的沃野已開墾兩百多年。受充分水源的滋潤，年年產出量多又好吃的稻米。鄰近的濱海溼地，每年冬天吸引大量候鳥捕食充足的魚蝦，是土地乾淨的證明。現在，五結鄉百年良田要推出全新風味，有香Q 的蘭陽五農米、鮮米、瓊香出眾的五農香米、源自日本的越光米。吃完一頓有蘭陽風味的飯後，再來一杯用宜蘭綠茶與稻米炒製的玄米茶，頓時清爽無比。\r\n訂購專線：03-9503190', '03-950-1161', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17668/480x360_082 在地好米--五結鄉--夢田之宴花樣禮盒 (400x320)-20151220171213.jpg', '宜蘭縣', '五結鄉', 24.6851467, 121.7976477),
(382, '夢田之宴伴手禮盒', '太平洋邊、冬山河與蘭陽溪間，宜蘭五結鄉的沃野已開墾兩百多年。受充分水源的滋潤，年年產出量多又好吃的稻米。鄰近的濱海溼地，每年冬天吸引大量候鳥捕食充足的魚蝦，是土地乾淨的證明。現在，五結鄉百年良田要推出全新風味，有香Q 的蘭陽五農米、鮮米、瓊香出眾的五農香米、源自日本的越光米。吃完一頓有蘭陽風味的飯後，再來一杯用宜蘭綠茶與稻米炒製的玄米茶，頓時清爽無比。', '03-950-1161', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17672/480x360_083 在地好米--五結鄉--夢田之宴伴手禮 (400x320)-20151220171420.jpg', '宜蘭縣', '五結鄉', 24.6851467, 121.7976477),
(383, '糙米產品系列組合', '濁水溪畔以產米聞名，出產的埤頭鄉【臺梗九號】糙米，富含植物纖維及營養素，具有養生保健的功能。以糙米為基底所研發而成的粥品及脆片，口感豐富、香味濃郁，是全家早餐、宵夜、點心、零嘴的好伴侶。', '04-892-2211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17748/480x360_152埤頭鄉農會_養生糙米-20151222093850.jpg', '彰化縣', '埤頭鄉', 23.8913137, 120.4634237999),
(384, '養生糙米系列(什穀糙米漿+糙米脆片)', '濁水溪畔以產米聞名，出產的埤頭鄉【臺梗九號】糙米，富含植物纖維及營養素，具有養生保健的功能。以糙米為基底所研發而成的粥品及脆片，口感豐富、香味濃郁，是全家早餐、宵夜、點心、零嘴的好伴侶。', '04-892-2211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17750/480x360_154埤頭鄉農會_養生糙米2-20151222093932.jpg', '彰化縣', '埤頭鄉', 23.8913137, 120.4634237999),
(385, '養生糙米系列(糙米粥+什穀糙米漿)', '濁水溪畔以產米聞名，出產的埤頭鄉【臺梗九號】糙米，富含植物纖維及營養素，具有養生保健的功能。以糙米為基底所研發而成的粥品及脆片，口感豐富、香味濃郁，是全家早餐、宵夜、點心、零嘴的好伴侶。', '04-892-2211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17751/480x360_155埤頭鄉農會_養生糙米1-20151222093946.jpg', '彰化縣', '埤頭鄉', 23.8913137, 120.4634237999),
(386, '國姓客庄咖啡', '臺灣人愛咖啡，早已名揚海外。九份二山的國姓客庄咖啡，在農民不辭辛勞、用心照料之下所生產出來的咖啡豆，粒粒圓潤、碩大飽滿，每嚐一口盡是濃郁香醇、回甘純厚，是咖啡迷不容錯過的好滋味。', '04-9272-1510', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17511/480x360_150國姓鄉農會_客庄咖啡-20151222093445.jpg', '南投縣', '國姓鄉', 24.024487, 120.846814),
(387, '福肉', '炎炎夏日，龍眼樹上結實纍纍的果實，在豔陽照射下，如金黃色般的珍珠，充滿著生命力。東山區的龍眼乾以土窯古法烘焙，採收後經過生火慢燻、脫粒、翻焙，去殼去籽，才能做出被稱為「福肉」的龍眼乾。關於東山龍眼的故事還很多，不妨走訪東山，來趟農趣體驗之旅。\r\n訂購電話：06-6802816', '06-680-0814', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17752/480x360_201至202東山區農會_龍眼乾-20151222095947.jpg', '臺南市', '東山區', 23.1781599, 120.483966),
(388, '冷凍即煮山藥禮盒', '環繞台北市區的青山上，滿是自然的瑰寶。士林區農會的「冷凍即煮山藥」，使用的是在山明水秀的陽明山採收的優質山藥。鮮採的陽明山山藥經削皮切塊、真空包裝後直接冷凍，保存山藥的美味與營養，而且不添加任何添加物。烹調免退冰，料理方便，讓您即刻享受營養衛生的美食。', '02-2882-8959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17709/480x360_098 士林區_山藥2 (400x320)-20151220230004.jpg', '臺北市', '士林區', 25.0932124, 121.523289),
(389, '洛神花', '有低污染淨土，與太平洋海風的吹拂，台東洛神花紫紅鮮豔，不需要色素，厚實的花瓣可沖泡出濃郁的紫色洛神茶，還含有豐富的花青素、黃酮素、多酚，營養豐富，作為冷飲、熱飲都好喝。', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17710/480x360_099 台東縣農會_洛神花 (400x320)-20151220230443.jpg', '臺東縣', '', 22.7555114, 121.1427744),
(390, '原色金針 (無硫)', '有低污染淨土，與太平洋海風的吹拂，台東洛神花紫紅鮮豔，不需要色素，厚實的花瓣可沖泡出濃郁的紫色洛神茶，還含有豐富的花青素、黃酮素、多酚，營養豐富，作為冷飲、熱飲都好喝。', '089-513111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17692/480x360_100 台東縣農會_原色金針 (400x320)-20151220230619.jpg', '臺東縣', '卑南鄉', 22.6955793, 121.022134),
(391, '小米', '台東地區豐富優美的原住民族文化，台東縣農會為了推薦部落傳統食材給消費大眾，推出「鳥兒歡唱─紅藜、樹豆與小米」，將部落健康美食介紹給社會大眾。 談到小米，就會想到原住民的小米酒，這說明小米和原住民之間息息相關。小米又稱為「栗」，它的黏性較強，柔軟富有彈性，也可製成糕餅，是相當美味的食物；在許多原住民的豐年祭中，更是必備的祭品之一，以祈求小米能年年豐收。', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17711/480x360_101 至103台東縣農會_小米 樹豆 紅藜 (400x320)-20151220231100.jpg', '臺東縣', '', 22.7555114, 121.1427744),
(392, '紅藜', '台東地區豐富優美的原住民族文化，台東縣農會為了推薦部落傳統食材給消費大眾，推出「鳥兒歡唱─紅藜、樹豆與小米」，將部落健康美食介紹給社會大眾。 紅藜，這樣一個翠綠垂墜狀的植物，長在原住民的部落，已經數千年，除了用來釀製小米酒外，也是非常重要的副食品，最常加在米飯中一起食用。原住民朋友就說，吃了紅黎釀製的小米酒會讓人「精神愉快，心情開心、可以解除煩惱」。', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17693/480x360_101 至103台東縣農會_小米 樹豆 紅藜 (400x320)-20151220231250.jpg', '臺東縣', '', 22.7555114, 121.1427744),
(393, '樹豆', '台東地區豐富優美的原住民族文化，台東縣農會為了推薦部落傳統食材給消費大眾，推出「鳥兒歡唱─紅藜、樹豆與小米」，將部落健康美食介紹給社會大眾。 提起「樹豆」，多數人一定流露出「那是什麼東西？」樹豆，被認為是幸福的代表。曾有一個傳說，一顆會跳動的樹豆，把美麗的女孩帶到鄰村去，因此得到好姻緣，從此樹豆大量被種植，以感念它帶來的恩賜。', '089-513111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17694/480x360_101 至103台東縣農會_小米 樹豆 紅藜 (400x320)-20151220231550.jpg', '臺東縣', '卑南鄉', 22.6955793, 121.022134),
(394, '洛神花', '台東太麻里，全台灣第一個迎接太陽東昇的地方。每天，洛神花沐浴在清晨自海平面升起的陽光，受陣陣清爽吹拂，在太麻里這片少受工 業污染的土地上欣欣向榮。經過仔細的乾燥加工後，洛神花依舊紅潤如紅寶石，不需人工染色或添加物。將洛神花置滾水中煮過，滿壺水是鮮豔亮麗的紫紅色，一口飲下，生津止渴，熱飲、冰鎮皆適宜。\r\n購買專線：08-9781531\r\n聯絡信箱：taimali03@taimalifarmer.com.tw', '08-978-0400', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17695/480x360_104 太麻里地區農會_洛神花 (400x320)-20151220231745.jpg', '臺東縣', '太麻里鄉', 22.604987, 121.001855),
(395, '真空綠竹筍', '竹子與關廟更有不解之緣，古早時候關廟的竹編器具早已暢銷全臺。今日的關廟利用前人留下的蒼翠竹林，生產富含纖維質的新鮮綠竹筍。收割竹筍，必須在天亮之前掘地將竹筍從竹子的根部割下。若到太陽出來後，竹筍吸收到日光，就會發芽長成竹子不能吃了。可見每一稞竹筍，都是農民辛勞的結晶。關廟區農會出品的真空包綠竹筍，利用高溫高壓蒸煮技術，將竹筍日出前的鮮﹑嫩﹑甜鎖住，加工過程不添加防腐劑，安全衛生，獲得國際品質及食品安全認證。\r\n產地直送，如何訂購: 台南市關廟區農會 (guanmiao.com.tw)', '06-596-6278', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17696/480x360_105 關廟農會_綠竹筍 (400x320)-20151220232127.jpg', '臺南市', '關廟區', 22.9624989, 120.3284754),
(396, '芝麻醬', '台南西港有名的胡麻，不只可以榨油還可以製作香醇美味的芝麻醬。由老師傅精選西港芝麻洗淨、曬乾、烘焙、研磨而成的芝麻醬，不管是塗抹麵包、拌麵或拌沙拉，風味香醇美味，令人回味無窮。\r\n產地直送，如何訂購: 台南市西港區農會', '06-795-2706', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17697/480x360_106 西港區農會_芝麻醬 (400x320)-20151220232706.jpg', '臺南市', '西港區', 23.1236716, 120.2051215),
(397, '黑麻油', '300 年前的西港區，曾是風帆片片的臺江內海港口。 多年後，滄海變桑田，繁榮的海港變成肥沃的田野，西港如今以油脂豐厚的胡麻聞名。西港區農會嚴格把關土壤與農藥殘留的契作農田，種出上等胡麻，遵古法煉製，產出的黑麻油與清麻油，營養、香醇又美味。', '06-795-2706', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17698/480x360_107 西港區農會_黑麻油 (400x320)-20151220232855.jpg', '臺南市', '西港區', 23.1236716, 120.2051215),
(398, '清麻油', '300 年前的西港區，曾是風帆片片的臺江內海港口。 多年後，滄海變桑田，繁榮的海港變成肥沃的田野，西港如今以油脂豐厚的胡麻聞名。西港區農會嚴格把關土壤與農藥殘留的契作農田，種出上等胡麻，遵古法煉製，產出的黑麻油與清麻油，營養、香醇又美味。', '06-795-2706', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17699/480x360_108 西港區農會_清麻油 (400x320)-20151220233141.jpg', '臺南市', '西港區', 23.1236716, 120.2051215),
(399, 'A 贏黑豆蔭油禮盒', '昔時倒風內海風帆片片，下營更是繁華，一度被稱作「小揚州」。歷經一番滄海桑田後，港口已不再，但是留給下營一片肥沃的原野。如今下營有三寶：白鵝.蠶桑.黑豆，尤其以黑豆台南 3 號及台南 5 號，是本地獨有的極品蔭油原料。\r\nA 贏黑豆蔭油採傳統陶缸釀製，留住下營黑豆特有的香蘊，提供家庭更優質的烹調食材，在飲食色. 香.味俱全之外，並兼顧安全.衛生.營養的需求，是在地、健康、美味、自然的產品。', '06-689-2209', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17700/480x360_109 下營區農會_A贏黑豆禮盒 (400x320)-20151220233404.jpg', '臺南市', '下營區', 23.2372631, 120.2672728999),
(400, '胡麻清油', '熱帶氣候下的嘉南平原，終年陽光普照，孕育各種作物。在曾文溪蜿蜒而過的善化區，從來是甘蔗盛產地，現在更有出名的胡麻，粒粒飽滿，最適合榨油。尤其善化區農會以「真」的精神生產的胡麻油，完全採用善化地區契作的胡麻，農田多達 150 公頃，產量充足，不怕買不到，就怕不知道！', '06-583-6111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19316/480x360_3.png', '臺南市', '善化區', 23.132757, 120.2976479999),
(401, '胡麻油', '熱帶氣候下的嘉南平原，終年陽光普照，孕育各種作物。在曾文溪蜿蜒而過的善化區，從來是甘蔗盛產地，現在更有出名的胡麻，粒粒飽滿，最適合榨油。尤其善化區農會以「真」的精神生產的胡麻油，完全採用善化地區契作的胡麻，農田多達 150 公頃，產量充足，不怕買不到，就怕不知道！', '06-583-6111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/19312/480x360_2.png', '臺南市', '善化區', 23.132757, 120.2976479999),
(402, '原味毛豆禮盒', '在遙遠的明鄭時期，位於來往台南府城與山區的這個地方形成新的市鎮，故名新市。熙熙攘攘的商賈在新市建造繁榮的市鎮，不過景象多元的新市還有生氣蓬勃的田園，現今生產全國知名，還遠銷日本的毛豆。新市人引以為傲的優質非基改毛豆由嚴格管理的契作農田種植。為了讓顧客吃到最新鮮甘甜的毛豆，農民不辭辛苦在清晨太陽出來前，毛豆上還淌著露水時快速採收，並於 4 小時內嚴選、精心製作，冷凍保存新鮮原味，是下飯配酒的好菜。\r\n訂購專線：06-5998545', '06-589-9888', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17702/480x360_113 新市區農會_原味毛豆 (400x320)-20151220234813.jpg', '臺南市', '新市區', 23.0792851, 120.2955182),
(403, '幸福袋', '有「紅豆故鄉」之盛名的萬丹鄉，所生產的紅豆粒粒飽滿不空心、色澤鮮紅亮麗，煮起來的紅豆香味濃郁、質感酥鬆且皮薄，品質相當優良，深得消費大眾喜愛。無論是煮紅豆湯，或是作為糕餅內餡，都可以實實在在地感受到綿密香濃的口感，所帶來的幸福好滋味。', '08-777-2007', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17713/480x360_156萬丹鄉農會_幸福袋1-20151221155403.jpg', '屏東縣', '萬丹鄉', 22.58455, 120.488925),
(404, '紅豆湯', '有「紅豆故鄉」之盛名的萬丹鄉，所生產的紅豆粒粒飽滿不空心、色澤鮮紅亮麗，煮起來的紅豆香味濃郁、質感酥鬆且皮薄，品質相當優良，深得消費大眾喜愛。無論是煮紅豆湯，或是作為糕餅內餡，都可以實實在在地感受到綿密香濃的口感，所帶來的幸福好滋味。', '08-777-2007', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17714/480x360_157萬丹鄉農會_紅豆湯1-20151221155534.jpg', '屏東縣', '萬丹鄉', 22.58455, 120.488925),
(405, '羅董有機糙米奶', '「羅董有機糙米奶」，選用蘭陽平原栽培的有機糙米為主要原料，並融合焙炒的加工方式，呈現出渾厚的米香，在堅持所有飲品使用玻璃瓶為包裝容器，及兼顧環境保護下，以保留住食物的天然原味。\r\n訂購專線：039574525#25\r\n產地直送，如何訂購: 羅董養生商品形象網 (loton.com.tw)', '03-951-8667', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17718/480x360_162羅東農會_有機糙米奶01-20151221161329.jpg', '宜蘭縣', '羅東鎮', 24.6709527, 121.763845),
(406, '芋香毛豆禮盒', '在遙遠的明鄭時期，位於來往台南府城與山區的這個地方形成新的市鎮，故名新市。熙熙攘攘的商賈在新市建造繁榮的市鎮，不過景象多元的新市還有生氣蓬勃的田園，現今生產全國知名，還遠銷日本的毛豆。\r\n新市人引以為傲的優質非基改毛豆由嚴格管理的契作農田種植。為了讓顧客吃到最新鮮甘甜的毛豆，農民不辭辛苦在清晨太陽出來前，毛豆上還淌著露水時快速採收，並於 4 小時內嚴選、精心製作，冷凍保存新鮮原味，是下飯配酒的好菜。', '06-589-9888', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17703/480x360_114 新市區農會_芋香毛豆 (400x320)-20151220234944.jpg', '臺南市', '新市區', 23.0792851, 120.2955182),
(407, '茉莉賞茶御品禮盒(烏龍+語茶)', '彰化縣花壇鄉，一個地如其名的地方，盛產清香的茉莉花，製成花茶用熱水一沖，瞬間馨香四溢，彷彿置身茉莉花叢。若混和同樣是本地名產鮮甜的烏龍茶，或是有奶香的金萱茶，甘甜的茶湯味加上沁鼻幽香，又是不同層次的感動。茉莉賞茶御品禮盒，把朵朵潔白的乾燥茉莉花置於玻璃瓶中，給您帶來不同的視覺享受，讓您在送禮中宛如華族般高貴優雅。', '04-786-7821', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17512/480x360_115 花壇農會_茉莉御品-20151220235511.jpg', '彰化縣', '花壇鄉', 24.02599, 120.537346),
(408, '茉莉賞茶極品禮盒', '彰化縣花壇鄉，一個地如其名的地方，盛產清香的茉莉花，製成花茶用熱水一沖，瞬間馨香四溢，彷彿置身茉莉花叢。若混和同樣是本地名產鮮甜的烏龍茶，或是有奶香的金萱茶，甘甜的茶湯味加上沁鼻幽香，又是不同層次的感動。茉莉賞茶御品禮盒，把朵朵潔白的乾燥茉莉花置於玻璃瓶中，給您帶來不同的視覺享受，讓您在送禮中宛如華族般高貴優雅。', '04-786-7821', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17513/480x360_116 花壇農會_茉莉賞茶 (400x320)-20151220235716.jpg', '彰化縣', '花壇鄉', 24.02599, 120.537346),
(409, '魚池香菇禮盒', '自古人們就上山摘取香菇食用，因產量有限，摘取須靠運氣，故可稱上「山珍海味」，直到二次大戰後，採用人工栽培方法，才使得產量大增，讓人們吃香菇不需要再靠運氣。魚池鄉的香菇多以太空包方式種植，品質優良，不論炒香菇、煮香菇雞湯，都令人讚不絕口。不論自己吃，或年節送禮，絕對是禮「輕」情義重的好選擇。\r\n訂購專線：049-2898453', '04-9289-5505', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17514/480x360_117 原鄉美食--11魚池農會--魚池香菇禮盒 (400x320)-20151221000043.jpg', '南投縣', '魚池鄉', 23.89618859999, 120.9350395999),
(410, '苦茶油', '玉山、中央山脈、阿里山等名山橫貫的南投縣，在那沒有農藥污染的翠綠山林中，長著難得的製油珍寶－油茶樹。製作苦茶油的油茶樹周年長青，它的果實被稱為稱為「天綠菓」，因為它開花結果到採收長達 300 天以上，費時、耗工。用高山出產、耗時全年栽培的油茶果製作的「南投苦茶油」，純天然、耐高溫，是料理最佳良品。\r\n訂購專線：049-2251170', '04-9225-1170', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17518/480x360_118 南投縣農會_苦茶油 (400x320)-20151221000849.jpg', '南投縣', '南投市', 23.9222475, 120.6723086),
(411, '紫鬱金薑黃粉', '南投青翠群山與清澈溪流間，藏著無數大自然的寶藏。薑黃－咖哩的原料，也是印度古醫學的常用藥材，在南投的好山好水中得到絕佳生機。薑黃含有高量薑黃素成份，對人體具有保健作用，日本天皇以此為御用養生聖品。名間鄉生產的薑黃有紫鬱金、秋鬱金、紅薑黃三種，風味各有特色，嚴選高海拔萬年紅礫土栽培種植，堅持自然農法管理，使用最新生技製程，完整保留高單位薑黃素成份，而無農藥殘留，純淨天然！', '04-9273-2111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17519/480x360_119 至121名間鄉農會_禮盒 (400x320)-20151221001109.jpg', '南投縣', '名間鄉', 23.8368341, 120.703988),
(412, '頂級黑金剛花生禮盒', '元長鄉因土壤肥沃，氣候適宜，每年黑金剛花生種植面積高達 1,000 公頃以上，居全台之冠。由於黑金剛花生相對於其它品種之花生有較低的油脂含量，加上其花生仁表膜含有花青素，讓黑金剛花生呈紫黑色，更受廣大之消費者喜愛，這也是近年來黑金剛花生急速竄起的主要原因。元長鄉農會邀您品嚐黑金剛花生香甜脆的最佳天然風味。\r\n訂購專線：05-78820056', '05-788-2005', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17524/480x360_122 原鄉美食--14元長鄉--黑金剛禮盒 (400x320)-20151221001915.jpg', '雲林縣', '元長鄉', 23.6501714, 120.3135554),
(413, '白醬油禮盒', '雲林西螺，台灣第一的黑豆產地，更擁有獨一無二的傳統蔭油釀造技術，從一百多年前傳承至今，現在推出兼具傳統又有突破性的新產品－白醬油。運用一世紀傳承下來的釀造技術，將黑豆蒸煮甕釀後的白醬油，透著琥珀色的光澤與甘甜味的口感。在加熱後，能將菜餚的美味更帶往另一風味層次。', '05-586-3621', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17526/480x360_124 原鄉美食--16西螺農會--白醬油禮盒 (400x320)-20151221002405.jpg', '雲林縣', '西螺鎮', 23.8003737, 120.4546302),
(414, '包種茶油', '有來自雪山清澈泉水的灌溉，坪林鄉有滿山青綠的茶園。從這塊福田出產的，除了有名的包種茶外，還有用新鮮茶籽製成的包種茶油。「茶籽油成分不易造假，農會完全使用包種茶籽榨油，百分之百純天然。」食安風暴後，家庭主婦對於食用油選擇更是謹慎，新北市坪林區農會自製的包種茶油也因而大賣。', '02-2665-7227', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17527/480x360_125 坪林區農會_包種茶油 (400x320)-20151221002602.jpg', '新北市', '坪林區', 24.936102, 121.7117779999),
(415, '蜜汁紅豆', '有「紅豆故鄉」之盛名的萬丹鄉，所生產的紅豆粒粒飽滿不空心、色澤鮮紅亮麗，煮起來的紅豆香味濃郁、質感酥鬆且皮薄，品質相當優良，深得消費大眾喜愛。無論是煮紅豆湯，或是作為糕餅內餡，都可以實實在在地感受到綿密香濃的口感，所帶來的幸福好滋味。', '08-777-2007', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17715/480x360_158萬丹鄉農會_蜜汁紅豆1-20151221160015.jpg', '屏東縣', '萬丹鄉', 22.58455, 120.488925),
(416, '花生湯', '花生，原名「落花生」，亦名番豆，臺灣話稱為土豆，相傳宋元之間廣東商人得自海上諸國，種於沙地上，民諺有「常吃花生能養生，吃了花生不想葷」。雲林縣是花生的故鄉，精心調處的「雲林花生湯」，口味迎合大眾，味美香醇，富含營養，夏季消暑飲品，冬季滋補食品。', '05-597-4101', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17716/480x360_159雲林縣農會_花生湯禮盒-20151221160358.jpg', '雲林縣', '斗南鎮', 23.6784845, 120.4825552),
(417, '八卦山咖啡', '唇齒留香、回甘強烈，是八掛山咖啡留給品客的一抹印象。在山泉水灌溉、傳統日曬、自然風涼乾等用心之下所烘焙出的新鮮咖啡豆，品質穩定、香醇、微酸、無苦澀，是咖啡中的新極品。', '04-725-1171', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17717/480x360_160彰化市農會_八卦山咖啡-20151221161005.jpg', '彰化縣', '彰化市', 24.086618, 120.53876),
(418, '羅董養生皮蛋', '有一句老顧客的話，講的很傳神：「皮蛋溏心的蛋黃，嚐起來的美味不輸大閘蟹的蟹膏」。所以好的皮蛋不用過度調味直接吃或加點蠔油、蔥花就很美味，這也是農會長期以來對「羅董養生皮蛋」品質的要求與堅持所在。\r\n訂購專線：03-9574525#25', '03-951-8667', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18552/480x360_6b29279c80e6429589eb5a9f32e0218c.jpg', '宜蘭縣', '羅東鎮', 24.6709527, 121.763845),
(419, '農好黑蒜精', '清明節前後，雲林農家一齊出動，採收當季最新鮮的大蒜，攻占全國 85% 的大蒜市占率。「農好黑蒜精」選用最佳蒜頭，進行 2~3 個月酶化發酵，讓白蒜本身的蛋白質轉化成人體必需胺基酸，消除了辛蒜氣味，顏色轉黑了，內容更營養，含有大量的多酚、天然檸檬酸和鐵、鋅質。「農好黑蒜精」隨時開罐即可飲用，送禮自用皆相宜。\r\n建議售價: 950元\r\n訂購專線：04-2485-3063#512', '04-2485-3063', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17712/480x360_129 中華民國農會_農好黑蒜精1 (400x320)-20151221003848.jpg', '臺中市', '大里區', 24.112515, 120.690106),
(420, '筊白筍脆片', '一片田野風光映入眼簾， 這裡是三芝「美人腿」的家鄉。三芝獨特水梯田地形所培育出的筊白筍都是筍肉潔白、光滑纖美、香甜鮮翠，口感 遠近馳名，製作成「筊白筍脆片」，更是一絕，讓人一口接一口！', '02-2636-3111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17753/480x360_130 三芝區農會_筊白筍脆片 (400x320)-20151222133803.jpg', '新北市', '三芝區', 25.2583618, 121.498994),
(421, 'A 贏生黑豆', '黑豆在古代藥典中出現很早，秦漢時期，中國第一部藥學書專著《神農草本經》就已經有黑豆的記載。尤以最著明的明朝李時珍所著《本草綱目》亦記載：黑豆氣味甘、平、無毒。「A 贏生黑豆」展現下營鄉農會產品自然原料、傳統生產的品牌堅持。', '06-689-2209', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17723/480x360_131下營區農會_A贏黑豆-20151221175627.jpg', '臺南市', '下營區', 23.2372631, 120.2672728999),
(422, '土豆人家', '色澤黝黑光亮，口感細緻香甜的北港鎮黑金剛土豆又稱長生果，其營養成分高，富含花青素，花生仁飽滿，香氣十足，口感濃郁細緻。在農會積極改造之下，將土豆搖身一變為優質的在地產業，更是北港鎮的金字招牌。', '05-783-2031', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17728/480x360_136土豆人家2-20151221180342.jpg', '雲林縣', '北港鎮', 23.5688534, 120.304131),
(423, '加比山頂級咖啡豆禮盒', '品嚐咖啡是一種生活的態度，也是一門藝術。位於加比山的古坑頂級咖啡豆，經專家評鑑，採購烘焙而成，其獨特之處，在於不苦不澀、入口滑潤，奢華的風味浸潤在舌尖與整個味蕾，是行家眼中的極品之選。\r\n訂購電話：05-5821009', '05-582-3102', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17729/480x360_137古坑鄉農會_加比山頂級咖啡豆禮盒-20151221180505.jpg', '雲林縣', '古坑鄉', 23.6435676, 120.5606759),
(424, '加比山典藏咖啡豆禮盒', '品嚐咖啡是一種生活的態度，也是一門藝術。位於加比山的古坑頂級咖啡豆，經專家評鑑，採購烘焙而成，其獨特之處，在於不苦不澀、入口滑潤，奢華的風味浸潤在舌尖與整個味蕾，是行家眼中的極品之選。', '05-582-3102', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17458/480x360_138古坑鄉農會_典藏咖啡豆禮盒-20151221180635.jpg', '雲林縣', '古坑鄉', 23.6435676, 120.5606759),
(425, '玉井芒果汁布丁', '芒果的香濃與果乾的Q 彈，沉浸在蜜糖的柔情蜜意當中，香、酥、Q 的口感，在唇齒之間恣意奔放，不時觸動人心。咬一口玉井芒果酥，感受到天堂的喜悅與幸福的滋味，此時體驗著濃濃情意，細細品味，甜蜜湧上心頭。而芒果汁布丁口口都是驚喜，其風味絕佳、動感多汁，品嚐的每一口都是種享受。', '06-574-2216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17459/480x360_139玉井區農會_芒果汁布丁-20151221180751.jpg', '臺南市', '玉井區', 23.17685709999, 120.4852097),
(426, '紅運高棗', '購買專線：03-7231626\r\n聯絡信箱：kungnon.food@msa.hinet.net\r\n公館鄉紅棗品種可以追溯至120年前，地方耆老陳煥南從廣東潮安引進栽種至今，紅運高棗精力湯是精選紅棗搭配五穀粉、豆粉混勻組合而成，無添加人工香料、色素及防腐劑，營養豐富均衡美味。', '03-722-5211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18097/480x360_076-099 紅運高棗_20101014 (400X320)-20151026153759.jpg', '苗栗縣', '公館鄉', 24.50464069999, 120.8273643999),
(427, '蓮藕粉精裝禮盒', '每年11 月到農曆過年前，是臺南白河鎮製作藕粉的季節。藕粉做起「相當費工，從挖蓮藕、洗粉到曝曬，每個步驟都遵循古法，相當耗時費工，婦女們」幾乎全體總動員。為了做出最好的藕粉，婆婆媽媽從早到晚忙個不停。說到藕粉，含有維生」素C、蛋白質，是營養價值很高的養生食品。', '06-685-0923', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17735/480x360_141-樂活飲食-白河農會--蓮藕粉禮盒-20151221181027.jpg', '臺南市', '白河區', 23.3516817, 120.4157373),
(428, '紫米餅禮盒', '紫米在古時是皇帝食用的米，又稱「貢米」。如今這難得珍品落腳臺東池上鄉，栽種在池上潔淨的水土上，經過池上鄉親的努力耕耘，豐收不斷，紫米不再是屬於皇帝的貢品，任誰都能一嚐古代皇帝獨享的好滋味。池上鄉農會出品的紫米餅以古法製作，口感紮實飽滿厚實而綿密、不甜不膩的。單片包裝設計不沾手，是您佐茶最佳點心，也是上好的伴手禮。', '08-986-4880', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17736/480x360_142池上鄉農會_紫米餅禮盒-20151221181146.jpg', '臺東縣', '池上鄉', 23.08902709999, 121.2278084999),
(429, '芝麻糖', '購買專線：06-7955217\r\n聯絡信箱：a059a059@yahoo.com.tw\r\n芝麻的營養成分豐富，是養生哲學中的最佳好食材。嚴選本土在地原料，香醇美味、口感絕佳的高鈣芝麻糖，是芝麻與麥芽糖混合而成，以純手工精心製作，香脆可口、唇齒留香，是泡茶族的最愛。其芝麻粉食用方式多元，可拌入蜂蜜當果醬，或加入牛奶、豆漿、優酪乳或優格，風味極佳，是老少咸宜的優質產品。', '06-795-2706', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17737/480x360_143西港區農會_芝麻糖-20151221181255.jpg', '臺南市', '西港區', 23.1236716, 120.2051215),
(430, '芝麻粉', '芝麻的營養成分豐富，是養生哲學中的最佳好食材。嚴選本土在地原料，香醇美味、口感絕佳的高鈣芝麻糖，是芝麻與麥芽糖混合而成，以純手工精心製作，香脆可口、唇齒留香，是泡茶族的最愛。其芝麻粉食用方式多元，可拌入蜂蜜當果醬，或加入牛奶、豆漿、優酪乳或優格，風味極佳，是老少咸宜的優質產品。', '06-795-2706', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17738/480x360_144西港區農會_芝麻粉-20151221181527.jpg', '臺南市', '西港區', 23.1236716, 120.2051215),
(431, '臺灣東山．濾泡式咖啡', '以「阿拉比卡」品種栽培為主的東山咖啡，品質優良、口感極佳，是臺灣本土咖啡之光。多年來農民不斷積極投入研發，以取得美國CQI 認證資格，並成功登陸美國CQI國際網路平臺，讓全世界愛好咖啡者，都 可以品嚐到東山咖啡。', '06-680-0814', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17739/480x360_145東山區農會_咖啡-20151221181710.jpg', '臺南市', '東山區', 23.1781599, 120.483966),
(432, '妖八度- 梅精壯氧飲料', '有清朝貢品之美稱的紅景天，在信義鄉農會採用產學合作技術的帶領下，將紅景天等特殊漢方結合於飲料內。運動過後，可作為養分補給，讓身體充滿活力； 亦可當平時保健及飲品，或送禮皆合宜。', '0975-801-959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17740/480x360_146信義鄉農會_梅精壯氧飲料-20151221181832.jpg', '南投縣', '信義鄉', 23.68870099999, 120.861791),
(433, '紅豆湯', '古法熬煮而成的蜜豆，甜淡適中，是道地的好口味。其黑豆含維生素與多種礦物質。而紅豆則採用萬丹鄉栽種的國產紅豆，其紅豆顆粒碩大，口感細緻綿密，並含植物蛋白質與維生素E。打開即食，調理方便，送禮自用兩相宜。', '08-722-3805', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17741/480x360_147屏東縣農會_紅豆湯-20151221182027.jpg', '屏東縣', '屏東市', 22.6631428, 120.5075365),
(434, '蜜豆禮盒', '古法熬煮而成的蜜豆，甜淡適中，是道地的好口味。其黑豆含維生素與多種礦物質。而紅豆則採用萬丹鄉栽種的國產紅豆，其紅豆顆粒碩大，口感細緻綿密，並含植物蛋白質與維生素E。打開即食，調理方便，送禮自用兩相宜。', '08-722-3805', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17742/480x360_148樂活飲食--屏東縣農會--蜜紅豆黑豆禮盒-20151221182447.jpg', '屏東縣', '屏東市', 22.6631428, 120.5075365),
(435, '東方美人茶飲', '東方美人茶原稱膨風茶。相傳早期有一茶農因茶園受蟲害侵食，不甘損失，乃挑至城中販售，沒想到竟因風味特殊而大受歡迎。回鄉後，茶農向鄉人提及此事，竟被指為「膨風」，從此膨風茶之名不脛而走。喜歡喝東方美人茶的人可以喝得更便利了，峨眉鄉農會推出了東方美人茶罐裝茶飲，讓民眾「花小錢，也能品嘗好茶」。', '03-580-0216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17743/480x360_149峨眉鄉農會_東方美人茶7-20151221182607.jpg', '新竹縣', '峨眉鄉', 24.6887921, 121.0195946),
(436, '峰田小町-綜合鮮菇乾', '霧峰是臺灣菇類的主要產地，尤其金針菇的品質達到國際水準之上，大規模的生產農場使霧峰榮獲「菇類王國」之美譽。走入當地菇園裡，一朵一傘的鮮甜好菇，是大自然以時間孕育出的美味，充滿了新鮮豐沛的天然活力！「峰田小町」將一點一滴最天然的色香味，完美保留在一片片原色原味、香酥清脆的鮮菇片裡，是兼顧香脆口感美味與膳食纖維質的好零食。', '04-2339-3404', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17720/480x360_164霧峰農會_小町鮮姑乾-20151221162025.jpg', '臺中市', '霧峰區', 24.0651751, 120.679522),
(437, '幸福禮盒', '紅棗、紫蘇均為公館特有農產，長久以來均被視為中藥材使用。公館鄉農會食品廠特別將紅棗製成可以一嘴一口的健康零嘴的紅棗干；紫蘇則開發成可作為佐菜香料的紫蘇醬，夾土司、配稀飯、拌飯或拌麵皆適宜。紅棗干和紫蘇醬再搭配客家名產福菜而組成禮盒，口味多元，送禮大方、又健康', '03-722-5211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18098/480x360_077-099 幸福禮盒_20101015 (400X320)-20151026153834.jpg', '苗栗縣', '公館鄉', 24.50464069999, 120.8273643999),
(438, '香蕉戀汝', '臺灣擁有「香蕉王國」美譽，藉由香蕉盛產國及乳品加工技術優勢，實實在在添加15% 國產香蕉原汁，沒有添加任何色素，讓消費者品嚐真正臺灣在地好味道。當香蕉遇上牛奶，水「乳」「蕉」融的好滋味，微微的酸、蜜蜜的甜，蹦出戀愛般的好滋味。', '04-2485-3063', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17724/480x360_132中華民國農會_香蕉戀汝-20151221175814.jpg', '臺中市', '大里區', 24.112515, 120.690106),
(439, 'FACE MILK', '四十多年的專業製造經驗，以現代化設備生產製造，採取超高溫殺菌及連續靜水壓減菌塔滅菌生產，保證不含防腐劑，品質卓越。健康的乳源，高品質的保久乳，口味多樣、質優味美，為國內玻璃瓶裝保久乳領導品牌。', '04-2485-3063', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17725/480x360_133中華民國農會_FACE MILK-20151221175936.jpg', '臺中市', '大里區', 24.112515, 120.690106),
(440, '有機黑木耳飲', '重視健康的你有沒有發現市面上出現愈來愈多木耳飲品？不僅占據了便利商店的一個角落，網路購物、電視購物都在大聲販賣，甚至還出現黑木耳專賣店。 原來，木耳富含蛋白質、礦物質、多種維生素、膠質與膳食纖維，素有「植物性燕窩」稱號。冬山鄉農會推出「有機黑木耳飲」與「有機白雪耳飲」，是忙碌的現代人補充營養的「方便飲」。\r\n產地直送，如何訂購：冬山鄉農會購物商城', '03-959-1122', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17726/480x360_135冬山鄉農會_黑木耳飲-20151221180056.jpg', '宜蘭縣', '冬山鄉', 24.6326533, 121.793597),
(441, '有機白雪耳飲', '重視健康的你有沒有發現市面上出現愈來愈多木耳飲品？不僅占據了便利商店的一個角落，網路購物、電視購物都在大聲販賣，甚至還出現黑木耳專賣店。 原來，木耳富含蛋白質、礦物質、多種維生素、膠質與膳食纖維，素有「植物性燕窩」稱號。冬山鄉農會推出「有機黑木耳飲」與「有機白雪耳飲」，是忙碌的現代人補充營養的「方便飲」。\r\n產地直送，如何訂購：冬山鄉農會購物商城', '03-959-1122', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17727/480x360_134冬山鄉農會_白雪耳飲-20151221180215.jpg', '宜蘭縣', '冬山鄉', 24.6326533, 121.793597),
(442, '玉井芒果酥', '芒果的香濃與果乾的Q 彈，沉浸在蜜糖的柔情蜜意當中，香、酥、Q 的口感，在唇齒之間恣意奔放，不時觸動人心。咬一口玉井芒果酥，感受到天堂的喜悅與幸福的滋味，此時體驗著濃濃情意，細細品味，甜蜜湧上心頭。而芒果汁布丁口口都是驚喜，其風味絕佳、動感多汁，品嚐的每一口都是種享受。', '06-574-2216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/17734/480x360_140玉井區農會_芒果酥-20151221180910.jpg', '臺南市', '玉井區', 23.17685709999, 120.4852097),
(443, '香煎芋糕', '購買專線：03-7226868\r\n產地直送，如何訂購: 巧軒餐館\r\n聯絡信箱：a037226868@yahoo.com.tw\r\n公館鄉種植的檳榔心芋頭品質一流，因後龍溪源頭無水源污染，富含礦物質，種出的芋頭特別綿密、鬆軟，利用當地食材及家鄉豐富的資源，精心研發，為在地的農民創造更大的效益。 香煎芋糕有別於傳統的芋粿，加上在來米、上等豬肉、香菇、油蔥，手工製作的芋糕切片煎過後，口感外酥內軟，芋頭香味濃郁，無添加任何防腐劑，芋頭經過農藥檢驗，大家可安心食用。', '03-722-6868', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18510/480x360_9ba74b535b3947979514f6fa9dd622da.jpg', '苗栗縣', '公館鄉', 24.472237, 120.8195033),
(444, '幸福心食作-香蕉脆片', '選用中寮鄉高海拔新鮮香蕉，一片片金黃誘人的香蕉脆片，酥脆的口感，絕對讓您一口接著一口停不下來。', '04-9269-1111', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18515/480x360_991bebaaee134d1ba01a11faabd84521.jpg', '南投縣', '中寮鄉', 23.8787397, 120.7678090999),
(445, '老少配-蘿蔔雞湯原料組', '購買專線：08-9814903\r\n聯絡信箱：dx0911@yahoo.com.tw\r\n由在地生產之蘿蔔進行醃漬及存放20年以上，並搭配3-5年蘿蔔乾，組成老少配原料組，成為最有古早氣息的菜餚，讓民眾購買回去後，不須任何調味即可煮出一鍋香濃可口的老蘿蔔雞湯，完全可以吃到食材原味，濃濃的鄉土味讓人回味無窮。', '08-981-1022', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18522/480x360_e84ec4319b1d413ea940171117aceb52.jpg', '臺東縣', '關山鎮', 23.04794679999, 121.1641561),
(446, '關農米乖乖', '購買專線：08-9814903\r\n聯絡信箱：arnu111@yahoo.com.tw\r\n關農米乖乖係採用關山在地良質米，不以傳統國外玉米澱粉進口原料，而採用米製作的米乖乖，連結長者的美好回憶，兒童多了一項新選擇，還能成為吃得到米香的貼心伴手禮，送禮自用兩相宜。', '08-981-1022', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18523/480x360_7d3a732f9c9c434a99da3d3d8312b683.jpg', '臺東縣', '關山鎮', 23.04794679999, 121.1641561),
(447, '蜜黑豆', '購買專線：08-7223805分機335\r\n聯絡信箱：ddt200287@hotmail.com\r\n採用屏東在地高雄七號黑豆，以古法熬煮傳承美味，拆封即可食用。含豐富植物蛋白質與維生素。搭配米(稀)飯、吐司麵包、煮湯圓，或牛奶等混合食用，更方便調理。', '08-722-3805', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18526/480x360_14ef5926710a46e7afe08f7c033b71da.jpg', '屏東縣', '屏東市', 22.6631428, 120.5075365),
(448, '銘泉金鑽旺來禮盒', '購買專線：08-7990220\r\n線上訂購: 銘泉生態休閒農場\r\n聯絡信箱：sn45685203@yahoo.com.tw\r\n銘泉聞名的鳳梨是屏東的銘果，選用友善栽種的有機鳳梨做為內餡，吃起來甜中帶酸，酸中帶香，最費工的是每顆內餡都需熬製6-7小時才能完成！\r\n酥皮中加入屏東在地穀類紅寶石之稱有機紅藜米與自然放牧的好蛋，皆由農場所栽種與飼養，吃起來除了安心，更增添了機能性的營養成分，全用鳳梨當餡料的豪氣，才是食材的真滋味。\r\n100%天然鳳梨乾經低溫乾燥保留天然好味道，利用陶缸釀造有機糙米醋為基底，加入有機鳳梨靜置融合，為健康打氣，為人生提味。', '08-799-0220', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18528/480x360_38571de51c3d459c81690836044f7983.jpg', '屏東縣', '瑪家鄉', 22.662026800425792, 120.63174962997437),
(449, '池上米餅', '購買專線：08-9864880\r\n使用在地食材台東池上無污染的優質米，細心拌入伯朗大道的稻香，創新研發製作成點心酥不但口感酥脆，也散發天然的稻米香味，化為美味與情感兼具的池上米餅，是您最佳的伴手禮選擇。', '08-986-4880', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18851/480x360_20200428-01.jpg', '臺東縣', '池上鄉', 23.08902709999, 121.2278084999),
(450, '古早味仙草', '購買專線：03-5872010\r\n聯絡信箱：csm0972132672@yahoo.com.tw\r\n將傳統古早味仙草凍加工製成易開罐，方便攜帶，美味可口的，罐內是一整顆仙草凍喔!', '03-587-4277', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18532/480x360_7f110a7742934f7a93f81defb6dc7799.jpg', '新竹縣', '關西鎮', 24.8032728, 121.1601904),
(451, '飛鼠請假', '購買專線：04-92791949\r\n聯絡信箱：hsinifa@gmail.com \r\n信義在地嚴選成熟黃梅低溫發酵後進橡木桶熟成，以國外 白葡萄酒或紅葡萄酒製作模式，移植至梅子上，除了黃梅 果香外，在橡木桶的加乘效果讓香氣更為豐富有櫻桃、花香撲鼻的感覺，為最原汁原味的梅酒亦是梅酒之首創。', '0975-801-959', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18534/480x360_4a425b11491e4f69a2519cde0b682a6d.jpg', '南投縣', '信義鄉', 23.68870099999, 120.861791),
(452, '玉井愛文芒果干', '購買專線：06-5748551\r\n聯絡信箱：mey877@yahoo.com.tw\r\n以新鮮愛文芒果肉製成，美味爽口不膩，且無任何添加物，完全保存芒果的獨特風味，是具健康、營養的最佳休閒食品。', '06-574-2216', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18538/480x360_61e6c0cec4974958812be5cce0e26e2a.jpg', '臺南市', '玉井區', 23.17685709999, 120.4852097),
(453, '紅藜好棒棒', '購買專線：08-9513111#15\r\n聯絡信箱：lucky.a168@msa.hinet.net\r\n選用台灣原生種脫殼紅藜和台東在地糙米製。\r\n酥脆好滋味，是您健康零食的好選擇。', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18542/480x360_cf669fcc2f4746909b95c41a626eecb4.jpg', '臺東縣', '', 22.7555108, 121.142776),
(454, '東方美人茶禮盒', '北埔茶葉以『膨風茶』又叫「東方美人茶」聞名於世，以受到小綠葉蟬咬食過的青心大冇茶樹的嫩葉種，用手採一心 一葉或一心二葉的嫩葉為茶菁，採重度發酵方式所製作出來的烏龍茶，為臺灣烏龍茶中最高品質的代表。', '03-580-2207', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18554/480x360_9f994920ad2b484aa927d34739e0b751.jpg', '新竹縣', '北埔鄉', 24.700475, 121.055203),
(455, '六香田米禮盒', '購買專線：0905-365658\r\n聯絡信箱：yam2aster@gmail.com\r\n我們的米，取用台灣西部甲及優良水體灌溉，無化學農藥耕作，守護寶山水庫集水區優良環境耕作，確保每一口飯都是純淨。在飯桌上，用心和口，深深記住家的味道。飯桌之外，就用整粒糙米和玄米糠製成的沖泡米麩，為生活中打拼的每一個人，帶來大自然滿滿的元氣。', '0916-335-213', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18556/480x360_ec9ca9be547746ee94a595b2293585c8.jpg', '新竹縣', '竹東鎮', 24.759802, 121.05899),
(456, '紅棗脆條巧克力', '購買專線：03-7231626\r\n聯絡信箱：K703588@gmail.com\r\n公館鄉是台灣唯一的紅棗專區，由農會契作收購，嚴選製作紅棗干，再搭配頂級巧克力，酥脆米香粒，舞動在舌尖，香甜中蘊含者甜蜜的幸福。', '03-722-5211', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18558/480x360_86c44e81a9264aa78602219f3b3f505a.jpg', '苗栗縣', '公館鄉', 24.50464069999, 120.8273643999),
(457, '冰薯', '苗栗縣西湖鄉是一連十二年獲得台灣環保評鑑第一名的好地方，全鄉好山好水，鄉民世代疼惜這片土地，用最傳統', '03-792-1715', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18957/480x360_55720201209.jpg', '苗栗縣', '西湖鄉', 24.557856401703386, 120.75734812462542),
(458, '橄欖禮盒', '購買專線：03-5760161\r\n聯絡信箱：grace.mrolive@gmail.com\r\n橄欖全身是寶，素有生命之樹美名，於新竹寶山的獨特天然環境與氣候下，孕育出天然不受污染的優質尖仁綠橄欖，以古法製做四種不同口味，鹹、辣、甘、甜，便是古早滋味與兒時回憶翻騰之時。', '03-576-0296', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18560/480x360_7a44234dcd434d8896abfffeea505b0f.jpg', '新竹縣', '寶山鄉', 24.721567, 120.998137),
(459, '有機原生種山苦瓜片', '購買專線：03-8656888\r\n產地直送，如何訂購: 壽豐鄉農會\r\n聯絡信箱：service@shoufong.org\r\n嚴選壽豐「有機原生種山苦瓜」，將山苦瓜整顆切片，低溫烘乾，口感清潤，味甘微苦，保留山苦瓜最自然清香。', '03-865-3101', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18564/480x360_f4c2fa037aba4b3cb44ad0e05b6eff78.jpg', '花蓮縣', '壽豐鄉', 23.8453491, 121.4976064),
(460, '原生種山苦瓜藤茶包', '購買專線：03-8656888\r\n產地直送，如何訂購：壽豐鄉農會\r\n聯絡信箱：service@shoufong.org\r\n嚴選壽豐「有機原生種山苦瓜之藤蔓(嚴選離地1公分以上直徑大於0.3公分)」，低溫烘乾，口感清潤，味甘微苦，保留山苦瓜最自然清香。', '03-865-3101', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18566/480x360_ea9127b07d974fe799df8c41c7c299ff.jpg', '花蓮縣', '壽豐鄉', 23.8453491, 121.4976064),
(461, '小農日記禮盒', '購買專線：08-9513111\r\n產地直送，如何訂購：臺東縣農會 (toyugimall.com.tw)\r\n聯絡信箱：lucky.a168@msa.hinet.net\r\n台東農特產品好漾精選，我們是這片大地的小小農夫，但有大大的夢想，謙卑敬愛環境，用心培養作物，用自己的雙手撫摸我的土地。台東在地特色，一次滿足。', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18569/480x360_5f461f7766704199a1d8c8855600dbc0.jpg', '臺東縣', '', 22.7555108, 121.142776),
(462, '山海經脈-花果三重奏', '購買專線：08-9513111\r\n產地直送，如何訂購: 臺東縣農會\r\n聯絡信箱：lucky.a168@msa.hinet.net\r\n取自在地生產在地製造，最新鮮，零污染農產品。純天然食材製成無任何不良食品添加物，是最可安心食用之飲品。', '08-932-4377', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18570/480x360_787f49d4fbf34d2098311619e2bc4480.jpg', '臺東縣', '', 22.7555108, 121.142776),
(463, '富麗米香花生糖', '購買專線：03-8821991\r\n產地直送、如何訂購 : 歡迎光臨～富里鄉農會 (fulifa.org.tw)\r\n聯絡信箱：aaw8266@gmail.com\r\n純正手作的「富麗米香花生糖」採用花蓮的富麗白米，由操刀十年米香的老師傅親手炮製及堅持要求生花生仁開始翻炒西點師傅製作，讓每口都能咬出爆米香口感及香氣，口齒留香，彷彿回到小時候最愛的那個滋味。', '03-882-1705', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18572/480x360_df3087a3057d45fc820190025ffd88c9.jpg', '花蓮縣', '富里鄉', 23.1838646, 121.2969674),
(464, '池上生活米禮盒', '購買專線：089-864880\r\n線上訂購 : 池上鄉農會\r\n聯絡信箱：chc81150@gmail.com\r\n平實的池上農夫，帶著關懷珍惜土地的情感，用心栽種頂級池上米，池上生活米禮盒，一次可品嚐到5種風味的米，成就你我口中奢侈感。', '08-986-4880', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18574/480x360_57a162ced9df4b3c9f2d8944f0979795.jpg', '臺東縣', '池上鄉', 23.08902709999, 121.2278084999),
(465, '嬌澳蝦餅', '購買專線：03-9962108#228\r\n產地直送，如何訂購 : 蘇澳區漁會 (suaofish.org.tw)\r\n聯絡信箱：johnshell83@yahoo.com.tw\r\n選用宜蘭海撈鮮蝦-長角鬥士赤蝦(全蝦)製作而成，不油炸，不含防腐劑，有別於一般油炸蝦餅，味道單純、美味，讓人感受濃郁的鮮蝦味道。', '03-996-2108', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18578/480x360_d529b3ef4cfb4b979e47895446ea4aec.jpg', '宜蘭縣', '蘇澳鎮', 24.5833868, 121.8659277),
(466, '花現金針山-安全金針', '每年的8~10月是金針山花開的季節，金針花原名萱草，每朵花盛開的時間都只有一天，因此又被稱為「一日美人」，每到夏天一大片澄黃色的花海在風中搖曳，讓人置身夢境中的浪漫中。「花現金針山-安全金針」嚴選太麻里海拔1375公尺之高山金針，不燻硫磺，符合國家安全標準，可以安心食用。', '08-978-0400', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18580/480x360_95f21afc97fd4bdbabb0e1bc6f69c147.jpg', '臺東縣', '太麻里鄉', 22.604987, 121.001855),
(467, '屏農紅豆湯', '購買專線：08-7223805分機335\r\n產地直送，如何訂購：屏東縣農會\r\n聯絡信箱：ddt200287@hotmail.com\r\n精選屏東萬丹鄉盛產之頂級紅豆，具產地標章認證及原料檢驗溯源二維碼。\r\n紅豆顆粒碩大晶瑩、口感細緻綿密，實實在在的用料，以傳統工法製造，甜度適中，道地珍饌古早味，紅豆營養滿載，送禮自用兩相宜。', '08-722-3805', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18582/480x360_663e383bb7314b67bdc92188aa2a2d27.jpg', '屏東縣', '屏東市', 22.6631428, 120.5075365),
(468, '92咖啡禮盒', '購買專線：04-92721879\r\n產地直送、如何訂購: 南投縣國姓鄉農會\r\n聯絡信箱：029fcrezo@gmail.com\r\n因九份二山海拔近1200公尺，又處兩溪的匯流點、溼氣充沛、日夜溫差約10℃、土壤肥沃、排水性佳、日照又穩定，咖啡豆生長速度緩慢，是最適合咖啡樹生長的地方，採自然友善耕法栽種，讓咖啡豆的品質保持優良。', '04-9272-1510', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18586/480x360_a10edf5d7a66440c8fee1899ed994eef.jpg', '南投縣', '國姓鄉', 24.024487, 120.846814),
(469, '禾樂幸福-手作米Pizza', 'Q彈爽口米飯再配上綿密鬆軟的大甲芋頭，利用特製的米穀粉手工揉製，在嘴裡咀嚼米飯香甜又散發出芋頭自然清香，讓您吃得安心，溫暖在心，每一口都是幸福的滋味!', '04-2686-3990', 'https://ezgo.ardswc.gov.tw/_api/content/images/souvenir/18590/480x360_15ab0981d65c422e908c5b2a59dc0c19.jpg', '臺中市', '大甲區', 24.334949, 120.615858);
